import { readdir, stat, access } from 'fs/promises';
import path from 'path';
import type { ProjectSummary } from '../types/index.js';
import { getGitStatus, getRecentCommits } from './git-reader.js';
import { parseDevFixList, parseExecutionPlan } from './markdown-parser.js';
import { readClaudeMd } from './config-reader.js';

const BASE_DIR = '/mnt/c/.netapps';

const IGNORE_DIRS = new Set([
  'node_modules', '.git', 'dist', 'build', 'data',
  'deployments_backup', 'mission-control',
]);

const IGNORE_FILES_PATTERN = /^\.|^-|\.env|\.json$|\.ps1$|\.yaml$|^Dockerfile$/;

async function exists(p: string): Promise<boolean> {
  try {
    await access(p);
    return true;
  } catch {
    return false;
  }
}

async function isDirectory(p: string): Promise<boolean> {
  try {
    const s = await stat(p);
    return s.isDirectory();
  } catch {
    return false;
  }
}

function getStatus(
  isGitRepo: boolean,
  isBootstrapped: boolean,
  lastCommitTime?: string,
): ProjectSummary['status'] {
  if (!isBootstrapped) return 'unconfigured';
  if (!lastCommitTime) return 'stale';
  const daysSince = (Date.now() - new Date(lastCommitTime).getTime()) / (1000 * 60 * 60 * 24);
  return daysSince <= 7 ? 'active' : 'stale';
}

export async function scanProjects(): Promise<ProjectSummary[]> {
  const entries = await readdir(BASE_DIR);
  const projects: ProjectSummary[] = [];

  for (const entry of entries) {
    if (IGNORE_DIRS.has(entry)) continue;
    if (IGNORE_FILES_PATTERN.test(entry)) continue;

    const fullPath = path.join(BASE_DIR, entry);
    if (!(await isDirectory(fullPath))) continue;

    const isGitRepo = await exists(path.join(fullPath, '.git'));
    const hasClaudeMd = await exists(path.join(fullPath, 'CLAUDE.md'));
    const hasPkgJson = await exists(path.join(fullPath, 'package.json'));
    const isBootstrapped = hasClaudeMd;

    let gitBranch: string | undefined;
    let lastCommitTime: string | undefined;
    let lastCommitMessage: string | undefined;

    if (isGitRepo) {
      try {
        const gitStatus = await getGitStatus(fullPath);
        gitBranch = gitStatus.branch;
        const commits = await getRecentCommits(fullPath, 1);
        if (commits.length > 0) {
          lastCommitTime = commits[0]!.date;
          lastCommitMessage = commits[0]!.message;
        }
      } catch { /* git read failed, skip */ }
    }

    let description: string | undefined;
    if (hasClaudeMd) {
      try {
        const claude = await readClaudeMd(fullPath);
        description = claude.description;
      } catch { /* skip */ }
    }

    let testPassRate: number | undefined;
    let bugCount: number | undefined;
    let warningCount: number | undefined;

    if (await exists(path.join(fullPath, 'DEV-FIX-LIST.md'))) {
      try {
        const tests = await parseDevFixList(fullPath);
        testPassRate = tests.passRate;
        bugCount = tests.bugs.length;
        warningCount = tests.warningList.length;
      } catch { /* skip */ }
    }

    let currentPhase: string | undefined;
    let phaseProgress: number | undefined;

    if (await exists(path.join(fullPath, 'EXECUTION-PLAN.md'))) {
      try {
        const roadmap = await parseExecutionPlan(fullPath);
        const inProgressPhase = roadmap.phases.find(p => p.progress > 0 && p.progress < 100);
        const lastPhase = roadmap.phases.filter(p => p.progress > 0).pop();
        const activePhase = inProgressPhase ?? lastPhase;
        if (activePhase) {
          currentPhase = activePhase.name;
          phaseProgress = activePhase.progress;
        }
      } catch { /* skip */ }
    }

    projects.push({
      name: entry,
      path: fullPath,
      status: getStatus(isGitRepo, isBootstrapped, lastCommitTime),
      description,
      gitBranch,
      lastCommitTime,
      lastCommitMessage,
      testPassRate,
      bugCount,
      warningCount,
      currentPhase,
      phaseProgress,
      isGitRepo,
      isBootstrapped: isBootstrapped || hasPkgJson,
    });
  }

  // Sort: active first, then stale, then unconfigured
  const statusOrder = { active: 0, stale: 1, unconfigured: 2 };
  projects.sort((a, b) => statusOrder[a.status] - statusOrder[b.status]);

  return projects;
}

