import { execFile } from 'child_process';
import { promisify } from 'util';
import type { GitCommit, GitStatus } from '../types/index.js';

const exec = promisify(execFile);

async function git(cwd: string, args: string[]): Promise<string> {
  const { stdout } = await exec('git', args, { cwd, timeout: 10000 });
  return stdout.trim();
}

export async function getGitStatus(projectPath: string): Promise<GitStatus> {
  const branch = await git(projectPath, ['rev-parse', '--abbrev-ref', 'HEAD']);

  let remoteUrl: string | undefined;
  try {
    remoteUrl = await git(projectPath, ['config', '--get', 'remote.origin.url']);
  } catch { /* no remote */ }

  const statusOutput = await git(projectPath, ['status', '--porcelain']);
  const dirtyFiles = statusOutput
    ? statusOutput.split('\n').map(l => l.trim()).filter(Boolean)
    : [];

  let ahead = 0;
  let behind = 0;
  try {
    const revList = await git(projectPath, ['rev-list', '--left-right', '--count', `${branch}...origin/${branch}`]);
    const parts = revList.split('\t');
