import { readFile, access } from 'fs/promises';
import path from 'path';
import type { InfraData, McpServer, Hook } from '../types/index.js';

async function exists(p: string): Promise<boolean> {
  try {
    await access(p);
    return true;
  } catch {
    return false;
  }
}

async function readJson(filePath: string): Promise<Record<string, unknown>> {
  try {
    const content = await readFile(filePath, 'utf-8');
    return JSON.parse(content) as Record<string, unknown>;
  } catch {
    return {};
  }
}

// ── CLAUDE.md reader ──
export async function readClaudeMd(projectPath: string): Promise<{
  description?: string;
  techStack?: string[];
  commands?: Record<string, string>;
}> {
  const filePath = path.join(projectPath, 'CLAUDE.md');
  if (!(await exists(filePath))) return {};

  const content = await readFile(filePath, 'utf-8');

  // Extract description from "## Project Overview" section
  const overviewMatch = content.match(/##\s*Project Overview\s*\n([\s\S]*?)(?=\n##|\n---)/i);
  const description = overviewMatch?.[1]?.trim().split('\n')[0]?.trim();

  // Extract tech stack mentions
  const techStack: string[] = [];
  const techPatterns = [
    'React', 'Express', 'Firestore', 'TypeScript', 'Vite', 'Tailwind',
    'Node', 'Python', 'Django', 'Flask', 'Next.js', 'Vue', 'Angular',
    'PostgreSQL', 'MongoDB', 'Redis', 'Docker', 'Kubernetes',
  ];
  for (const tech of techPatterns) {
    if (content.includes(tech)) techStack.push(tech);
  }

  // Extract commands from ## Commands section
  const commands: Record<string, string> = {};
  const cmdMatch = content.match(/##\s*Commands\s*\n([\s\S]*?)(?=\n##|\n---)/i);
  if (cmdMatch) {
    const lines = cmdMatch[1]!.split('\n');
    for (const line of lines) {
      const match = line.match(/[-*]\s*\*\*(.+?):\*\*\s*`(.+?)`/);
      if (match) {
        commands[match[1]!] = match[2]!;
      }
    }
  }

  return { description, techStack, commands };
}

// ── Infrastructure reader ──
export async function readInfraData(projectPath: string): Promise<InfraData> {
  // .mcp.json — MCP servers
  const mcpServers: McpServer[] = [];
  const mcpPath = path.join(projectPath, '.mcp.json');
  if (await exists(mcpPath)) {
    const mcpData = await readJson(mcpPath);
    const servers = mcpData.mcpServers as Record<string, Record<string, unknown>> | undefined;
    if (servers) {
      for (const [name, config] of Object.entries(servers)) {
        mcpServers.push({
          name,
          command: (config.command as string) ?? '',
          args: (config.args as string[]) ?? [],
        });
      }
    }
  }

  // .claude/settings.json — hooks
  const hooks: Hook[] = [];
  const settingsPath = path.join(projectPath, '.claude', 'settings.json');
  if (await exists(settingsPath)) {
    const settings = await readJson(settingsPath);
    const hookData = settings.hooks as Record<string, Array<{ command: string }>> | undefined;
    if (hookData) {
      for (const [event, hookList] of Object.entries(hookData)) {
        for (const hook of hookList) {
          hooks.push({ event, command: hook.command ?? String(hook) });
        }
      }
    }
  }

  // DEVOPS.md — deployment info
  let deploymentUrl: string | undefined;
  let cloudRunRegion: string | undefined;
  const devopsPath = path.join(projectPath, 'DEVOPS.md');
  if (await exists(devopsPath)) {
    const content = await readFile(devopsPath, 'utf-8');
    const urlMatch = content.match(/https:\/\/[\w-]+\.run\.app/);
    if (urlMatch) deploymentUrl = urlMatch[0];
    const regionMatch = content.match(/region[:\s]*`?(europe-west\d|us-[\w-]+|asia-[\w-]+)/i);
    if (regionMatch) cloudRunRegion = regionMatch[1];
  }

  // CI/CD presence
  const hasCloudBuild = await exists(path.join(projectPath, 'cloudbuild.yaml'));
  const hasDockerfile = await exists(path.join(projectPath, 'Dockerfile'));

  // package.json
  let packageName: string | undefined;
  let scripts: Record<string, string> = {};
  let dependencies: Record<string, string> = {};
  let devDependencies: Record<string, string> = {};
  const pkgPath = path.join(projectPath, 'package.json');
  if (await exists(pkgPath)) {
    const pkg = await readJson(pkgPath);
    packageName = pkg.name as string | undefined;
    scripts = (pkg.scripts ?? {}) as Record<string, string>;
    dependencies = (pkg.dependencies ?? {}) as Record<string, string>;
    devDependencies = (pkg.devDependencies ?? {}) as Record<string, string>;
  }

  return {
    mcpServers,
    hooks,
    deploymentUrl,
    cloudRunRegion,
    cicd: { hasCloudBuild, hasDockerfile },
    packageName,
    scripts,
    dependencies,
    devDependencies,
  };
}

