// Mirror of server types for the frontend

export interface ProjectSummary {
  name: string;
  path: string;
  status: 'active' | 'stale' | 'unconfigured';
  description?: string;
  gitBranch?: string;
  lastCommitTime?: string;
  lastCommitMessage?: string;
  testPassRate?: number;
  bugCount?: number;
  warningCount?: number;
  currentPhase?: string;
  phaseProgress?: number;
  isGitRepo: boolean;
  isBootstrapped: boolean;
}

export interface GitCommit {
  hash: string;
  shortHash: string;
  message: string;
  author: string;
  date: string;
  relativeDate: string;
}

export interface GitStatus {
  branch: string;
  remoteUrl?: string;
  dirty: boolean;
  dirtyFiles: string[];
  ahead: number;
  behind: number;
}

export interface GitData {
  status: GitStatus;
  commits: GitCommit[];
}

export interface Bug {
  id: string;
  title: string;
  page: string;
  severity: string;
  description: string;
  status?: string;
}

export interface Warning {
  id: string;
  title: string;
  page: string;
  severity: string;
  description: string;
}

export interface TestData {
  totalTests: number;
  passing: number;
  failing: number;
  warnings: number;
  passRate: number;
  bugs: Bug[];
  warningList: Warning[];
  generatedDate?: string;
}

export interface PlanTask {
  id: string;
  task: string;
  status: string;
  priority?: string;
  complexity?: string;
  agent?: string;
}

export interface Phase {
  id: string;
  name: string;
  goal?: string;
  tasks: PlanTask[];
  totalTasks: number;
  completedTasks: number;
  progress: number;
}

export interface RoadmapData {
  phases: Phase[];
  totalTasks: number;
  completedTasks: number;
  overallProgress: number;
}

export interface Session {
  id: string;
  date: string;
  agent?: string;
  workingDirectory?: string;
  summary: string[];
  commits: string[];
  decisions: string[];
  pendingWork: string[];
}

export interface SessionData {
  sessions: Session[];
}

export interface McpServer {
  name: string;
  command: string;
  args?: string[];
}

export interface Hook {
  event: string;
  command: string;
}

export interface InfraData {
  mcpServers: McpServer[];
  hooks: Hook[];
  deploymentUrl?: string;
  cloudRunRegion?: string;
  cicd: {
    hasCloudBuild: boolean;
    hasDockerfile: boolean;
  };
  packageName?: string;
  scripts: Record<string, string>;
  dependencies: Record<string, string>;
  devDependencies: Record<string, string>;
}

export interface ProjectDetail extends ProjectSummary {
  git?: GitData;
  tests?: TestData;
  roadmap?: RoadmapData;
  sessions?: SessionData;
  infra?: InfraData;
  techStack?: string[];
  commands?: Record<string, string>;
}

