import type { ProjectSummary, ProjectDetail } from '../types';

const BASE = '/api';

async function fetchJson<T>(url: string): Promise<T> {
  const res = await fetch(`${BASE}${url}`);
  if (!res.ok) throw new Error(`API error: ${res.status} ${res.statusText}`);
  return res.json() as Promise<T>;
}

export async function fetchProjects(): Promise<ProjectSummary[]> {
  return fetchJson<ProjectSummary[]>('/projects');
}

export async function fetchProjectDetail(name: string): Promise<ProjectDetail> {
  return fetchJson<ProjectDetail>(`/projects/${encodeURIComponent(name)}`);
}

export async function bootstrapProject(name: string): Promise<{ success: boolean; message: string }> {
  const res = await fetch(`${BASE}/projects/${encodeURIComponent(name)}/bootstrap`, {
    method: 'POST',
  });
  if (!res.ok) throw new Error('Bootstrap failed');
  return res.json() as Promise<{ success: boolean; message: string }>;
}

