import { Outlet, Link, useLocation } from 'react-router-dom';
import { Radar, ArrowLeft } from 'lucide-react';

export default function Layout() {
  const location = useLocation();
  const isDetail = location.pathname.startsWith('/projects/');

  return (
    <div className="min-h-screen relative">
      <div className="bg-pattern" />

      <header className="sticky top-0 z-50 glass-card border-b border-border-default">
        <div className="max-w-[1440px] mx-auto px-6 h-14 flex items-center gap-4">
          {isDetail && (
            <Link
              to="/"
              className="flex items-center gap-1.5 text-text-muted hover:text-brand-teal transition-colors text-sm"
            >
              <ArrowLeft size={16} />
              Back
            </Link>
          )}

          <Link to="/" className="flex items-center gap-2.5">
            <Radar size={22} className="text-brand-teal" />
            <span className="font-heading font-semibold text-lg text-text-primary">
              Mission Control
            </span>
          </Link>

          <div className="flex-1" />

          <span className="text-xs text-text-muted font-mono">localhost</span>
        </div>
      </header>

      <main className="relative z-10 max-w-[1440px] mx-auto px-6 py-6">
        <Outlet />
      </main>
    </div>
  );
}

