import { FolderGit2, Activity, Bug, Rocket } from 'lucide-react';
import type { ProjectSummary } from '../types';

interface Props {
  projects: ProjectSummary[];
}

export default function StatsRow({ projects }: Props) {
  const total = projects.length;
  const active = projects.filter(p => p.status === 'active').length;
  const openBugs = projects.reduce((sum, p) => sum + (p.bugCount ?? 0), 0);
  const bootstrapped = projects.filter(p => p.isBootstrapped).length;

  const stats = [
    { label: 'Total Projects', value: total, icon: FolderGit2, color: 'text-brand-teal' },
    { label: 'Active (7d)', value: active, icon: Activity, color: 'text-status-compliant' },
    { label: 'Open Bugs', value: openBugs, icon: Bug, color: openBugs > 0 ? 'text-status-critical' : 'text-text-muted' },
    { label: 'Bootstrapped', value: bootstrapped, icon: Rocket, color: 'text-brand-peach' },
  ];

  return (
    <div className="grid grid-cols-2 md:grid-cols-4 gap-4 mb-8">
      {stats.map(s => (
        <div key={s.label} className="glass-card rounded-xl p-4 flex items-center gap-3">
          <div className={`${s.color} p-2 rounded-lg bg-surface-elevated`}>
            <s.icon size={20} />
          </div>
          <div>
            <div className="text-2xl font-heading font-bold text-text-primary">{s.value}</div>
            <div className="text-xs text-text-muted">{s.label}</div>
          </div>
        </div>
      ))}
    </div>
  );
}

