          <div className={`${s.color} p-2 rounded-lg bg-surface-elevated`}>
            <s.icon size={20} />
          </div>
          <div>
            <div className="text-2xl font-heading font-bold text-text-primary">{s.value}</div>
            <div className="text-xs text-text-muted">{s.label}</div>
