import { Link } from 'react-router-dom';
import { GitBranch, Bug, AlertTriangle, Clock, ChevronRight } from 'lucide-react';
import { formatDistanceToNow } from 'date-fns';
import type { ProjectSummary } from '../types';

interface Props {
  project: ProjectSummary;
  onBootstrap: (name: string) => void;
}

const statusConfig = {
  active: { dot: 'bg-status-compliant', label: 'Active', ring: 'border-status-compliant/20' },
  stale: { dot: 'bg-status-warning', label: 'Stale', ring: 'border-status-warning/20' },
  unconfigured: { dot: 'bg-text-muted', label: 'Unconfigured', ring: 'border-text-muted/20' },
};

export default function ProjectCard({ project, onBootstrap }: Props) {
  const sc = statusConfig[project.status];

  const commitAge = project.lastCommitTime
    ? formatDistanceToNow(new Date(project.lastCommitTime), { addSuffix: true })
    : null;

  return (
    <div className="glass-card card-hover rounded-xl overflow-hidden group">
      {/* Header */}
      <div className="p-5 pb-3">
        <div className="flex items-start justify-between mb-2">
          <div className="flex items-center gap-2.5">
            <span className={`w-2.5 h-2.5 rounded-full ${sc.dot} shrink-0`} />
            <h3 className="font-heading font-semibold text-base text-text-primary group-hover:text-brand-peach transition-colors">
              {project.name}
            </h3>
          </div>
          <span className={`text-[10px] font-mono uppercase tracking-wider px-2 py-0.5 rounded-full border ${sc.ring} text-text-muted`}>
            {sc.label}
          </span>
        </div>

        {project.description && (
          <p className="text-xs text-text-muted line-clamp-2 mb-3 leading-relaxed">
            {project.description}
          </p>
        )}

        {/* Git info */}
        {project.gitBranch && (
          <div className="flex items-center gap-3 text-xs text-text-muted mb-2">
            <span className="flex items-center gap-1">
              <GitBranch size={12} className="text-brand-teal" />
              <span className="font-mono">{project.gitBranch}</span>
            </span>
            {commitAge && (
              <span className="flex items-center gap-1">
                <Clock size={12} />
                {commitAge}
              </span>
            )}
          </div>
        )}

        {project.lastCommitMessage && (
          <p className="text-xs text-text-secondary font-mono truncate mb-3">
            {project.lastCommitMessage}
          </p>
        )}

        {/* Metrics row */}
        {project.status !== 'unconfigured' && (
          <div className="flex items-center gap-4 text-xs">
            {project.testPassRate !== undefined && (
              <span className={`flex items-center gap-1 ${
                project.testPassRate >= 90 ? 'text-status-compliant' :
                project.testPassRate >= 70 ? 'text-status-warning' : 'text-status-critical'
              }`}>
                {project.testPassRate}% pass
              </span>
            )}
            {(project.bugCount ?? 0) > 0 && (
              <span className="flex items-center gap-1 text-status-critical">
                <Bug size={12} />
                {project.bugCount}
              </span>
            )}
            {(project.warningCount ?? 0) > 0 && (
              <span className="flex items-center gap-1 text-status-warning">
                <AlertTriangle size={12} />
                {project.warningCount}
              </span>
            )}
          </div>
        )}

        {/* Phase progress */}
        {project.currentPhase && project.phaseProgress !== undefined && (
          <div className="mt-3">
            <div className="flex items-center justify-between text-[10px] text-text-muted mb-1">
              <span>{project.currentPhase}</span>
              <span>{project.phaseProgress}%</span>
            </div>
            <div className="h-1.5 rounded-full bg-surface-elevated overflow-hidden">
              <div
                className="h-full rounded-full bg-gradient-to-r from-brand-teal to-brand-blue transition-all duration-500"
                style={{ width: `${project.phaseProgress}%` }}
              />
            </div>
          </div>
        )}
      </div>

      {/* Footer */}
      <div className="border-t border-border-subtle px-5 py-2.5">
        {project.status === 'unconfigured' ? (
          <button
            onClick={(e) => { e.preventDefault(); onBootstrap(project.name); }}
            className="text-xs text-brand-teal hover:text-brand-peach transition-colors font-medium"
          >
            Initialize Project
          </button>
        ) : (
          <Link
            to={`/projects/${project.name}`}
            className="flex items-center justify-between text-xs text-text-muted hover:text-brand-teal transition-colors"
          >
            <span>View details</span>
            <ChevronRight size={14} className="group-hover:translate-x-0.5 transition-transform" />
          </Link>
        )}
      </div>
    </div>
  );
}

