import { X, Rocket } from 'lucide-react';

interface Props {
  projectName: string;
  isLoading: boolean;
  onConfirm: () => void;
  onClose: () => void;
}

export default function BootstrapModal({ projectName, isLoading, onConfirm, onClose }: Props) {
  return (
    <div className="fixed inset-0 z-50 flex items-center justify-center p-4">
      <div className="absolute inset-0 bg-black/60 backdrop-blur-sm" onClick={onClose} />

      <div className="glass-card-elevated rounded-2xl p-6 w-full max-w-md relative animate-fade-in-up">
        <button
          onClick={onClose}
          className="absolute top-4 right-4 text-text-muted hover:text-text-primary transition-colors"
        >
          <X size={18} />
        </button>

        <div className="flex items-center gap-3 mb-4">
          <div className="p-2 rounded-lg bg-brand-teal/10 text-brand-teal">
            <Rocket size={22} />
          </div>
          <h3 className="font-heading text-lg font-semibold text-brand-peach">
            Initialize Project
          </h3>
        </div>

        <p className="text-sm text-text-secondary mb-2">
          This will create the following template files in <code className="text-brand-teal">{projectName}/</code>:
        </p>

        <ul className="text-xs font-mono text-text-muted space-y-1 mb-6 ml-4">
          <li>CLAUDE.md</li>
          <li>SESSION-HISTORY.md</li>
          <li>DEV-FIX-LIST.md</li>
          <li>EXECUTION-PLAN.md</li>
          <li>.claude/settings.json</li>
        </ul>

        <div className="flex gap-3">
          <button
            onClick={onClose}
            className="flex-1 px-4 py-2 rounded-lg border border-border-default text-text-muted hover:text-text-primary hover:border-border-focus text-sm transition-colors"
          >
            Cancel
          </button>
          <button
            onClick={onConfirm}
            disabled={isLoading}
            className="flex-1 px-4 py-2 rounded-lg bg-brand-teal text-white font-medium text-sm hover:bg-brand-deep transition-colors disabled:opacity-50"
          >
            {isLoading ? 'Creating...' : 'Initialize'}
          </button>
        </div>
      </div>
    </div>
  );
}

