import { GitBranch, GitCommit as GitCommitIcon, Clock, ExternalLink, AlertCircle, Terminal } from 'lucide-react';
import type { ProjectDetail } from '../../types';

interface Props {
  project: ProjectDetail;
}

export default function OverviewTab({ project }: Props) {
  const git = project.git;

  return (
    <div className="space-y-6">
      {/* Description + Tech Stack */}
      <div className="glass-card rounded-xl p-5">
        <h3 className="font-heading text-sm font-semibold text-brand-peach mb-3">About</h3>
        {project.description && (
          <p className="text-sm text-text-secondary mb-4">{project.description}</p>
        )}

        {project.techStack && project.techStack.length > 0 && (
          <div className="flex flex-wrap gap-2 mb-4">
            {project.techStack.map(tech => (
              <span key={tech} className="px-2.5 py-1 rounded-md bg-surface-elevated text-xs font-mono text-brand-teal border border-border-subtle">
                {tech}
              </span>
            ))}
          </div>
        )}

        {project.commands && Object.keys(project.commands).length > 0 && (
          <div>
            <h4 className="text-xs text-text-muted uppercase tracking-wider mb-2 flex items-center gap-1.5">
              <Terminal size={12} /> Commands
            </h4>
            <div className="space-y-1.5">
              {Object.entries(project.commands).map(([name, cmd]) => (
                <div key={name} className="flex items-center gap-3 text-xs">
                  <span className="text-text-muted w-28 shrink-0">{name}</span>
                  <code className="text-brand-blue bg-surface-dark px-2 py-0.5 rounded">{cmd}</code>
                </div>
              ))}
            </div>
          </div>
        )}
      </div>

      {/* Git Status */}
      {git && (
        <div className="glass-card rounded-xl p-5">
          <h3 className="font-heading text-sm font-semibold text-brand-peach mb-3">Git</h3>

          <div className="flex flex-wrap items-center gap-4 mb-4 text-sm">
            <span className="flex items-center gap-1.5">
              <GitBranch size={14} className="text-brand-teal" />
              <span className="font-mono text-text-primary">{git.status.branch}</span>
            </span>

            {git.status.dirty && (
              <span className="flex items-center gap-1.5 text-status-warning text-xs">
                <AlertCircle size={12} />
                {git.status.dirtyFiles.length} dirty files
              </span>
            )}

            {git.status.ahead > 0 && (
              <span className="text-xs text-status-info">{git.status.ahead} ahead</span>
            )}
            {git.status.behind > 0 && (
              <span className="text-xs text-status-warning">{git.status.behind} behind</span>
            )}

            {git.status.remoteUrl && (
              <a
                href={git.status.remoteUrl.replace(/\.git$/, '').replace(/^git@github\.com:/, 'https://github.com/')}
                target="_blank"
                rel="noopener noreferrer"
                className="flex items-center gap-1 text-xs text-text-muted hover:text-brand-teal transition-colors ml-auto"
              >
                <ExternalLink size={12} /> Remote
              </a>
            )}
          </div>

          {/* Commit history */}
          <h4 className="text-xs text-text-muted uppercase tracking-wider mb-2 flex items-center gap-1.5">
            <GitCommitIcon size={12} /> Recent Commits
          </h4>

          <div className="space-y-0">
            {git.commits.map((commit, i) => (
              <div
                key={commit.hash}
                className={`flex items-start gap-3 py-2 ${i > 0 ? 'border-t border-border-subtle' : ''}`}
              >
                <code className="text-[10px] text-brand-teal font-mono mt-0.5 shrink-0 w-14">
                  {commit.shortHash}
                </code>
                <div className="flex-1 min-w-0">
                  <p className="text-xs text-text-primary truncate">{commit.message}</p>
                  <div className="flex items-center gap-2 text-[10px] text-text-muted mt-0.5">
                    <span>{commit.author}</span>
                    <span className="flex items-center gap-0.5">
                      <Clock size={9} /> {commit.relativeDate}
                    </span>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
}

