import { CheckCircle2, Circle, Target } from 'lucide-react';
import type { ProjectDetail } from '../../types';

interface Props {
  project: ProjectDetail;
}

export default function ProgressTab({ project }: Props) {
  const roadmap = project.roadmap;

  if (!roadmap || roadmap.phases.length === 0) {
    return (
      <div className="glass-card rounded-xl p-8 text-center">
        <Target size={32} className="text-text-muted mx-auto mb-3" />
        <p className="text-text-muted text-sm">No execution plan found</p>
        <p className="text-text-muted text-xs mt-1">Create an EXECUTION-PLAN.md to track progress</p>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Overall progress */}
      <div className="glass-card rounded-xl p-5">
        <div className="flex items-center justify-between mb-3">
          <h3 className="font-heading text-sm font-semibold text-brand-peach">Overall Progress</h3>
          <span className="text-2xl font-heading font-bold text-brand-teal">{roadmap.overallProgress}%</span>
        </div>
        <div className="h-3 rounded-full bg-surface-elevated overflow-hidden">
          <div
            className="h-full rounded-full bg-gradient-to-r from-brand-teal to-brand-blue transition-all duration-700"
            style={{ width: `${roadmap.overallProgress}%` }}
          />
        </div>
        <p className="text-xs text-text-muted mt-2">
          {roadmap.completedTasks} of {roadmap.totalTasks} tasks completed
        </p>
      </div>

      {/* Phase breakdown */}
      {roadmap.phases.map(phase => (
        <div key={phase.id} className="glass-card rounded-xl p-5">
          <div className="flex items-center justify-between mb-1">
            <h4 className="font-heading text-sm font-semibold text-text-primary">
              Phase {phase.id}: {phase.name}
            </h4>
            <span className={`text-sm font-mono font-bold ${
              phase.progress === 100 ? 'text-status-compliant' :
              phase.progress > 0 ? 'text-brand-teal' : 'text-text-muted'
            }`}>
              {phase.progress}%
            </span>
          </div>

          {phase.goal && (
            <p className="text-xs text-text-muted mb-3">{phase.goal}</p>
          )}

          <div className="h-2 rounded-full bg-surface-elevated overflow-hidden mb-3">
            <div
              className={`h-full rounded-full transition-all duration-500 ${
                phase.progress === 100
                  ? 'bg-status-compliant'
                  : 'bg-gradient-to-r from-brand-teal to-brand-blue'
              }`}
              style={{ width: `${phase.progress}%` }}
            />
          </div>

          <p className="text-[10px] text-text-muted mb-2">
            {phase.completedTasks}/{phase.totalTasks} tasks
          </p>

          {/* Task list */}
          <div className="space-y-1">
            {phase.tasks.map(task => (
              <div key={task.id} className="flex items-start gap-2 py-1">
                {task.status === 'DONE' ? (
                  <CheckCircle2 size={14} className="text-status-compliant mt-0.5 shrink-0" />
                ) : (
                  <Circle size={14} className="text-text-muted mt-0.5 shrink-0" />
                )}
                <div className="flex-1 min-w-0">
                  <span className={`text-xs ${task.status === 'DONE' ? 'text-text-muted line-through' : 'text-text-primary'}`}>
                    {task.task}
                  </span>
                  <div className="flex gap-2 mt-0.5">
                    {task.priority && (
                      <span className={`text-[10px] font-mono ${
                        task.priority === 'P1' ? 'text-status-critical' :
                        task.priority === 'P2' ? 'text-status-warning' : 'text-text-muted'
                      }`}>
                        {task.priority}
                      </span>
                    )}
                    {task.complexity && (
                      <span className="text-[10px] text-text-muted">{task.complexity}</span>
                    )}
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      ))}
    </div>
  );
}

