import { Bug, AlertTriangle, CheckCircle2, ShieldAlert } from 'lucide-react';
import type { ProjectDetail } from '../../types';

interface Props {
  project: ProjectDetail;
}

export default function QualityTab({ project }: Props) {
  const tests = project.tests;

  if (!tests) {
    return (
      <div className="glass-card rounded-xl p-8 text-center">
        <ShieldAlert size={32} className="text-text-muted mx-auto mb-3" />
        <p className="text-text-muted text-sm">No test data found</p>
        <p className="text-text-muted text-xs mt-1">Create a DEV-FIX-LIST.md to track quality</p>
      </div>
    );
  }

  const passColor = tests.passRate >= 90 ? 'text-status-compliant' :
    tests.passRate >= 70 ? 'text-status-warning' : 'text-status-critical';

  const ringColor = tests.passRate >= 90 ? 'border-status-compliant' :
    tests.passRate >= 70 ? 'border-status-warning' : 'border-status-critical';

  return (
    <div className="space-y-6">
      {/* Test summary */}
      <div className="glass-card rounded-xl p-5">
        <div className="flex items-center gap-6">
          {/* Pass rate gauge */}
          <div className={`w-24 h-24 rounded-full border-4 ${ringColor} flex items-center justify-center shrink-0`}>
            <div className="text-center">
              <span className={`text-2xl font-heading font-bold ${passColor}`}>{tests.passRate}%</span>
            </div>
          </div>

          <div className="flex-1">
            <h3 className="font-heading text-sm font-semibold text-brand-peach mb-3">Test Results</h3>
            <div className="grid grid-cols-4 gap-3">
              <div>
                <div className="text-lg font-heading font-bold text-text-primary">{tests.totalTests}</div>
                <div className="text-[10px] text-text-muted uppercase">Total</div>
              </div>
              <div>
                <div className="text-lg font-heading font-bold text-status-compliant">{tests.passing}</div>
                <div className="text-[10px] text-text-muted uppercase flex items-center gap-0.5">
                  <CheckCircle2 size={9} /> Pass
                </div>
              </div>
              <div>
                <div className="text-lg font-heading font-bold text-status-critical">{tests.failing}</div>
                <div className="text-[10px] text-text-muted uppercase flex items-center gap-0.5">
                  <Bug size={9} /> Fail
                </div>
              </div>
              <div>
                <div className="text-lg font-heading font-bold text-status-warning">{tests.warnings}</div>
                <div className="text-[10px] text-text-muted uppercase flex items-center gap-0.5">
                  <AlertTriangle size={9} /> Warn
                </div>
              </div>
            </div>
            {tests.generatedDate && (
              <p className="text-[10px] text-text-muted mt-2">Generated: {tests.generatedDate}</p>
            )}
          </div>
        </div>
      </div>

      {/* Bugs */}
      {tests.bugs.length > 0 && (
        <div className="glass-card rounded-xl p-5">
          <h3 className="font-heading text-sm font-semibold text-brand-peach mb-3 flex items-center gap-2">
            <Bug size={14} className="text-status-critical" /> Bugs ({tests.bugs.length})
          </h3>
          <div className="space-y-3">
            {tests.bugs.map(bug => (
              <div key={bug.id} className="border-l-2 border-status-critical pl-3 py-1">
                <div className="flex items-center gap-2 mb-0.5">
                  <span className="font-mono text-xs text-status-critical font-bold">{bug.id}</span>
                  <span className={`text-[10px] px-1.5 py-0 rounded-full ${
                    bug.severity.includes('P1') ? 'bg-status-critical/20 text-status-critical' :
                    bug.severity.includes('P2') ? 'bg-status-warning/20 text-status-warning' :
                    'bg-text-muted/20 text-text-muted'
                  }`}>
                    {bug.severity}
                  </span>
                </div>
                <p className="text-xs text-text-primary">{bug.title}</p>
                <p className="text-[10px] text-text-muted mt-0.5">
                  Page: <code className="text-brand-blue">{bug.page}</code>
                </p>
              </div>
            ))}
          </div>
        </div>
      )}

      {/* Warnings */}
      {tests.warningList.length > 0 && (
        <div className="glass-card rounded-xl p-5">
          <h3 className="font-heading text-sm font-semibold text-brand-peach mb-3 flex items-center gap-2">
            <AlertTriangle size={14} className="text-status-warning" /> Warnings ({tests.warningList.length})
          </h3>
          <div className="space-y-2">
            {tests.warningList.map(w => (
              <div key={w.id} className="border-l-2 border-status-warning/40 pl-3 py-1">
                <div className="flex items-center gap-2 mb-0.5">
                  <span className="font-mono text-xs text-status-warning">{w.id}</span>
                  <span className="text-[10px] px-1.5 rounded-full bg-text-muted/10 text-text-muted">{w.severity}</span>
                </div>
                <p className="text-xs text-text-secondary">{w.title}</p>
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
}

