                {/* Summary */}
                {session.summary.length > 0 && (
                  <div>
                    <h4 className="text-[10px] text-text-muted uppercase tracking-wider mb-1.5">What happened</h4>
                    <ul className="space-y-1">
                      {session.summary.map((item, i) => (
                        <li key={i} className="text-xs text-text-secondary flex gap-2">
