import { Server, Webhook, Cloud, Container, Package } from 'lucide-react';
import type { ProjectDetail } from '../../types';

interface Props {
  project: ProjectDetail;
}

export default function InfraTab({ project }: Props) {
  const infra = project.infra;

  if (!infra) {
    return (
      <div className="glass-card rounded-xl p-8 text-center">
        <Server size={32} className="text-text-muted mx-auto mb-3" />
        <p className="text-text-muted text-sm">No infrastructure data found</p>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* MCP Servers */}
      <div className="glass-card rounded-xl p-5">
        <h3 className="font-heading text-sm font-semibold text-brand-peach mb-3 flex items-center gap-2">
          <Server size={14} className="text-brand-teal" /> MCP Servers ({infra.mcpServers.length})
        </h3>
        {infra.mcpServers.length === 0 ? (
          <p className="text-xs text-text-muted">No MCP servers configured</p>
        ) : (
          <div className="space-y-2">
            {infra.mcpServers.map(server => (
              <div key={server.name} className="flex items-center gap-3 p-2 rounded-lg bg-surface-elevated">
                <div className="w-2 h-2 rounded-full bg-status-compliant shrink-0" />
                <div>
                  <span className="text-xs text-text-primary font-medium">{server.name}</span>
                  <p className="text-[10px] font-mono text-text-muted">
                    {server.command} {(server.args ?? []).join(' ')}
                  </p>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>

      {/* Hooks */}
      <div className="glass-card rounded-xl p-5">
        <h3 className="font-heading text-sm font-semibold text-brand-peach mb-3 flex items-center gap-2">
          <Webhook size={14} className="text-brand-teal" /> Hooks ({infra.hooks.length})
        </h3>
        {infra.hooks.length === 0 ? (
          <p className="text-xs text-text-muted">No hooks configured</p>
        ) : (
          <div className="space-y-2">
            {infra.hooks.map((hook, i) => (
              <div key={i} className="flex items-start gap-3 p-2 rounded-lg bg-surface-elevated">
                <span className="text-[10px] font-mono text-status-ai bg-status-ai/10 px-1.5 py-0.5 rounded shrink-0">
                  {hook.event}
                </span>
                <code className="text-xs text-text-secondary break-all">{hook.command}</code>
              </div>
            ))}
          </div>
        )}
      </div>

      {/* Deployment */}
      <div className="glass-card rounded-xl p-5">
        <h3 className="font-heading text-sm font-semibold text-brand-peach mb-3 flex items-center gap-2">
          <Cloud size={14} className="text-brand-teal" /> Deployment
        </h3>
        <div className="space-y-2 text-xs">
          <div className="flex items-center gap-3">
            <span className="text-text-muted w-24 shrink-0">URL</span>
            {infra.deploymentUrl ? (
              <a href={infra.deploymentUrl} target="_blank" rel="noopener noreferrer" className="text-brand-teal hover:underline font-mono">
                {infra.deploymentUrl}
              </a>
            ) : (
              <span className="text-text-muted">Not configured</span>
            )}
          </div>
          {infra.cloudRunRegion && (
            <div className="flex items-center gap-3">
              <span className="text-text-muted w-24 shrink-0">Region</span>
              <span className="text-text-secondary">{infra.cloudRunRegion}</span>
            </div>
          )}
        </div>
      </div>

      {/* CI/CD */}
      <div className="glass-card rounded-xl p-5">
        <h3 className="font-heading text-sm font-semibold text-brand-peach mb-3 flex items-center gap-2">
          <Container size={14} className="text-brand-teal" /> CI/CD
        </h3>
        <div className="flex gap-4">
          <div className="flex items-center gap-2 text-xs">
            <div className={`w-2 h-2 rounded-full ${infra.cicd.hasCloudBuild ? 'bg-status-compliant' : 'bg-text-muted'}`} />
            <span className="text-text-secondary">Cloud Build</span>
          </div>
          <div className="flex items-center gap-2 text-xs">
            <div className={`w-2 h-2 rounded-full ${infra.cicd.hasDockerfile ? 'bg-status-compliant' : 'bg-text-muted'}`} />
            <span className="text-text-secondary">Dockerfile</span>
          </div>
        </div>
      </div>

      {/* Scripts */}
      {Object.keys(infra.scripts).length > 0 && (
        <div className="glass-card rounded-xl p-5">
          <h3 className="font-heading text-sm font-semibold text-brand-peach mb-3 flex items-center gap-2">
            <Package size={14} className="text-brand-teal" /> Scripts
          </h3>
          <div className="space-y-1.5">
            {Object.entries(infra.scripts).map(([name, cmd]) => (
              <div key={name} className="flex items-center gap-3 text-xs">
                <code className="text-brand-teal w-20 shrink-0">{name}</code>
                <code className="text-text-muted">{cmd}</code>
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
}

