import { Router } from 'express';
import path from 'path';
import { scanProjects } from '../services/scanner.js';
import { getGitStatus, getRecentCommits } from '../services/git-reader.js';
import { parseDevFixList, parseExecutionPlan, parseSessionHistory } from '../services/markdown-parser.js';
import { readClaudeMd, readInfraData } from '../services/config-reader.js';
import { bootstrapProject } from '../services/bootstrap.js';
import type { ProjectDetail } from '../types/index.js';

const BASE_DIR = '/mnt/c/.netapps';
const router = Router();

function projectPath(name: string): string {
  // Prevent path traversal
  const clean = path.basename(name);
  return path.join(BASE_DIR, clean);
}

// GET /api/projects — List all projects with summary
router.get('/', async (_req, res) => {
  try {
    const projects = await scanProjects();
    res.json(projects);
  } catch (err) {
    console.error('Failed to scan projects:', err);
    res.status(500).json({ error: 'Failed to scan projects' });
  }
});

// GET /api/projects/:name — Full project detail
router.get('/:name', async (req, res) => {
  try {
    const pp = projectPath(req.params.name!);
    const projects = await scanProjects();
    const summary = projects.find(p => p.name === req.params.name);
    if (!summary) {
      res.status(404).json({ error: 'Project not found' });
      return;
    }

    const detail: ProjectDetail = { ...summary };

    // Fetch all data in parallel
    const [claude, git, tests, roadmap, sessions, infra] = await Promise.allSettled([
      readClaudeMd(pp),
      summary.isGitRepo
        ? Promise.all([getGitStatus(pp), getRecentCommits(pp, 20)]).then(([status, commits]) => ({ status, commits }))
        : Promise.resolve(undefined),
      parseDevFixList(pp).catch(() => undefined),
      parseExecutionPlan(pp).catch(() => undefined),
      parseSessionHistory(pp).catch(() => undefined),
      readInfraData(pp),
    ]);

    if (claude.status === 'fulfilled' && claude.value) {
      detail.techStack = claude.value.techStack;
      detail.commands = claude.value.commands;
      if (claude.value.description) detail.description = claude.value.description;
    }
    if (git.status === 'fulfilled') detail.git = git.value;
    if (tests.status === 'fulfilled') detail.tests = tests.value;
    if (roadmap.status === 'fulfilled') detail.roadmap = roadmap.value;
    if (sessions.status === 'fulfilled') detail.sessions = sessions.value;
    if (infra.status === 'fulfilled') detail.infra = infra.value;

    res.json(detail);
  } catch (err) {
    console.error('Failed to get project detail:', err);
    res.status(500).json({ error: 'Failed to get project detail' });
  }
});

// GET /api/projects/:name/git
router.get('/:name/git', async (req, res) => {
  try {
    const pp = projectPath(req.params.name!);
    const [status, commits] = await Promise.all([
      getGitStatus(pp),
      getRecentCommits(pp, 20),
    ]);
    res.json({ status, commits });
  } catch (err) {
    res.status(500).json({ error: 'Failed to read git data' });
  }
});

// GET /api/projects/:name/tests
router.get('/:name/tests', async (req, res) => {
  try {
    const pp = projectPath(req.params.name!);
    const data = await parseDevFixList(pp);
    res.json(data);
  } catch {
    res.json({ totalTests: 0, passing: 0, failing: 0, warnings: 0, passRate: 0, bugs: [], warningList: [] });
  }
});

// GET /api/projects/:name/roadmap
router.get('/:name/roadmap', async (req, res) => {
  try {
    const pp = projectPath(req.params.name!);
    const data = await parseExecutionPlan(pp);
    res.json(data);
  } catch {
    res.json({ phases: [], totalTasks: 0, completedTasks: 0, overallProgress: 0 });
  }
});

// GET /api/projects/:name/sessions
router.get('/:name/sessions', async (req, res) => {
  try {
    const pp = projectPath(req.params.name!);
    const data = await parseSessionHistory(pp);
    res.json(data);
  } catch {
    res.json({ sessions: [] });
  }
});

// GET /api/projects/:name/infra
router.get('/:name/infra', async (req, res) => {
  try {
    const pp = projectPath(req.params.name!);
    const data = await readInfraData(pp);
    res.json(data);
  } catch {
    res.json({ mcpServers: [], hooks: [], cicd: { hasCloudBuild: false, hasDockerfile: false }, scripts: {}, dependencies: {}, devDependencies: {} });
  }
});

// POST /api/projects/:name/bootstrap
router.post('/:name/bootstrap', async (req, res) => {
  try {
    const name = req.params.name!;
    const pp = projectPath(name);
    await bootstrapProject(pp, name);
    res.json({ success: true, message: `Bootstrapped ${name}` });
  } catch (err) {
    console.error('Bootstrap failed:', err);
    res.status(500).json({ error: 'Bootstrap failed' });
  }
});

export default router;

