import { useState } from 'react';
import { useQuery, useQueryClient } from '@tanstack/react-query';
import { fetchProjects, bootstrapProject } from '../services/api';
import StatsRow from '../components/StatsRow';
import ProjectCard from '../components/ProjectCard';
import BootstrapModal from '../components/BootstrapModal';
import type { ProjectSummary } from '../types';

export default function ProjectsPage() {
  const queryClient = useQueryClient();
  const [bootstrapTarget, setBootstrapTarget] = useState<string | null>(null);
  const [isBootstrapping, setIsBootstrapping] = useState(false);

  const { data: projects, isLoading, error } = useQuery<ProjectSummary[]>({
    queryKey: ['projects'],
    queryFn: fetchProjects,
  });

  const handleBootstrap = async () => {
    if (!bootstrapTarget) return;
    setIsBootstrapping(true);
    try {
      await bootstrapProject(bootstrapTarget);
      await queryClient.invalidateQueries({ queryKey: ['projects'] });
      setBootstrapTarget(null);
    } catch (err) {
      console.error('Bootstrap failed:', err);
    } finally {
      setIsBootstrapping(false);
    }
  };

  if (isLoading) {
    return (
      <div className="space-y-6 animate-fade-in">
        <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
          {[...Array(4)].map((_, i) => (
            <div key={i} className="glass-card rounded-xl p-4 h-20 animate-shimmer" />
          ))}
        </div>
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-5">
          {[...Array(6)].map((_, i) => (
            <div key={i} className="glass-card rounded-xl h-48 animate-shimmer" />
          ))}
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="glass-card rounded-xl p-8 text-center">
        <p className="text-status-critical mb-2">Failed to load projects</p>
        <p className="text-text-muted text-sm">Is the server running on port 3001?</p>
      </div>
    );
  }

  return (
    <div className="animate-fade-in">
      <div className="mb-6">
        <h1 className="text-2xl font-heading font-bold text-brand-peach mb-1">Projects</h1>
        <p className="text-sm text-text-muted">
          Monitoring {projects?.length ?? 0} project directories under /mnt/c/.netapps/
        </p>
      </div>

      <StatsRow projects={projects ?? []} />

      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-5">
        {(projects ?? []).map((project, i) => (
          <div key={project.name} className={`opacity-0 animate-fade-in-up stagger-${Math.min(i + 1, 6)}`}>
            <ProjectCard
              project={project}
              onBootstrap={(name) => setBootstrapTarget(name)}
            />
          </div>
        ))}
      </div>

      {bootstrapTarget && (
        <BootstrapModal
          projectName={bootstrapTarget}
          isLoading={isBootstrapping}
          onConfirm={handleBootstrap}
          onClose={() => setBootstrapTarget(null)}
        />
      )}
    </div>
  );
}

