import { useState } from 'react';
import { useQuery } from '@tanstack/react-query';
import { Bot, ChevronDown, ChevronRight, GitCommit, AlertCircle, Lightbulb, Zap, Clock, Cpu } from 'lucide-react';
import { formatDistanceToNow } from 'date-fns';
import { fetchProjectAgents } from '../../services/api';
import type { ProjectDetail, AgentActivity } from '../../types';

function LiveAgentCard({ agent }: { agent: AgentActivity }) {
  const isActive = agent.status === 'active';
  const timeAgo = formatDistanceToNow(new Date(agent.lastTimestamp), { addSuffix: true });

  return (
    <div className={`p-4 rounded-xl ${
      isActive
        ? 'bg-status-compliant/5 border border-status-compliant/20 glow-teal'
        : 'bg-surface-elevated border border-border-subtle'
    }`}>
      <div className="flex items-center gap-2 mb-2">
        <div className="relative">
          <div className={`w-2.5 h-2.5 rounded-full ${isActive ? 'bg-status-compliant' : 'bg-status-warning'}`} />
          {isActive && (
            <div className="absolute inset-0 w-2.5 h-2.5 rounded-full bg-status-compliant animate-ping opacity-50" />
          )}
        </div>
        <span className={`text-[10px] font-mono uppercase tracking-wider px-2 py-0.5 rounded-full ${
          isActive
            ? 'bg-status-compliant/10 text-status-compliant'
            : 'bg-status-warning/10 text-status-warning'
        }`}>
          {isActive ? 'LIVE NOW' : 'Recent'}
        </span>
        {agent.slug && (
          <span className="text-[10px] font-mono text-text-muted">{agent.slug}</span>
        )}
      </div>

      <p className="text-xs text-text-secondary mb-2 line-clamp-2">{agent.lastMessage}</p>

      <div className="flex flex-wrap items-center gap-3 text-[10px] text-text-muted">
        <span className="flex items-center gap-0.5">
          <Clock size={9} /> {timeAgo}
        </span>
        {agent.duration && (
          <span className="flex items-center gap-0.5">
            <Zap size={9} /> {agent.duration}
          </span>
        )}
        {agent.subagentCount > 0 && (
          <span className="flex items-center gap-0.5">
            <Cpu size={9} /> {agent.subagentCount} sub-agent{agent.subagentCount > 1 ? 's' : ''}
          </span>
        )}
        <span className="font-mono">{agent.entryCount} events</span>
      </div>
    </div>
  );
}

interface Props {
  project: ProjectDetail;
}

export default function SessionsTab({ project }: Props) {
  const sessions = project.sessions?.sessions ?? [];
  const [expanded, setExpanded] = useState<Set<string>>(new Set(sessions[0]?.id ? [sessions[0].id] : []));

  const { data: liveAgents } = useQuery({
    queryKey: ['agents-project', project.name],
    queryFn: () => fetchProjectAgents(project.name),
    refetchInterval: 5000,
  });

  const toggle = (id: string) => {
    setExpanded(prev => {
      const next = new Set(prev);
      if (next.has(id)) next.delete(id);
      else next.add(id);
      return next;
    });
  };

  if (sessions.length === 0 && (!liveAgents || liveAgents.length === 0)) {
    return (
      <div className="glass-card rounded-xl p-8 text-center">
        <Bot size={32} className="text-text-muted mx-auto mb-3" />
        <p className="text-text-muted text-sm">No session history found</p>
        <p className="text-text-muted text-xs mt-1">Create a SESSION-HISTORY.md to track agent activity</p>
      </div>
    );
  }

  return (
    <div className="space-y-3">
      {/* Live agents section */}
      {liveAgents && liveAgents.length > 0 && (
        <div className="glass-card rounded-xl p-5 mb-4">
          <h3 className="font-heading text-sm font-semibold text-brand-peach mb-3 flex items-center gap-2">
            <Zap size={14} className="text-status-compliant" />
            Live Agent Activity
          </h3>
          <div className="space-y-2">
