import { useState } from 'react';
import { Bot, ChevronDown, ChevronRight, GitCommit, AlertCircle, Lightbulb } from 'lucide-react';
import type { ProjectDetail } from '../../types';

interface Props {
  project: ProjectDetail;
}

export default function SessionsTab({ project }: Props) {
  const sessions = project.sessions?.sessions ?? [];
  const [expanded, setExpanded] = useState<Set<string>>(new Set(sessions[0]?.id ? [sessions[0].id] : []));

  const toggle = (id: string) => {
    setExpanded(prev => {
      const next = new Set(prev);
      if (next.has(id)) next.delete(id);
      else next.add(id);
      return next;
    });
  };

  if (sessions.length === 0) {
    return (
      <div className="glass-card rounded-xl p-8 text-center">
        <Bot size={32} className="text-text-muted mx-auto mb-3" />
        <p className="text-text-muted text-sm">No session history found</p>
        <p className="text-text-muted text-xs mt-1">Create a SESSION-HISTORY.md to track agent activity</p>
      </div>
    );
  }

  return (
    <div className="space-y-3">
      {sessions.map(session => {
        const isOpen = expanded.has(session.id);

        return (
          <div key={session.id} className="glass-card rounded-xl overflow-hidden">
            {/* Header — always visible */}
            <button
              onClick={() => toggle(session.id)}
              className="w-full flex items-center gap-3 p-4 text-left hover:bg-surface-hover/30 transition-colors"
            >
              {isOpen ? <ChevronDown size={16} className="text-text-muted" /> : <ChevronRight size={16} className="text-text-muted" />}
              <Bot size={16} className="text-status-ai shrink-0" />
              <div className="flex-1 min-w-0">
                <span className="text-sm text-text-primary font-medium">Session {session.id}</span>
                <span className="text-xs text-text-muted ml-2">{session.date}</span>
                {session.agent && (
                  <span className="text-[10px] text-status-ai ml-2">{session.agent}</span>
                )}
              </div>
              <span className="text-[10px] text-text-muted shrink-0">
                {session.summary.length} items
              </span>
            </button>

            {/* Expanded content */}
            {isOpen && (
              <div className="border-t border-border-subtle px-4 pb-4 pt-3 space-y-4">
                {/* Summary */}
                {session.summary.length > 0 && (
                  <div>
                    <h4 className="text-[10px] text-text-muted uppercase tracking-wider mb-1.5">What happened</h4>
                    <ul className="space-y-1">
                      {session.summary.map((item, i) => (
                        <li key={i} className="text-xs text-text-secondary flex gap-2">
                          <span className="text-brand-teal mt-0.5 shrink-0">-</span>
                          {item}
                        </li>
                      ))}
                    </ul>
                  </div>
                )}

                {/* Commits */}
                {session.commits.length > 0 && (
                  <div>
                    <h4 className="text-[10px] text-text-muted uppercase tracking-wider mb-1.5 flex items-center gap-1">
                      <GitCommit size={10} /> Commits
                    </h4>
                    <ul className="space-y-1">
                      {session.commits.map((c, i) => (
                        <li key={i} className="text-xs font-mono text-brand-blue">{c}</li>
                      ))}
                    </ul>
                  </div>
                )}

                {/* Decisions */}
                {session.decisions.length > 0 && (
                  <div>
                    <h4 className="text-[10px] text-text-muted uppercase tracking-wider mb-1.5 flex items-center gap-1">
                      <Lightbulb size={10} /> Decisions
                    </h4>
                    <ul className="space-y-1">
                      {session.decisions.map((d, i) => (
                        <li key={i} className="text-xs text-text-secondary">{d}</li>
                      ))}
                    </ul>
                  </div>
                )}

                {/* Pending */}
                {session.pendingWork.length > 0 && (
                  <div>
                    <h4 className="text-[10px] text-text-muted uppercase tracking-wider mb-1.5 flex items-center gap-1">
                      <AlertCircle size={10} className="text-status-warning" /> Pending
                    </h4>
                    <ul className="space-y-1">
                      {session.pendingWork.map((p, i) => (
                        <li key={i} className="text-xs text-status-warning">{p}</li>
                      ))}
                    </ul>
                  </div>
                )}
              </div>
            )}
          </div>
        );
      })}
    </div>
  );
}

