import { readdir, stat, readFile } from 'fs/promises';
import path from 'path';

const CLAUDE_DIR = '/home/rajiv/.claude';
const PROJECTS_DIR = path.join(CLAUDE_DIR, 'projects');

// How recently a file must be modified to be "active"
const ACTIVE_THRESHOLD_MS = 5 * 60 * 1000; // 5 minutes
const RECENT_THRESHOLD_MS = 60 * 60 * 1000; // 1 hour

export interface AgentActivity {
  sessionId: string;
  slug?: string;
  projectPath?: string;
  projectName?: string;
  agentId?: string;
  status: 'active' | 'recent' | 'completed';
  lastTimestamp: string;
  lastType: string;
  lastMessage?: string;
  duration?: string;
  entryCount: number;
  subagentCount: number;
  version?: string;
  gitBranch?: string;
}

export interface LiveDashboard {
  activeAgents: AgentActivity[];
  recentAgents: AgentActivity[];
  totalSessions: number;
}

/** Decode the URL-encoded project directory name back to a path */
function decodeProjectDir(dirName: string): string {
  return dirName.replace(/-/g, '/');
}

/** Extract project name from decoded path */
function extractProjectName(decodedPath: string): string {
  const parts = decodedPath.split('/').filter(Boolean);
  return parts[parts.length - 1] ?? decodedPath;
}

/** Read last N lines of a file efficiently */
async function tailFile(filePath: string, lines: number): Promise<string[]> {
  try {
    const content = await readFile(filePath, 'utf-8');
    const allLines = content.trim().split('\n');
    return allLines.slice(-lines);
  } catch {
    return [];
  }
}

/** Parse a JSONL entry safely */
function parseJsonl(line: string): Record<string, unknown> | null {
  try {
    return JSON.parse(line) as Record<string, unknown>;
  } catch {
    return null;
  }
}

/** Calculate human-readable duration */
function formatDuration(startMs: number, endMs: number): string {
  const diff = endMs - startMs;
  const mins = Math.floor(diff / 60000);
  const hours = Math.floor(mins / 60);
  if (hours > 0) return `${hours}h ${mins % 60}m`;
  if (mins > 0) return `${mins}m`;
  return '<1m';
}

/** Get a human-readable description of what the agent is doing */
function describeActivity(entry: Record<string, unknown>): string {
  const type = entry.type as string;

  if (type === 'assistant') {
    const msg = entry.message as Record<string, unknown> | undefined;
    if (msg) {
      const content = msg.content;
      if (typeof content === 'string') {
        return content.slice(0, 120);
      }
      if (Array.isArray(content)) {
        // Find text block
        for (const block of content) {
          const b = block as Record<string, unknown>;
          if (b.type === 'text' && typeof b.text === 'string') {
            return (b.text as string).slice(0, 120);
          }
          if (b.type === 'tool_use') {
            return `Using tool: ${b.name as string}`;
          }
        }
      }
    }
    return 'Generating response...';
  }

  if (type === 'progress') {
    const data = entry.data as Record<string, unknown> | undefined;
    if (data) {
      if (data.type === 'hook_progress') {
        return `Running hook: ${(data.hookEvent as string) ?? 'unknown'}`;
      }
      if (data.type === 'agent_progress') {
        return 'Agent processing...';
      }
      const message = data.message as Record<string, unknown> | undefined;
      if (message?.content) {
        const c = message.content as string;
        return c.slice(0, 120);
      }
    }
    return 'Processing...';
  }

  if (type === 'user') return 'Waiting for input';
  if (type === 'system') return 'System event';

  return type;
}

/** Scan a single session JSONL file */
async function scanSession(
  jsonlPath: string,
  projectDirName: string,
): Promise<AgentActivity | null> {
  try {
    const fileStat = await stat(jsonlPath);
    const modifiedMs = fileStat.mtimeMs;
    const now = Date.now();
    const age = now - modifiedMs;

    // Skip sessions older than 1 hour for performance
    if (age > RECENT_THRESHOLD_MS) return null;

    const sessionId = path.basename(jsonlPath, '.jsonl');

    // Read first and last entries
    const firstLines = await tailFile(jsonlPath, 1000);
    if (firstLines.length === 0) return null;

    const firstEntry = parseJsonl(firstLines[0]!);
    const lastEntry = parseJsonl(firstLines[firstLines.length - 1]!);
    if (!lastEntry) return null;

    // Count subagents
    let subagentCount = 0;
    const subagentDir = path.join(path.dirname(jsonlPath), sessionId, 'subagents');
    try {
      const subagents = await readdir(subagentDir);
      subagentCount = subagents.filter(f => f.endsWith('.jsonl')).length;
    } catch { /* no subagents dir */ }

    const decodedPath = decodeProjectDir(projectDirName);
    const projectName = extractProjectName(decodedPath);

    const status: AgentActivity['status'] = age <= ACTIVE_THRESHOLD_MS ? 'active' : 'recent';

    const startTime = firstEntry?.timestamp as string | undefined;
    const endTime = lastEntry.timestamp as string | undefined;
    let duration: string | undefined;
    if (startTime && endTime) {
      duration = formatDuration(new Date(startTime).getTime(), new Date(endTime).getTime());
    }

    return {
      sessionId,
      slug: (lastEntry.slug ?? firstEntry?.slug) as string | undefined,
      projectPath: decodedPath,
      projectName,
      status,
      lastTimestamp: (endTime ?? new Date(modifiedMs).toISOString()),
      lastType: lastEntry.type as string,
      lastMessage: describeActivity(lastEntry),
      duration,
      entryCount: firstLines.length,
      subagentCount,
      version: (firstEntry?.version ?? lastEntry.version) as string | undefined,
      gitBranch: (firstEntry?.gitBranch ?? lastEntry.gitBranch) as string | undefined,
    };
  } catch {
    return null;
  }
}

/** Scan all projects for active/recent agent sessions */
export async function getLiveAgentDashboard(): Promise<LiveDashboard> {
  const activeAgents: AgentActivity[] = [];
  const recentAgents: AgentActivity[] = [];
  let totalSessions = 0;

  try {
    const projectDirs = await readdir(PROJECTS_DIR);

    for (const projectDir of projectDirs) {
      const projectPath = path.join(PROJECTS_DIR, projectDir);
      const dirStat = await stat(projectPath);
      if (!dirStat.isDirectory()) continue;

      const entries = await readdir(projectPath);
      const jsonlFiles = entries.filter(e => e.endsWith('.jsonl'));
      totalSessions += jsonlFiles.length;

      for (const jsonl of jsonlFiles) {
        const activity = await scanSession(
          path.join(projectPath, jsonl),
          projectDir,
        );
        if (!activity) continue;

        if (activity.status === 'active') {
          activeAgents.push(activity);
        } else {
          recentAgents.push(activity);
        }
      }
    }
  } catch (err) {
    console.error('Failed to scan agent sessions:', err);
  }

  // Sort by timestamp descending
  activeAgents.sort((a, b) => new Date(b.lastTimestamp).getTime() - new Date(a.lastTimestamp).getTime());
  recentAgents.sort((a, b) => new Date(b.lastTimestamp).getTime() - new Date(a.lastTimestamp).getTime());

  return { activeAgents, recentAgents, totalSessions };
}

/** Get active agents for a specific project */
export async function getProjectAgents(projectName: string): Promise<AgentActivity[]> {
  const dashboard = await getLiveAgentDashboard();
  return [...dashboard.activeAgents, ...dashboard.recentAgents]
    .filter(a => a.projectName === projectName);
}

