import { useQuery } from '@tanstack/react-query';
import { Link } from 'react-router-dom';
import { fetchLiveAgents } from '../services/api';
import {
  Bot, Zap, Clock, GitBranch, Cpu, FileCode, Terminal,
  MessageSquare, Webhook, Search, PenLine, Eye, ChevronRight,
} from 'lucide-react';
import { formatDistanceToNow, format } from 'date-fns';
import type { AgentActivity, ActivityEvent } from '../types';

const EVENT_ICONS: Record<string, typeof Bot> = {
  tool_use: Terminal,
  text: MessageSquare,
  hook: Webhook,
  user_input: MessageSquare,
  system: Zap,
};

const TOOL_ICONS: Record<string, typeof Bot> = {
  Read: Eye,
  Write: FileCode,
  Edit: PenLine,
  Bash: Terminal,
  Grep: Search,
  Glob: Search,
  Task: Bot,
};

function EventRow({ event }: { event: ActivityEvent }) {
  const Icon = event.detail ? (TOOL_ICONS[event.detail] ?? EVENT_ICONS[event.type]) : (EVENT_ICONS[event.type] ?? Zap);
  const time = format(new Date(event.timestamp), 'HH:mm:ss');

  const typeColor = {
    tool_use: 'text-brand-teal',
    text: 'text-text-secondary',
    hook: 'text-status-ai',
    user_input: 'text-brand-peach',
    system: 'text-text-muted',
    tool_result: 'text-brand-blue',
  }[event.type] ?? 'text-text-muted';

  return (
    <div className="flex items-start gap-2 py-1.5 border-b border-border-subtle/50 last:border-0">
      <span className="text-[10px] font-mono text-text-muted w-16 shrink-0 mt-0.5">{time}</span>
      <Icon size={12} className={`${typeColor} mt-0.5 shrink-0`} />
      <span className="text-xs text-text-secondary break-all">{event.summary}</span>
    </div>
  );
}

function AgentCard({ agent }: { agent: AgentActivity }) {
  const isActive = agent.status === 'active';
  const timeAgo = formatDistanceToNow(new Date(agent.lastTimestamp), { addSuffix: true });

  return (
    <div className={`glass-card rounded-xl overflow-hidden ${isActive ? 'glow-teal' : ''}`}>
      {/* Header */}
      <div className="p-4 pb-3">
        <div className="flex items-center gap-2 mb-2">
          <div className="relative">
            <div className={`w-3 h-3 rounded-full ${isActive ? 'bg-status-compliant' : 'bg-status-warning'}`} />
            {isActive && (
              <div className="absolute inset-0 w-3 h-3 rounded-full bg-status-compliant animate-ping opacity-40" />
            )}
          </div>

          <Link
            to={`/projects/${agent.projectName}`}
            className="font-heading text-sm font-semibold text-brand-peach hover:text-brand-teal transition-colors flex items-center gap-1"
          >
            {agent.projectName}
            <ChevronRight size={12} />
          </Link>

          <span className={`text-[10px] font-mono uppercase tracking-wider px-2 py-0.5 rounded-full ${
            isActive
              ? 'bg-status-compliant/10 text-status-compliant border border-status-compliant/20'
              : 'bg-status-warning/10 text-status-warning border border-status-warning/20'
          }`}>
            {isActive ? 'LIVE' : 'Recent'}
          </span>

          {agent.slug && (
            <span className="text-[10px] font-mono text-text-muted ml-auto">{agent.slug}</span>
          )}
        </div>

        {/* Current activity */}
        <div className="flex items-center gap-2 mb-3">
          {agent.currentTool && (
            <span className="text-[10px] font-mono bg-brand-teal/10 text-brand-teal px-1.5 py-0.5 rounded">
              {agent.currentTool}
            </span>
          )}
          <p className="text-xs text-text-primary truncate">{agent.lastMessage}</p>
        </div>

        {/* Stats row */}
        <div className="flex flex-wrap items-center gap-4 text-[10px] text-text-muted">
          <span className="flex items-center gap-1">
            <Clock size={10} /> {timeAgo}
          </span>
          {agent.duration && (
            <span className="flex items-center gap-1">
              <Zap size={10} /> {agent.duration}
            </span>
          )}
          {agent.gitBranch && (
            <span className="flex items-center gap-1">
              <GitBranch size={10} /> <span className="font-mono">{agent.gitBranch}</span>
            </span>
          )}
          {agent.subagentCount > 0 && (
            <span className="flex items-center gap-1">
              <Cpu size={10} /> {agent.subagentCount} sub-agent{agent.subagentCount > 1 ? 's' : ''}
            </span>
          )}
          <span className="font-mono">~{agent.entryCount} events</span>
          {agent.version && (
            <span className="font-mono">v{agent.version}</span>
          )}
        </div>
      </div>

      {/* Activity Feed */}
      {agent.recentEvents.length > 0 && (
        <div className="border-t border-border-default">
          <div className="px-4 py-2 bg-surface-elevated/50">
            <span className="text-[10px] text-text-muted uppercase tracking-wider font-semibold">
              Activity Feed
            </span>
          </div>
          <div className="px-4 pb-3 max-h-64 overflow-y-auto">
            {agent.recentEvents.map((event, i) => (
              <EventRow key={i} event={event} />
            ))}
          </div>
        </div>
      )}
    </div>
  );
}

export default function AgentsPage() {
  const { data, isLoading, error } = useQuery({
    queryKey: ['agents-live'],
    queryFn: fetchLiveAgents,
    refetchInterval: 3000, // 3s polling for live view
  });

  if (isLoading) {
    return (
      <div className="space-y-4 animate-fade-in">
        {[...Array(2)].map((_, i) => (
          <div key={i} className="glass-card rounded-xl h-48 animate-shimmer" />
        ))}
      </div>
    );
  }

  if (error) {
    return (
      <div className="glass-card rounded-xl p-8 text-center">
        <p className="text-status-critical mb-2">Failed to load agent data</p>
        <p className="text-text-muted text-sm">Is the server running on port 3001?</p>
      </div>
    );
  }

  const activeCount = data?.activeAgents.length ?? 0;
  const recentCount = data?.recentAgents.length ?? 0;
  const allAgents = [...(data?.activeAgents ?? []), ...(data?.recentAgents ?? [])];

  return (
    <div className="animate-fade-in">
      {/* Header */}
      <div className="mb-6">
        <div className="flex items-center gap-3 mb-1">
          <h1 className="text-2xl font-heading font-bold text-brand-peach">Agents</h1>
          {activeCount > 0 && (
            <span className="flex items-center gap-1.5 text-xs font-mono text-status-compliant bg-status-compliant/10 px-2.5 py-1 rounded-full border border-status-compliant/20">
              <span className="relative flex h-2 w-2">
                <span className="animate-ping absolute inline-flex h-full w-full rounded-full bg-status-compliant opacity-75" />
                <span className="relative inline-flex rounded-full h-2 w-2 bg-status-compliant" />
              </span>
              {activeCount} live
            </span>
          )}
        </div>
        <p className="text-sm text-text-muted">
          {activeCount} active, {recentCount} recent (24h) across {data?.totalSessions ?? 0} total sessions
        </p>
      </div>

      {/* Agent cards */}
      {allAgents.length === 0 ? (
        <div className="glass-card rounded-xl p-12 text-center">
          <Bot size={40} className="text-text-muted mx-auto mb-4" />
          <p className="text-text-muted">No active or recent agent sessions</p>
          <p className="text-text-muted text-xs mt-1">Sessions appear here when Claude Code is running on a project</p>
        </div>
      ) : (
        <div className="space-y-5">
          {allAgents.map(agent => (
            <AgentCard key={agent.sessionId} agent={agent} />
          ))}
        </div>
      )}
    </div>
  );
}

