import { Outlet, Link, useLocation } from 'react-router-dom';
import { useQuery } from '@tanstack/react-query';
import { fetchLiveAgents } from '../../services/api';
import { Radar, FolderGit2, Bot } from 'lucide-react';

export default function Layout() {
  const location = useLocation();

  const { data: agentData } = useQuery({
    queryKey: ['agents-live-header'],
    queryFn: fetchLiveAgents,
    refetchInterval: 5000,
  });

  const activeCount = agentData?.activeAgents.length ?? 0;

  const navItems = [
    { to: '/', label: 'Projects', icon: FolderGit2 },
    { to: '/agents', label: 'Agents', icon: Bot, badge: activeCount },
  ];

  return (
    <div className="min-h-screen relative">
      <div className="bg-pattern" />

      <header className="sticky top-0 z-50 glass-card border-b border-border-default">
        <div className="max-w-[1440px] mx-auto px-6 h-14 flex items-center gap-6">
          <Link to="/" className="flex items-center gap-2.5 shrink-0">
            <Radar size={22} className="text-brand-teal" />
            <span className="font-heading font-semibold text-lg text-text-primary">
              Mission Control
            </span>
          </Link>

          <nav className="flex items-center gap-1">
            {navItems.map(item => {
              const isActive = item.to === '/'
                ? location.pathname === '/' || location.pathname.startsWith('/projects/')
                : location.pathname === item.to;

              return (
                <Link
                  key={item.to}
                  to={item.to}
                  className={`flex items-center gap-1.5 px-3 py-1.5 rounded-lg text-xs font-medium transition-all ${
                    isActive
                      ? 'text-brand-teal bg-brand-teal/10'
                      : 'text-text-muted hover:text-text-primary hover:bg-surface-hover'
                  }`}
                >
                  <item.icon size={14} />
                  {item.label}
                  {item.badge !== undefined && item.badge > 0 && (
                    <span className="relative flex items-center">
                      <span className="flex items-center gap-1 text-[10px] font-mono bg-status-compliant/15 text-status-compliant px-1.5 py-0 rounded-full border border-status-compliant/25">
                        <span className="relative flex h-1.5 w-1.5">
                          <span className="animate-ping absolute inline-flex h-full w-full rounded-full bg-status-compliant opacity-75" />
                          <span className="relative inline-flex rounded-full h-1.5 w-1.5 bg-status-compliant" />
                        </span>
                        {item.badge}
                      </span>
                    </span>
                  )}
                </Link>
              );
            })}
          </nav>

          <div className="flex-1" />

          <span className="text-xs text-text-muted font-mono">localhost</span>
        </div>
      </header>

      <main className="relative z-10 max-w-[1440px] mx-auto px-6 py-6">
        <Outlet />
      </main>
    </div>
  );
}

