import { useQuery } from '@tanstack/react-query';
import { Link } from 'react-router-dom';
import { fetchLiveAgents } from '../services/api';
import { Bot, Zap, Clock, GitBranch, Cpu, ChevronRight, Terminal, MessageSquare } from 'lucide-react';
import { formatDistanceToNow } from 'date-fns';
import type { AgentActivity } from '../types';

function AgentRow({ agent }: { agent: AgentActivity }) {
  const isActive = agent.status === 'active';
  const timeAgo = formatDistanceToNow(new Date(agent.lastTimestamp), { addSuffix: true });

  return (
    <div className={`flex items-start gap-3 p-3 rounded-lg ${
      isActive ? 'bg-status-compliant/5 border border-status-compliant/20' : 'bg-surface-elevated'
    }`}>
      <div className="relative mt-1 shrink-0">
        <div className={`w-2.5 h-2.5 rounded-full ${isActive ? 'bg-status-compliant' : 'bg-status-warning'}`} />
        {isActive && (
          <div className="absolute inset-0 w-2.5 h-2.5 rounded-full bg-status-compliant animate-ping opacity-50" />
        )}
      </div>

      <div className="flex-1 min-w-0">
        <div className="flex items-center gap-2 mb-0.5">
          <Link
            to={`/projects/${agent.projectName}`}
            className="text-xs font-medium text-text-primary hover:text-brand-teal transition-colors"
          >
            {agent.projectName}
          </Link>
          {agent.slug && (
            <span className="text-[10px] font-mono text-text-muted">{agent.slug}</span>
          )}
          {isActive && (
            <span className="text-[10px] font-mono text-status-compliant bg-status-compliant/10 px-1.5 py-0 rounded-full">
              LIVE
            </span>
          )}
        </div>

        {/* Current activity */}
        <div className="flex items-center gap-1.5 mb-1">
          {agent.currentTool && (
            <span className="text-[10px] font-mono bg-brand-teal/10 text-brand-teal px-1 py-0 rounded">
              {agent.currentTool}
            </span>
          )}
          <p className="text-xs text-text-secondary truncate">{agent.lastMessage}</p>
        </div>

        {/* Last 3 events mini-feed */}
        {agent.recentEvents.length > 0 && (
          <div className="space-y-0.5 mt-1.5">
            {agent.recentEvents.slice(-3).map((event, i) => (
              <div key={i} className="flex items-center gap-1.5 text-[10px] text-text-muted">
                {event.type === 'tool_use' ? (
                  <Terminal size={8} className="text-brand-teal shrink-0" />
                ) : (
                  <MessageSquare size={8} className="text-text-muted shrink-0" />
                )}
                <span className="truncate">{event.summary}</span>
              </div>
            ))}
          </div>
        )}

        {/* Meta row */}
        <div className="flex flex-wrap items-center gap-3 text-[10px] text-text-muted mt-1.5">
          <span className="flex items-center gap-0.5">
            <Clock size={9} /> {timeAgo}
          </span>
          {agent.duration && (
            <span className="flex items-center gap-0.5">
              <Zap size={9} /> {agent.duration}
            </span>
          )}
          {agent.gitBranch && (
            <span className="flex items-center gap-0.5">
              <GitBranch size={9} /> {agent.gitBranch}
            </span>
          )}
          {agent.subagentCount > 0 && (
            <span className="flex items-center gap-0.5">
              <Cpu size={9} /> {agent.subagentCount} sub
            </span>
          )}
        </div>
      </div>
    </div>
  );
}

export default function LiveAgentsPanel() {
  const { data, isLoading } = useQuery({
    queryKey: ['agents-live'],
    queryFn: fetchLiveAgents,
    refetchInterval: 5000,
  });

  const activeCount = data?.activeAgents.length ?? 0;
  const recentCount = data?.recentAgents.length ?? 0;

  if (isLoading) {
    return (
      <div className="glass-card rounded-xl p-4 mb-6 animate-shimmer">
        <div className="h-16" />
      </div>
    );
  }

  if (activeCount === 0 && recentCount === 0) return null;

  return (
    <div className="glass-card rounded-xl p-5 mb-6">
      <div className="flex items-center gap-2 mb-3">
        <Bot size={16} className="text-status-ai" />
        <h3 className="font-heading text-sm font-semibold text-brand-peach">Live Agents</h3>
        {activeCount > 0 && (
          <span className="text-[10px] font-mono text-status-compliant bg-status-compliant/10 px-2 py-0.5 rounded-full">
            {activeCount} active
          </span>
        )}
        <Link
          to="/agents"
          className="ml-auto flex items-center gap-1 text-[10px] text-text-muted hover:text-brand-teal transition-colors"
        >
          View all <ChevronRight size={10} />
        </Link>
      </div>

      <div className="space-y-2">
        {(data?.activeAgents ?? []).map(agent => (
          <AgentRow key={agent.sessionId} agent={agent} />
        ))}
        {(data?.recentAgents ?? []).slice(0, 3).map(agent => (
          <AgentRow key={agent.sessionId} agent={agent} />
        ))}
      </div>
    </div>
  );
}

