          </span>
          {agent.slug && (
            <span className="text-[10px] font-mono text-text-muted">
              {agent.slug}
            </span>
          )}
          {isActive && (
            <span className="text-[10px] font-mono text-status-compliant bg-status-compliant/10 px-1.5 py-0 rounded-full">
              LIVE
            </span>
          )}
        </div>

        {/* What the agent is doing */}
        <p className="text-xs text-text-secondary truncate mb-1">
          {agent.lastMessage}
        </p>

        {/* Meta row */}
        <div className="flex flex-wrap items-center gap-3 text-[10px] text-text-muted">
          <span className="flex items-center gap-0.5">
            <Clock size={9} /> {timeAgo}
          </span>
          {agent.duration && (
            <span className="flex items-center gap-0.5">
              <Zap size={9} /> {agent.duration}
            </span>
          )}
          {agent.gitBranch && (
            <span className="flex items-center gap-0.5">
              <GitBranch size={9} /> {agent.gitBranch}
            </span>
          )}
          {agent.subagentCount > 0 && (
            <span className="flex items-center gap-0.5">
              <Cpu size={9} /> {agent.subagentCount} sub-agent{agent.subagentCount > 1 ? 's' : ''}
            </span>
          )}
          {agent.version && (
            <span className="font-mono">v{agent.version}</span>
          )}
        </div>
      </div>
