  });

  const activeCount = data?.activeAgents.length ?? 0;
  const recentCount = data?.recentAgents.length ?? 0;

  if (isLoading) {
    return (
      <div className="glass-card rounded-xl p-4 mb-6 animate-shimmer">
        <div className="h-16" />
