            {activeCount} active
          </span>
        )}
        <span className="text-[10px] text-text-muted ml-auto">
          {data?.totalSessions ?? 0} total sessions
        </span>
      </div>

      <div className="space-y-2">
        {(data?.activeAgents ?? []).map(agent => (
          <AgentRow key={agent.sessionId} agent={agent} />
        ))}
        {(data?.recentAgents ?? []).map(agent => (
          <AgentRow key={agent.sessionId} agent={agent} />
        ))}
      </div>
    </div>
  );
