import { useState, useMemo } from 'react';
import { useQuery, useQueryClient } from '@tanstack/react-query';
import { fetchProjects, bootstrapProject } from '../services/api';
import { Search } from 'lucide-react';
import StatsRow from '../components/StatsRow';
import ProjectCard from '../components/ProjectCard';
import BootstrapModal from '../components/BootstrapModal';
import LiveAgentsPanel from '../components/LiveAgentsPanel';
import type { ProjectSummary } from '../types';

type StatusFilter = 'all' | 'active' | 'stale' | 'unconfigured';

export default function ProjectsPage() {
  const queryClient = useQueryClient();
  const [bootstrapTarget, setBootstrapTarget] = useState<string | null>(null);
  const [isBootstrapping, setIsBootstrapping] = useState(false);
  const [search, setSearch] = useState('');
  const [statusFilter, setStatusFilter] = useState<StatusFilter>('all');

  const { data: projects, isLoading, error } = useQuery<ProjectSummary[]>({
    queryKey: ['projects'],
    queryFn: fetchProjects,
  });

  const filtered = useMemo(() => {
    let list = projects ?? [];

    if (search) {
      const q = search.toLowerCase();
      list = list.filter(p =>
        p.name.toLowerCase().includes(q) ||
        (p.description?.toLowerCase().includes(q) ?? false)
      );
    }

    if (statusFilter !== 'all') {
      list = list.filter(p => p.status === statusFilter);
    }

    return list;
  }, [projects, search, statusFilter]);

  const handleBootstrap = async () => {
    if (!bootstrapTarget) return;
    setIsBootstrapping(true);
    try {
      await bootstrapProject(bootstrapTarget);
      await queryClient.invalidateQueries({ queryKey: ['projects'] });
      setBootstrapTarget(null);
    } catch (err) {
      console.error('Bootstrap failed:', err);
    } finally {
      setIsBootstrapping(false);
    }
  };

  if (isLoading) {
    return (
      <div className="space-y-6 animate-fade-in">
        <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
          {[...Array(4)].map((_, i) => (
            <div key={i} className="glass-card rounded-xl p-4 h-20 animate-shimmer" />
          ))}
        </div>
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-5">
          {[...Array(6)].map((_, i) => (
            <div key={i} className="glass-card rounded-xl h-48 animate-shimmer" />
          ))}
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="glass-card rounded-xl p-8 text-center">
        <p className="text-status-critical mb-2">Failed to load projects</p>
        <p className="text-text-muted text-sm">Is the server running on port 3001?</p>
      </div>
    );
  }

  const filterTabs: { id: StatusFilter; label: string; count: number }[] = [
    { id: 'all', label: 'All', count: projects?.length ?? 0 },
    { id: 'active', label: 'Active', count: projects?.filter(p => p.status === 'active').length ?? 0 },
    { id: 'stale', label: 'Stale', count: projects?.filter(p => p.status === 'stale').length ?? 0 },
    { id: 'unconfigured', label: 'Unconfigured', count: projects?.filter(p => p.status === 'unconfigured').length ?? 0 },
  ];

  return (
    <div className="animate-fade-in">
      <div className="mb-6">
        <h1 className="text-2xl font-heading font-bold text-brand-peach mb-1">Projects</h1>
        <p className="text-sm text-text-muted">
          Monitoring {projects?.length ?? 0} project directories under /mnt/c/.netapps/
        </p>
      </div>

      <LiveAgentsPanel />

      <StatsRow projects={projects ?? []} />

      {/* Search + Filter bar */}
      <div className="flex flex-col sm:flex-row items-start sm:items-center gap-3 mb-5">
        <div className="relative flex-1 max-w-xs">
          <Search size={14} className="absolute left-3 top-1/2 -translate-y-1/2 text-text-muted" />
          <input
            type="text"
            placeholder="Search projects..."
            value={search}
            onChange={e => setSearch(e.target.value)}
            className="w-full pl-9 pr-3 py-2 text-xs bg-surface-card border border-border-default rounded-lg text-text-primary placeholder:text-text-muted focus:border-brand-teal focus:outline-none transition-colors"
          />
        </div>

        <div className="flex gap-1">
          {filterTabs.map(tab => (
            <button
              key={tab.id}
              onClick={() => setStatusFilter(tab.id)}
              className={`px-3 py-1.5 rounded-lg text-xs font-medium transition-all ${
                statusFilter === tab.id
                  ? 'glass-card-elevated text-brand-teal'
                  : 'text-text-muted hover:text-text-primary hover:bg-surface-hover'
              }`}
            >
              {tab.label}
              <span className="ml-1 text-[10px] opacity-60">{tab.count}</span>
            </button>
          ))}
        </div>
      </div>

      {/* Grid */}
      {filtered.length === 0 ? (
        <div className="glass-card rounded-xl p-8 text-center">
          <p className="text-text-muted text-sm">No projects match your filter</p>
        </div>
      ) : (
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-5">
          {filtered.map((project, i) => (
            <div key={project.name} className={`opacity-0 animate-fade-in-up stagger-${Math.min(i + 1, 6)}`}>
              <ProjectCard
                project={project}
                onBootstrap={(name) => setBootstrapTarget(name)}
              />
            </div>
          ))}
        </div>
      )}

      {bootstrapTarget && (
        <BootstrapModal
          projectName={bootstrapTarget}
          isLoading={isBootstrapping}
          onConfirm={handleBootstrap}
          onClose={() => setBootstrapTarget(null)}
        />
      )}
    </div>
  );
}

