 71→// ── EXECUTION-PLAN.md Parser ──
 72→
 73→export async function parseExecutionPlan(projectPath: string): Promise<RoadmapData> {
 74→  const content = await readFile(path.join(projectPath, 'EXECUTION-PLAN.md'), 'utf-8');
 75→
 76→  const phases: Phase[] = [];
 77→
 78→  // Split by phase headers: ## PHASE N: NAME
 79→  const phaseRegex = /##\s*PHASE\s+(\d+):\s*(.+?)(?:\s*\(.*?\))?\s*\n([\s\S]*?)(?=##\s*PHASE|\n---\s*\n\s*$|$)/gi;
 80→  let phaseMatch;
 81→
 82→  while ((phaseMatch = phaseRegex.exec(content)) !== null) {
 83→    const phaseId = phaseMatch[1]!;
 84→    const phaseName = phaseMatch[2]!.trim();
 85→    const phaseBody = phaseMatch[3]!;
 86→
 87→    // Extract goal
 88→    const goalMatch = phaseBody.match(/###\s*Goal:\s*(.+)/i);
 89→    const goal = goalMatch?.[1]?.trim();
 90→
 91→    // Parse task table rows: | N.N | Task | Status/Priority | ... |
 92→    const tasks: PlanTask[] = [];
 93→    const tableRowRegex = /\|\s*(\d+\.\d+)\s*\|\s*(.+?)\s*\|\s*(.+?)\s*\|\s*(.+?)\s*\|(?:\s*(.+?)\s*\|)?/g;
 94→    let rowMatch;
 95→
 96→    while ((rowMatch = tableRowRegex.exec(phaseBody)) !== null) {
 97→      const id = rowMatch[1]!.trim();
 98→      const task = rowMatch[2]!.replace(/\*\*/g, '').trim();
 99→      const col3 = rowMatch[3]!.trim();
100→      const col4 = rowMatch[4]!.trim();
101→      const col5 = rowMatch[5]?.trim();
102→
103→      // Detect if this is the Phase 0 format (Status column) or Phase 1+ format (Priority column)
104→      const isDone = /DONE|complete/i.test(col3) || /DONE|complete/i.test(col4);
105→      const status = isDone ? 'DONE' : 'TODO';
106→
107→      tasks.push({
108→        id,
109→        task,
110→        status,
111→        priority: /^P\d/.test(col3) ? col3 : undefined,
112→        complexity: /Low|Medium|High/i.test(col4) ? col4 : undefined,
113→        agent: col5 ?? col4,
114→      });
115→    }
116→
117→    const completedTasks = tasks.filter(t => t.status === 'DONE').length;
118→    const totalTasks = tasks.length;
119→    const progress = totalTasks > 0 ? Math.round((completedTasks / totalTasks) * 100) : 0;
120→
121→    phases.push({
122→      id: phaseId,
123→      name: phaseName,
124→      goal,
125→      tasks,
126→      totalTasks,
127→      completedTasks,
128→      progress,
129→    });
130→  }
131→
132→  const totalTasks = phases.reduce((sum, p) => sum + p.totalTasks, 0);
133→  const completedTasks = phases.reduce((sum, p) => sum + p.completedTasks, 0);
