// ── EXECUTION-PLAN.md Parser ──

export async function parseExecutionPlan(projectPath: string): Promise<RoadmapData> {
  const content = await readFile(path.join(projectPath, 'EXECUTION-PLAN.md'), 'utf-8');

  const phases: Phase[] = [];

  // Split by phase headers: ## PHASE N: NAME
  const phaseRegex = /##\s*PHASE\s+(\d+):\s*(.+?)(?:\s*\(.*?\))?\s*\n([\s\S]*?)(?=##\s*PHASE|\n---\s*\n\s*$|$)/gi;
  let phaseMatch;

  while ((phaseMatch = phaseRegex.exec(content)) !== null) {
    const phaseId = phaseMatch[1]!;
    const phaseName = phaseMatch[2]!.trim();
    const phaseBody = phaseMatch[3]!;

    // Extract goal
    const goalMatch = phaseBody.match(/###\s*Goal:\s*(.+)/i);
    const goal = goalMatch?.[1]?.trim();

    // Parse task table rows: | N.N | Task | Status/Priority | ... |
    const tasks: PlanTask[] = [];
    const tableRowRegex = /\|\s*(\d+\.\d+)\s*\|\s*(.+?)\s*\|\s*(.+?)\s*\|\s*(.+?)\s*\|(?:\s*(.+?)\s*\|)?/g;
    let rowMatch;

    while ((rowMatch = tableRowRegex.exec(phaseBody)) !== null) {
      const id = rowMatch[1]!.trim();
      const task = rowMatch[2]!.replace(/\*\*/g, '').trim();
      const col3 = rowMatch[3]!.trim();
      const col4 = rowMatch[4]!.trim();
      const col5 = rowMatch[5]?.trim();

      // Detect if this is the Phase 0 format (Status column) or Phase 1+ format (Priority column)
      const isDone = /DONE|complete/i.test(col3) || /DONE|complete/i.test(col4);
      const status = isDone ? 'DONE' : 'TODO';

      tasks.push({
        id,
        task,
        status,
        priority: /^P\d/.test(col3) ? col3 : undefined,
        complexity: /Low|Medium|High/i.test(col4) ? col4 : undefined,
        agent: col5 ?? col4,
      });
    }

    const completedTasks = tasks.filter(t => t.status === 'DONE').length;
    const totalTasks = tasks.length;
    const progress = totalTasks > 0 ? Math.round((completedTasks / totalTasks) * 100) : 0;

    phases.push({
      id: phaseId,
      name: phaseName,
      goal,
      tasks,
      totalTasks,
      completedTasks,
      progress,
    });
  }

  const totalTasks = phases.reduce((sum, p) => sum + p.totalTasks, 0);
  const completedTasks = phases.reduce((sum, p) => sum + p.completedTasks, 0);
  const overallProgress = totalTasks > 0 ? Math.round((completedTasks / totalTasks) * 100) : 0;

  return { phases, totalTasks, completedTasks, overallProgress };
}

// ── SESSION-HISTORY.md Parser ──

export async function parseSessionHistory(projectPath: string): Promise<SessionData> {
  const content = await readFile(path.join(projectPath, 'SESSION-HISTORY.md'), 'utf-8');

  const sessions: Session[] = [];

  // Split by session headers: ## Session N — DATE
  const sessionRegex = /##\s*Session\s+(\d+)\s*[—–-]\s*(.+?)\n([\s\S]*?)(?=##\s*Session\s+\d|$)/gi;
  let sessionMatch;

  while ((sessionMatch = sessionRegex.exec(content)) !== null) {
    const id = sessionMatch[1]!;
    const dateInfo = sessionMatch[2]!.trim();
    const body = sessionMatch[3]!;

    // Extract date portion (before parenthetical)
    const dateClean = dateInfo.replace(/\s*\(.*?\)\s*$/, '').trim();

    // Extract agent
    const agentMatch = body.match(/\*\*Agent:\*\*\s*(.+)/);
    const agent = agentMatch?.[1]?.trim();

    // Extract working directory
    const wdMatch = body.match(/\*\*Working directory:\*\*\s*`?(.+?)`?\s*\n/);
    const workingDirectory = wdMatch?.[1]?.trim();

    // Extract summary items (lines under "What happened" or "What was done")
    const summary: string[] = [];
    const summaryMatch = body.match(/###?\s*What (?:happened|was done):?\s*\n([\s\S]*?)(?=###|\n---|\n\*\*|$)/i);
    if (summaryMatch) {
      const lines = summaryMatch[1]!.split('\n');
      for (const line of lines) {
        const cleaned = line.replace(/^[-*]\s*/, '').trim();
        if (cleaned && !cleaned.startsWith('#') && !cleaned.startsWith('|')) {
          summary.push(cleaned);
        }
      }
    }

    // Extract commits
    const commits: string[] = [];
    const commitsMatch = body.match(/###?\s*Commits:?\s*\n([\s\S]*?)(?=###|\n---|\n\*\*|$)/i);
    if (commitsMatch) {
      const lines = commitsMatch[1]!.split('\n');
      for (const line of lines) {
        const cleaned = line.replace(/^[-*]\s*/, '').trim();
        if (cleaned && cleaned.startsWith('`')) {
          commits.push(cleaned.replace(/`/g, ''));
        }
      }
    }

    // Extract decisions
    const decisions: string[] = [];
    const decisionsMatch = body.match(/###?\s*(?:Key )?[Dd]ecisions:?\s*\n([\s\S]*?)(?=###|\n---|\n\*\*|$)/i);
    if (decisionsMatch) {
      const lines = decisionsMatch[1]!.split('\n');
      for (const line of lines) {
        const cleaned = line.replace(/^[-*]\s*/, '').trim();
        if (cleaned) decisions.push(cleaned);
      }
    }

    // Extract pending work
    const pendingWork: string[] = [];
    const pendingMatch = body.match(/###?\s*(?:Pending|Outstanding|Next):?\s*\n([\s\S]*?)(?=###|\n---|\n\*\*|$)/i);
    if (pendingMatch) {
      const lines = pendingMatch[1]!.split('\n');
      for (const line of lines) {
        const cleaned = line.replace(/^[-*]\s*/, '').trim();
        if (cleaned) pendingWork.push(cleaned);
      }
    }

    sessions.push({
      id,
      date: dateClean,
      agent,
      workingDirectory,
      summary,
      commits,
      decisions,
      pendingWork,
    });
  }

  // Reverse so newest first
  sessions.reverse();

  return { sessions };
}

