// Mirror of server types for the frontend

export interface ProjectSummary {
  name: string;
  path: string;
  status: 'active' | 'stale' | 'unconfigured';
  description?: string;
  gitBranch?: string;
  lastCommitTime?: string;
  lastCommitMessage?: string;
  testPassRate?: number;
  bugCount?: number;
  warningCount?: number;
  currentPhase?: string;
  phaseProgress?: number;
  isGitRepo: boolean;
  isBootstrapped: boolean;
}

export interface GitCommit {
  hash: string;
  shortHash: string;
  message: string;
  author: string;
  date: string;
  relativeDate: string;
}

export interface GitStatus {
  branch: string;
  remoteUrl?: string;
  dirty: boolean;
  dirtyFiles: string[];
  ahead: number;
  behind: number;
}

export interface GitData {
  status: GitStatus;
  commits: GitCommit[];
}

export interface Bug {
  id: string;
  title: string;
  page: string;
  severity: string;
  description: string;
  status?: string;
}

export interface Warning {
  id: string;
  title: string;
  page: string;
  severity: string;
  description: string;
}

export interface TestData {
  totalTests: number;
  passing: number;
  failing: number;
  warnings: number;
  passRate: number;
  bugs: Bug[];
  warningList: Warning[];
  generatedDate?: string;
}

export interface PlanTask {
  id: string;
  task: string;
  status: string;
  priority?: string;
  complexity?: string;
  agent?: string;
}

export interface Phase {
