const content = await readFile('/mnt/c/.REDACTED.md', 'utf-8');

// Test phase regex
const phaseRegex = /##\s*PHASE\s+(\d+):\s*(.+?)(?:\s*\(.*?\))?\s*\n([\s\S]*?)(?=##\s*PHASE|\n---\s*\n\s*$|$)/gi;
let m;
while ((m = phaseRegex.exec(content)) !== null) {
  const phaseId = m[1]!;
  const phaseName = m[2]!.trim();
  const body = m[3]!;
  console.log(`Phase ${phaseId}: ${phaseName} (body: ${body.length} chars)`);

  // Count table rows in body
  const tableRowRegex = /\|\s*(\d+\.\d+)\s*\|\s*(.+?)\s*\|\s*(.+?)\s*\|\s*(.+?)\s*\|(?:\s*(.+?)\s*\|)?/g;
  let rowMatch;
  let count = 0;
  while ((rowMatch = tableRowRegex.exec(body)) !== null) {
    count++;
    const id = rowMatch[1]!.trim();
    const col3 = rowMatch[3]!.trim();
    const col4 = rowMatch[4]!.trim();
    const col5 = rowMatch[5]?.trim();
    const isDone = /DONE|complete/i.test(col3) || /DONE|complete/i.test(col4);
    const agentName = col5 ? col5 : col4;
    console.log(`  ${id}: col3="${col3}" col4="${col4}" col5="${col5}" isDone=${isDone} agent="${agentName}"`);
  }
  console.log(`  Total rows matched: ${count}`);
}

