import { useState } from 'react';
import { useParams } from 'react-router-dom';
import { useQuery } from '@tanstack/react-query';
import { fetchProjectDetail } from '../services/api';
import { GitBranch, Target, ShieldCheck, Bot, Server, Users } from 'lucide-react';
import type { ProjectDetail } from '../types';
import OverviewTab from '../components/detail/OverviewTab';
import PipelineTab from '../components/detail/PipelineTab';
import ProgressTab from '../components/detail/ProgressTab';
import QualityTab from '../components/detail/QualityTab';
import SessionsTab from '../components/detail/SessionsTab';
import InfraTab from '../components/detail/InfraTab';

const TABS = [
  { id: 'overview', label: 'Overview', icon: GitBranch },
  { id: 'pipeline', label: 'Pipeline', icon: Users },
  { id: 'progress', label: 'Progress', icon: Target },
  { id: 'quality', label: 'Quality', icon: ShieldCheck },
  { id: 'sessions', label: 'Sessions', icon: Bot },
  { id: 'infra', label: 'Infrastructure', icon: Server },
] as const;

type TabId = typeof TABS[number]['id'];

export default function ProjectDetailPage() {
  const { name } = useParams<{ name: string }>();
  const [activeTab, setActiveTab] = useState<TabId>('overview');

  const { data: project, isLoading, error } = useQuery<ProjectDetail>({
    queryKey: ['project', name],
    queryFn: () => fetchProjectDetail(name!),
    enabled: !!name,
  });

  if (isLoading) {
    return (
      <div className="space-y-4 animate-fade-in">
        <div className="glass-card rounded-xl h-20 animate-shimmer" />
        <div className="glass-card rounded-xl h-12 animate-shimmer" />
        <div className="glass-card rounded-xl h-64 animate-shimmer" />
      </div>
    );
  }

  if (error || !project) {
    return (
      <div className="glass-card rounded-xl p-8 text-center">
        <p className="text-status-critical mb-2">Failed to load project</p>
        <p className="text-text-muted text-sm">Project "{name}" not found or server unavailable</p>
      </div>
    );
  }

  const statusConfig = {
    active: { dot: 'bg-status-compliant', label: 'Active' },
    stale: { dot: 'bg-status-warning', label: 'Stale' },
    unconfigured: { dot: 'bg-text-muted', label: 'Unconfigured' },
  };
  const sc = statusConfig[project.status];

  return (
    <div className="animate-fade-in">
      {/* Project header */}
      <div className="glass-card rounded-xl p-5 mb-5">
        <div className="flex items-center gap-3 mb-1">
          <span className={`w-3 h-3 rounded-full ${sc.dot}`} />
          <h1 className="text-xl font-heading font-bold text-brand-peach">{project.name}</h1>
          <span className="text-xs font-mono text-text-muted bg-surface-elevated px-2 py-0.5 rounded-full">
            {sc.label}
          </span>
        </div>
        {project.description && (
          <p className="text-sm text-text-secondary ml-6">{project.description}</p>
        )}

        {/* Quick stats */}
        <div className="flex flex-wrap items-center gap-5 mt-3 ml-6 text-xs">
          {project.gitBranch && (
            <span className="flex items-center gap-1 text-text-muted">
              <GitBranch size={12} className="text-brand-teal" />
              <span className="font-mono">{project.gitBranch}</span>
            </span>
          )}
          {project.testPassRate !== undefined && (
            <span className={`font-mono ${
              project.testPassRate >= 90 ? 'text-status-compliant' :
              project.testPassRate >= 70 ? 'text-status-warning' : 'text-status-critical'
            }`}>
              {project.testPassRate}% pass rate
            </span>
          )}
          {project.roadmap && (
            <span className="text-text-muted">
              {project.roadmap.overallProgress}% roadmap complete
            </span>
          )}
        </div>
      </div>

      {/* Tab bar */}
      <div className="flex gap-1 mb-5 overflow-x-auto pb-1">
        {TABS.map(tab => {
          const isActive = activeTab === tab.id;
          return (
            <button
              key={tab.id}
              onClick={() => setActiveTab(tab.id)}
              className={`flex items-center gap-1.5 px-4 py-2 rounded-lg text-xs font-medium whitespace-nowrap transition-all ${
                isActive
                  ? 'glass-card-elevated text-brand-teal glow-teal'
                  : 'text-text-muted hover:text-text-primary hover:bg-surface-hover'
              }`}
            >
              <tab.icon size={14} />
              {tab.label}
            </button>
          );
        })}
      </div>

      {/* Tab content */}
      <div>
        {activeTab === 'overview' && <OverviewTab project={project} />}
        {activeTab === 'pipeline' && <PipelineTab projectName={project.name} />}
        {activeTab === 'progress' && <ProgressTab project={project} />}
        {activeTab === 'quality' && <QualityTab project={project} />}
        {activeTab === 'sessions' && <SessionsTab project={project} />}
        {activeTab === 'infra' && <InfraTab project={project} />}
      </div>
    </div>
  );
}

