  1→import { readFile, readdir, stat, access } from 'fs/promises';
  2→import path from 'path';
  3→
  4→// ── Types ──
  5→
  6→export interface AgentRole {
  7→  name: string;          // e.g. "Fullstack", "Frontend", "Tester", "BA", "DevOps"
  8→  responsibility: string;
  9→  keyFiles: string[];
 10→  pipelineOrder: number; // 0=BA, 1=Fullstack, 2=Frontend, 3=Tester, 4=DevOps
 11→}
 12→
 13→export interface AgentTask {
 14→  id: string;
 15→  task: string;
 16→  status: 'DONE' | 'TODO' | 'IN_PROGRESS';
 17→  phase: string;
 18→  phaseId: string;
 19→  priority?: string;
 20→  complexity?: string;
 21→}
 22→
 23→export interface AgentDocument {
 24→  name: string;
 25→  path: string;
 26→  sizeKb: number;
 27→  lastModified: string;
 28→  producer: string;      // Which agent produced this
 29→}
 30→
 31→export interface PipelineAgent {
 32→  role: AgentRole;
 33→  status: 'idle' | 'active' | 'blocked' | 'complete';
 34→  tasks: AgentTask[];
 35→  totalTasks: number;
 36→  completedTasks: number;
 37→  progress: number;
 38→  documents: AgentDocument[];
 39→  currentPhase?: string; // Which phase this agent is currently working in
 40→  blockedReason?: string;
 41→}
 42→
 43→export interface PipelineData {
 44→  agents: PipelineAgent[];
 45→  currentPhase: string;
 46→  currentPhaseId: string;
 47→  overallProgress: number;
 48→  totalTasks: number;
 49→  completedTasks: number;
 50→}
 51→
 52→// ── Pipeline order (left to right) ──
 53→
 54→const PIPELINE_ORDER: Record<string, number> = {
 55→  'ba': 0,
 56→  'ba agent': 0,
 57→  'system analyst': 0,
 58→  'claude code': 1,
 59→  'fullstack': 2,
 60→  'frontend': 3,
 61→  'frontend agent': 3,
 62→  'tester': 4,
 63→  'devops': 5,
 64→};
 65→
 66→const ROLE_DEFAULTS: Record<string, { responsibility: string; keyFiles: string[] }> = {
 67→  'BA': { responsibility: 'Requirements, user stories, acceptance criteria', keyFiles: ['Doc1-*.txt', 'Doc2-*.txt', 'Doc3-*.txt'] },
 68→  'Claude Code': { responsibility: 'Infrastructure, tooling, session management', keyFiles: ['CLAUDE.md', 'SESSION-HISTORY.md', 'EXECUTION-PLAN.md'] },
 69→  'Fullstack': { responsibility: 'Server routes, API integrations, data models', keyFiles: ['server/src/'] },
 70→  'Frontend': { responsibility: 'UI components, pages, styling', keyFiles: ['app/src/pages/', 'app/src/components/'] },
 71→  'Tester': { responsibility: 'Selenium tests, unit tests, integration tests', keyFiles: ['tests/', 'DEV-FIX-LIST.md'] },
 72→  'DevOps': { responsibility: 'CI/CD, monitoring, deployment, security', keyFiles: ['cloudbuild.yaml', 'Dockerfile', 'DEVOPS.md'] },
 73→};
 74→
 75→// ── Document detection ──
 76→
 77→async function exists(p: string): Promise<boolean> {
 78→  try { await access(p); return true; } catch { return false; }
 79→}
 80→
 81→/** Scan for known document files and attribute to agent */
 82→async function scanDocuments(projectPath: string): Promise<AgentDocument[]> {
 83→  const docs: AgentDocument[] = [];
 84→
 85→  const docPatterns: { pattern: RegExp; producer: string }[] = [
 86→    { pattern: /^Doc1-.*\.txt$/i, producer: 'BA' },
 87→    { pattern: /^Doc2-.*\.txt$/i, producer: 'BA' },
 88→    { pattern: /^Doc3-.*\.txt$/i, producer: 'BA' },
 89→    { pattern: /^Doc4-.*\.md$/i, producer: 'Frontend' },
 90→    { pattern: /^CLAUDE\.md$/i, producer: 'Claude Code' },
 91→    { pattern: /^SESSION-HISTORY\.md$/i, producer: 'Claude Code' },
 92→    { pattern: /^DEV-FIX-LIST\.md$/i, producer: 'Tester' },
 93→    { pattern: /^EXECUTION-PLAN\.md$/i, producer: 'Claude Code' },
 94→    { pattern: /^DEVOPS\.md$/i, producer: 'DevOps' },
 95→    { pattern: /^cloudbuild\.yaml$/i, producer: 'DevOps' },
 96→    { pattern: /^Dockerfile$/i, producer: 'DevOps' },
 97→  ];
 98→
 99→  try {
100→    const entries = await readdir(projectPath);
101→    for (const entry of entries) {
102→      for (const { pattern, producer } of docPatterns) {
103→        if (pattern.test(entry)) {
104→          const fullPath = path.join(projectPath, entry);
105→          const fileStat = await stat(fullPath);
106→          docs.push({
107→            name: entry,
108→            path: entry,
109→            sizeKb: Math.round(fileStat.size / 1024),
110→            lastModified: fileStat.mtime.toISOString(),
111→            producer,
112→          });
113→          break;
114→        }
115→      }
116→    }
117→  } catch { /* skip */ }
118→
119→  return docs;
120→}
121→
122→// ── Main parser ──
123→
124→export async function parsePipelineData(projectPath: string): Promise<PipelineData | null> {
125→  const planPath = path.join(projectPath, 'EXECUTION-PLAN.md');
126→  if (!(await exists(planPath))) return null;
127→
128→  const content = await readFile(planPath, 'utf-8');
129→
130→  // Parse agent roles from ## AGENT ROLES table
131→  const parsedRoles: Map<string, AgentRole> = new Map();
132→  const rolesMatch = content.match(/##\s*AGENT ROLES\s*\n([\s\S]*?)(?=\n---|\n##)/i);
133→  if (rolesMatch) {
134→    const roleRowRegex = /\|\s*\*\*(.+?)\*\*\s*\|\s*(.+?)\s*\|\s*`?(.+?)`?\s*\|/g;
135→    let rm;
136→    while ((rm = roleRowRegex.exec(rolesMatch[1]!)) !== null) {
137→      const name = rm[1]!.trim();
138→      const responsibility = rm[2]!.trim();
139→      const keyFiles = rm[3]!.split(',').map(f => f.trim().replace(/`/g, ''));
140→      const order = PIPELINE_ORDER[name.toLowerCase()] ?? 99;
141→      parsedRoles.set(name, { name, responsibility, keyFiles, pipelineOrder: order });
142→    }
143→  }
144→
145→  // Ensure default roles exist
146→  for (const [name, defaults] of Object.entries(ROLE_DEFAULTS)) {
147→    if (!parsedRoles.has(name)) {
148→      parsedRoles.set(name, {
149→        name,
150→        responsibility: defaults.responsibility,
151→        keyFiles: defaults.keyFiles,
152→        pipelineOrder: PIPELINE_ORDER[name.toLowerCase()] ?? 99,
153→      });
154→    }
155→  }
156→
157→  // Parse all tasks from all phases with agent assignments
158→  const agentTasks: Map<string, AgentTask[]> = new Map();
159→
160→  const phaseRegex = /##\s*PHASE\s+(\d+):\s*(.+?)(?:\s*\(.*?\))?\s*\n([\s\S]*?)(?=##\s*PHASE|\n---\s*\n\s*$|$)/gi;
161→  let phaseMatch;
162→  let latestActivePhaseId = '0';
163→  let latestActivePhaseName = 'INFRASTRUCTURE';
164→
165→  while ((phaseMatch = phaseRegex.exec(content)) !== null) {
166→    const phaseId = phaseMatch[1]!;
167→    const phaseName = phaseMatch[2]!.trim();
168→    const phaseBody = phaseMatch[3]!;
169→
170→    // Parse task rows line-by-line to avoid cross-line matching
171→    const lines = phaseBody.split('\n');
172→    const tableRowRegex = /^\|\s*(\d+\.\d+)\s*\|\s*(.+?)\s*\|\s*(.+?)\s*\|\s*(.+?)\s*\|(?:\s*(.+?)\s*\|)?\s*$/;
173→    let phaseHasTodo = false;
174→    let phaseHasDone = false;
175→
176→    for (const line of lines) {
177→      const rowMatch = tableRowRegex.exec(line);
178→      if (!rowMatch) continue;
179→      const id = rowMatch[1]!.trim();
180→      const task = rowMatch[2]!.replace(/\*\*/g, '').trim();
181→      const col3 = rowMatch[3]!.trim();
182→      const col4 = rowMatch[4]!.trim();
183→      const col5 = rowMatch[5]?.trim();
184→
185→      const isDone = /DONE|complete/i.test(col3) || /DONE|complete/i.test(col4);
186→      const status: AgentTask['status'] = isDone ? 'DONE' : 'TODO';
187→
188→      if (isDone) phaseHasDone = true;
189→      else phaseHasTodo = true;
190→
191→      // Determine agent name — last column is always agent
192→      let agentName: string;
193→      if (col5) {
194→        agentName = col5;
195→      } else {
196→        // Phase 0 format: | # | Task | Status | Agent |
197→        agentName = col4;
198→      }
199→
200→      // Normalize agent name
201→      agentName = agentName.replace(/\*\*/g, '').trim();
202→      if (agentName === '-' || agentName === '') agentName = 'Unassigned';
203→
204→      // Map aliases
205→      if (/^ba$/i.test(agentName) || /ba agent/i.test(agentName)) agentName = 'BA';
206→      if (/frontend agent/i.test(agentName)) agentName = 'Frontend';
207→
208→      if (!agentTasks.has(agentName)) agentTasks.set(agentName, []);
209→      agentTasks.get(agentName)!.push({
210→        id,
211→        task,
212→        status,
213→        phase: phaseName,
214→        phaseId,
215→        priority: /^P\d/.test(col3) ? col3 : undefined,
216→        complexity: /Low|Medium|High/i.test(col4) ? col4 : undefined,
217→      });
218→    }
219→
220→    // Track current phase (has both done and todo, or first with only todo)
221→    if (phaseHasTodo && phaseHasDone) {
222→      latestActivePhaseId = phaseId;
223→      latestActivePhaseName = phaseName;
224→    } else if (phaseHasTodo && !phaseHasDone && latestActivePhaseId === '0') {
225→      latestActivePhaseId = phaseId;
226→      latestActivePhaseName = phaseName;
227→    }
228→  }
229→
230→  // Scan documents
231→  const documents = await scanDocuments(projectPath);
232→
233→  // Build pipeline agents
234→  const agents: PipelineAgent[] = [];
235→  let totalTasks = 0;
236→  let completedTasks = 0;
237→
238→  for (const [, role] of parsedRoles) {
239→    const tasks = agentTasks.get(role.name) ?? [];
240→    const done = tasks.filter(t => t.status === 'DONE').length;
241→    const total = tasks.length;
242→    const progress = total > 0 ? Math.round((done / total) * 100) : 0;
243→
244→    totalTasks += total;
245→    completedTasks += done;
246→
247→    // Determine agent documents
248→    const agentDocs = documents.filter(d => d.producer === role.name);
249→
250→    // Determine status
251→    let status: PipelineAgent['status'];
252→    if (total === 0) {
253→      status = 'idle';
254→    } else if (done === total) {
255→      status = 'complete';
256→    } else if (done > 0) {
257→      status = 'active';
258→    } else {
259→      // Check if prerequisite agents are done
260→      const prereqAgents = [...parsedRoles.values()]
261→        .filter(r => r.pipelineOrder < role.pipelineOrder);
262→      const prereqsDone = prereqAgents.every(pa => {
263→        const paTasks = agentTasks.get(pa.name) ?? [];
264→        return paTasks.length === 0 || paTasks.every(t => t.status === 'DONE');
265→      });
266→      status = prereqsDone ? 'idle' : 'blocked';
267→    }
268→
269→    // Current phase for this agent
270→    const activePhaseTasks = tasks.filter(t => t.status === 'TODO');
271→    const currentPhase = activePhaseTasks.length > 0 ? activePhaseTasks[0]!.phase : undefined;
272→
273→    agents.push({
274→      role,
275→      status,
276→      tasks,
277→      totalTasks: total,
278→      completedTasks: done,
279→      progress,
280→      documents: agentDocs,
281→      currentPhase,
282→    });
283→  }
284→
285→  // Also add Claude Code tasks if present
286→  const ccTasks = agentTasks.get('Claude Code') ?? [];
287→  if (ccTasks.length > 0 && !agents.find(a => a.role.name === 'Claude Code')) {
288→    const done = ccTasks.filter(t => t.status === 'DONE').length;
289→    agents.push({
290→      role: parsedRoles.get('Claude Code')!,
291→      status: done === ccTasks.length ? 'complete' : 'active',
292→      tasks: ccTasks,
293→      totalTasks: ccTasks.length,
294→      completedTasks: done,
295→      progress: Math.round((done / ccTasks.length) * 100),
296→      documents: documents.filter(d => d.producer === 'Claude Code'),
297→    });
298→  }
299→
300→  // Sort by pipeline order
301→  agents.sort((a, b) => a.role.pipelineOrder - b.role.pipelineOrder);
302→
303→  const overallProgress = totalTasks > 0 ? Math.round((completedTasks / totalTasks) * 100) : 0;
304→
305→  return {
306→    agents,
307→    currentPhase: latestActivePhaseName,
308→    currentPhaseId: latestActivePhaseId,
309→    overallProgress,
310→    totalTasks,
311→    completedTasks,
312→  };
313→}
314→

 1→import express from 'express';
