 67→          <h1 className="text-xl font-heading font-bold text-brand-peach">{project.name}</h1>
 68→          <span className="text-xs font-mono text-text-muted bg-surface-elevated px-2 py-0.5 rounded-full">
 69→            {sc.label}
 70→          </span>
 71→        </div>
 72→        {project.description && (
 73→          <p className="text-sm text-text-secondary ml-6">{project.description}</p>
