  8→function LiveAgentCard({ agent }: { agent: AgentActivity }) {
  9→  const isActive = agent.status === 'active';
 10→  const timeAgo = formatDistanceToNow(new Date(agent.lastTimestamp), { addSuffix: true });
 11→
 12→  return (
 13→    <div className={`p-4 rounded-xl ${
 14→      isActive
 15→        ? 'bg-status-compliant/5 border border-status-compliant/20 glow-teal'
 16→        : 'bg-surface-elevated border border-border-subtle'
 17→    }`}>
 18→      <div className="flex items-center gap-2 mb-2">
 19→        <div className="relative">
 20→          <div className={`w-2.5 h-2.5 rounded-full ${isActive ? 'bg-status-compliant' : 'bg-status-warning'}`} />
 21→          {isActive && (
 22→            <div className="absolute inset-0 w-2.5 h-2.5 rounded-full bg-status-compliant animate-ping opacity-50" />
 23→          )}
 24→        </div>
 25→        <span className={`text-[10px] font-mono uppercase tracking-wider px-2 py-0.5 rounded-full ${
 26→          isActive
 27→            ? 'bg-status-compliant/10 text-status-compliant'
 28→            : 'bg-status-warning/10 text-status-warning'
 29→        }`}>
 30→          {isActive ? 'LIVE NOW' : 'Recent'}
 31→        </span>
 32→        {agent.slug && (
 33→          <span className="text-[10px] font-mono text-text-muted">{agent.slug}</span>
 34→        )}
 35→      </div>
 36→
 37→      <p className="text-xs text-text-secondary mb-2 line-clamp-2">{agent.lastMessage}</p>
 38→
 39→      <div className="flex flex-wrap items-center gap-3 text-[10px] text-text-muted">
 40→        <span className="flex items-center gap-0.5">
 41→          <Clock size={9} /> {timeAgo}
 42→        </span>
 43→        {agent.duration && (
 44→          <span className="flex items-center gap-0.5">
 45→            <Zap size={9} /> {agent.duration}
 46→          </span>
 47→        )}
 48→        {agent.subagentCount > 0 && (
 49→          <span className="flex items-center gap-0.5">
 50→            <Cpu size={9} /> {agent.subagentCount} sub-agent{agent.subagentCount > 1 ? 's' : ''}
 51→          </span>
 52→        )}
 53→        <span className="font-mono">{agent.entryCount} events</span>
 54→      </div>
 55→    </div>
 56→  );
 57→}
