 63→export default function SessionsTab({ project }: Props) {
 64→  const sessions = project.sessions?.sessions ?? [];
 65→  const [expanded, setExpanded] = useState<Set<string>>(new Set(sessions[0]?.id ? [sessions[0].id] : []));
 66→
 67→  const { data: liveAgents } = useQuery({
 68→    queryKey: ['agents-project', project.name],
 69→    queryFn: () => fetchProjectAgents(project.name),
 70→    refetchInterval: 5000,
 71→  });
 72→
 73→  const toggle = (id: string) => {
 74→    setExpanded(prev => {
 75→      const next = new Set(prev);
 76→      if (next.has(id)) next.delete(id);
 77→      else next.add(id);
 78→      return next;
 79→    });
 80→  };
 81→
 82→  if (sessions.length === 0 && (!liveAgents || liveAgents.length === 0)) {
 83→    return (
 84→      <div className="glass-card rounded-xl p-8 text-center">
 85→        <Bot size={32} className="text-text-muted mx-auto mb-3" />
 86→        <p className="text-text-muted text-sm">No session history found</p>
 87→        <p className="text-text-muted text-xs mt-1">Create a SESSION-HISTORY.md to track agent activity</p>
 88→      </div>
 89→    );
 90→  }
 91→
 92→  return (
 93→    <div className="space-y-3">
 94→      {/* Live agents section */}
 95→      {liveAgents && liveAgents.length > 0 && (
 96→        <div className="glass-card rounded-xl p-5 mb-4">
 97→          <h3 className="font-heading text-sm font-semibold text-brand-peach mb-3 flex items-center gap-2">
 98→            <Zap size={14} className="text-status-compliant" />
 99→            Live Agent Activity
100→          </h3>
101→          <div className="space-y-2">
102→            {liveAgents.map(agent => (
103→              <LiveAgentCard key={agent.sessionId} agent={agent} />
104→            ))}
105→          </div>
106→        </div>
107→      )}
108→
109→      {/* Historical sessions */}
110→      {sessions.length > 0 && (
111→        <h3 className="font-heading text-xs font-semibold text-text-muted uppercase tracking-wider mb-2">
112→          Session History
113→        </h3>
114→      )}
115→      {sessions.map(session => {
116→        const isOpen = expanded.has(session.id);
117→
118→        return (
119→          <div key={session.id} className="glass-card rounded-xl overflow-hidden">
120→            {/* Header — always visible */}
121→            <button
122→              onClick={() => toggle(session.id)}
123→              className="w-full flex items-center gap-3 p-4 text-left hover:bg-surface-hover/30 transition-colors"
124→            >
125→              {isOpen ? <ChevronDown size={16} className="text-text-muted" /> : <ChevronRight size={16} className="text-text-muted" />}
126→              <Bot size={16} className="text-status-ai shrink-0" />
127→              <div className="flex-1 min-w-0">
128→                <span className="text-sm text-text-primary font-medium">Session {session.id}</span>
129→                <span className="text-xs text-text-muted ml-2">{session.date}</span>
130→                {session.agent && (
131→                  <span className="text-[10px] text-status-ai ml-2">{session.agent}</span>
132→                )}
133→              </div>
134→              <span className="text-[10px] text-text-muted shrink-0">
135→                {session.summary.length} items
136→              </span>
137→            </button>
138→
139→            {/* Expanded content */}
140→            {isOpen && (
141→              <div className="border-t border-border-subtle px-4 pb-4 pt-3 space-y-4">
142→                {/* Summary */}
143→                {session.summary.length > 0 && (
144→                  <div>
145→                    <h4 className="text-[10px] text-text-muted uppercase tracking-wider mb-1.5">What happened</h4>
146→                    <ul className="space-y-1">
147→                      {session.summary.map((item, i) => (
148→                        <li key={i} className="text-xs text-text-secondary flex gap-2">
149→                          <span className="text-brand-teal mt-0.5 shrink-0">-</span>
150→                          {item}
151→                        </li>
152→                      ))}
153→                    </ul>
154→                  </div>
155→                )}
156→
157→                {/* Commits */}
158→                {session.commits.length > 0 && (
159→                  <div>
160→                    <h4 className="text-[10px] text-text-muted uppercase tracking-wider mb-1.5 flex items-center gap-1">
161→                      <GitCommit size={10} /> Commits
162→                    </h4>
163→                    <ul className="space-y-1">
164→                      {session.commits.map((c, i) => (
165→                        <li key={i} className="text-xs font-mono text-brand-blue">{c}</li>
166→                      ))}
167→                    </ul>
168→                  </div>
169→                )}
170→
171→                {/* Decisions */}
172→                {session.decisions.length > 0 && (
173→                  <div>
174→                    <h4 className="text-[10px] text-text-muted uppercase tracking-wider mb-1.5 flex items-center gap-1">
175→                      <Lightbulb size={10} /> Decisions
176→                    </h4>
177→                    <ul className="space-y-1">
178→                      {session.decisions.map((d, i) => (
179→                        <li key={i} className="text-xs text-text-secondary">{d}</li>
180→                      ))}
181→                    </ul>
182→                  </div>
183→                )}
184→
185→                {/* Pending */}
186→                {session.pendingWork.length > 0 && (
187→                  <div>
188→                    <h4 className="text-[10px] text-text-muted uppercase tracking-wider mb-1.5 flex items-center gap-1">
189→                      <AlertCircle size={10} className="text-status-warning" /> Pending
190→                    </h4>
191→                    <ul className="space-y-1">
192→                      {session.pendingWork.map((p, i) => (
193→                        <li key={i} className="text-xs text-status-warning">{p}</li>
194→                      ))}
195→                    </ul>
196→                  </div>
197→                )}
198→              </div>
199→            )}
200→          </div>
201→        );
202→      })}
203→    </div>
204→  );
205→}
