 5→async function fetchJson<T>(url: string): Promise<T> {
 6→  const res = await fetch(`${BASE}${url}`);
 7→  if (!res.ok) throw new Error(`API error: ${res.status} ${res.statusText}`);
 8→  return res.json() as Promise<T>;
 9→}
