19→export async function bootstrapProject(name: string): Promise<{ success: boolean; message: string }> {
20→  const res = await fetch(`${BASE}/projects/${encodeURIComponent(name)}/bootstrap`, {
21→    method: 'POST',
22→  });
23→  if (!res.ok) throw new Error('Bootstrap failed');
24→  return res.json() as Promise<{ success: boolean; message: string }>;
25→}
