 45→// ── Path decoding ──
 46→// Claude encodes project paths as: /mnt/c/.netapps/socialhomes → -mnt-c--netapps-socialhomes
 47→// Rules: leading / becomes leading -, each / becomes -, literal . becomes -, but the FIRST entry's
 48→// cwd field has the real path, so we read it from the JSONL itself.
 49→
 50→function extractProjectName(dirName: string): string {
 51→  // Take last segment after the final - that follows a known pattern
 52→  // e.g. -mnt-c--netapps-socialhomes → socialhomes
 53→  const parts = dirName.split('-').filter(Boolean);
 54→  return parts[parts.length - 1] ?? dirName;
 55→}
 56→
 57→// ── File reading ──
 58→
 59→/** Read the last N bytes of a file and return lines */
 60→async function tailFileBytes(filePath: string, maxBytes: number): Promise<string[]> {
 61→  try {
 62→    const fileStat = await stat(filePath);
 63→    const size = fileStat.size;
 64→    const readStart = Math.max(0, size - maxBytes);
 65→    const fh = await open(filePath, 'r');
 66→    try {
 67→      const buf = Buffer.alloc(Math.min(maxBytes, size));
 68→      await fh.read(buf, 0, buf.length, readStart);
 69→      const text = buf.toString('utf-8');
 70→      const lines = text.split('\n').filter(l => l.trim());
 71→      // First line may be partial if we started mid-file; skip it unless we read from start
 72→      if (readStart > 0 && lines.length > 0) lines.shift();
 73→      return lines;
 74→    } finally {
 75→      await fh.close();
 76→    }
 77→  } catch {
 78→    return [];
 79→  }
 80→}
 81→
 82→/** Read the first line of a file */
 83→async function readFirstLine(filePath: string): Promise<string | null> {
 84→  try {
 85→    const fh = await open(filePath, 'r');
 86→    try {
 87→      const buf = Buffer.alloc(4096);
 88→      await fh.read(buf, 0, 4096, 0);
 89→      const text = buf.toString('utf-8');
 90→      const nl = text.indexOf('\n');
 91→      return nl >= 0 ? text.slice(0, nl) : text;
 92→    } finally {
 93→      await fh.close();
 94→    }
 95→  } catch {
 96→    return null;
 97→  }
 98→}
 99→
100→function parseJsonl(line: string): Record<string, unknown> | null {
101→  try {
102→    return JSON.parse(line) as Record<string, unknown>;
103→  } catch {
104→    return null;
105→  }
106→}
107→
108→function formatDuration(startMs: number, endMs: number): string {
109→  const diff = endMs - startMs;
110→  const secs = Math.floor(diff / 1000);
111→  const mins = Math.floor(secs / 60);
112→  const hours = Math.floor(mins / 60);
113→  if (hours > 0) return `${hours}h ${mins % 60}m`;
114→  if (mins > 0) return `${mins}m ${secs % 60}s`;
115→  return `${secs}s`;
116→}
117→
118→// ── Activity parsing ──
119→
120→function parseActivityEvents(lines: string[], maxEvents: number): ActivityEvent[] {
121→  const events: ActivityEvent[] = [];
122→
123→  for (let i = lines.length - 1; i >= 0 && events.length < maxEvents; i--) {
124→    const entry = parseJsonl(lines[i]!);
125→    if (!entry) continue;
126→
127→    const ts = entry.timestamp as string;
128→    const type = entry.type as string;
129→
130→    if (type === 'assistant') {
131→      const msg = entry.message as Record<string, unknown> | undefined;
132→      if (!msg) continue;
133→      const content = msg.content;
134→
135→      if (Array.isArray(content)) {
136→        for (const block of content) {
137→          const b = block as Record<string, unknown>;
138→          if (b.type === 'tool_use') {
139→            const toolName = b.name as string;
140→            const input = b.input as Record<string, unknown> | undefined;
141→            const summary = describeToolUse(toolName, input);
142→            events.push({ timestamp: ts, type: 'tool_use', summary, detail: toolName });
143→          } else if (b.type === 'text' && typeof b.text === 'string') {
144→            const text = (b.text as string).trim();
145→            if (text.length > 0) {
146→              events.push({ timestamp: ts, type: 'text', summary: text.slice(0, 150) });
147→            }
148→          }
149→        }
150→      } else if (typeof content === 'string' && content.trim()) {
151→        events.push({ timestamp: ts, type: 'text', summary: content.slice(0, 150) });
152→      }
153→    } else if (type === 'progress') {
154→      const data = entry.data as Record<string, unknown> | undefined;
155→      if (!data) continue;
156→      if (data.type === 'hook_progress') {
157→        events.push({
158→          timestamp: ts,
159→          type: 'hook',
160→          summary: `Hook: ${data.hookEvent as string ?? 'unknown'}`,
161→          detail: data.hookName as string | undefined,
162→        });
163→      }
164→    } else if (type === 'user') {
165→      const msg = entry.message as Record<string, unknown> | undefined;
166→      if (msg) {
167→        const content = typeof msg.content === 'string' ? msg.content : '';
168→        events.push({
169→          timestamp: ts,
170→          type: 'user_input',
171→          summary: content.slice(0, 100) || 'User message',
172→        });
173→      }
174→    }
175→  }
176→
177→  return events.reverse(); // Chronological order
178→}
179→
180→function describeToolUse(toolName: string, input: Record<string, unknown> | undefined): string {
181→  if (!input) return toolName;
182→
183→  switch (toolName) {
184→    case 'Read':
185→      return `Read ${shortenPath(input.file_path as string)}`;
186→    case 'Write':
187→      return `Write ${shortenPath(input.file_path as string)}`;
188→    case 'Edit':
189→      return `Edit ${shortenPath(input.file_path as string)}`;
190→    case 'Bash':
191→      return `Bash: ${(input.command as string ?? '').slice(0, 80)}`;
192→    case 'Glob':
193→      return `Glob: ${input.pattern as string ?? ''}`;
194→    case 'Grep':
195→      return `Grep: ${(input.pattern as string ?? '').slice(0, 50)}`;
196→    case 'Task':
197→      return `Agent: ${(input.description as string ?? input.prompt as string ?? '').slice(0, 60)}`;
198→    case 'TaskCreate':
199→      return `Task: ${(input.subject as string ?? '').slice(0, 60)}`;
200→    case 'TaskUpdate':
201→      return `Task #${input.taskId as string}: ${input.status as string ?? 'update'}`;
202→    case 'WebFetch':
203→      return `Fetch: ${(input.url as string ?? '').slice(0, 60)}`;
204→    case 'WebSearch':
205→      return `Search: ${(input.query as string ?? '').slice(0, 60)}`;
206→    case 'mcp__telegram__notify_user':
207→    case 'mcp__telegram__send_message':
208→      return `Telegram: ${(input.message as string ?? '').slice(0, 60)}`;
209→    default:
210→      return toolName;
211→  }
212→}
213→
214→function shortenPath(p: string | undefined): string {
215→  if (!p) return '?';
216→  const parts = p.split('/');
217→  if (parts.length <= 3) return p;
218→  return `.../${parts.slice(-2).join('/')}`;
219→}
220→
221→/** Describe what the agent is doing right now based on the last entry */
222→function describeCurrentActivity(lastEntry: Record<string, unknown>): { message: string; currentTool?: string } {
223→  const type = lastEntry.type as string;
224→
225→  if (type === 'assistant') {
226→    const msg = lastEntry.message as Record<string, unknown> | undefined;
227→    if (msg && Array.isArray(msg.content)) {
228→      for (const block of msg.content as Array<Record<string, unknown>>) {
229→        if (block.type === 'tool_use') {
230→          const toolName = block.name as string;
231→          const input = block.input as Record<string, unknown> | undefined;
232→          return {
233→            message: describeToolUse(toolName, input),
234→            currentTool: toolName,
235→          };
236→        }
237→        if (block.type === 'text' && typeof block.text === 'string') {
238→          return { message: (block.text as string).slice(0, 120) };
239→        }
240→      }
241→    }
242→    if (msg && typeof msg.content === 'string') {
243→      return { message: (msg.content as string).slice(0, 120) };
244→    }
245→    return { message: 'Generating response...' };
246→  }
247→
248→  if (type === 'progress') {
249→    const data = lastEntry.data as Record<string, unknown> | undefined;
250→    if (data?.type === 'hook_progress') {
251→      return { message: `Running hook: ${data.hookEvent as string ?? ''}` };
252→    }
253→    return { message: 'Processing...' };
254→  }
255→
256→  if (type === 'user') return { message: 'Waiting for input' };
257→  return { message: type };
258→}
259→
260→// ── Session scanner ──
261→
262→async function scanSession(
263→  jsonlPath: string,
264→  projectDirName: string,
265→): Promise<AgentActivity | null> {
266→  try {
267→    const fileStat = await stat(jsonlPath);
268→    const modifiedMs = fileStat.mtimeMs;
269→    const now = Date.now();
270→    const age = now - modifiedMs;
271→
272→    if (age > RECENT_THRESHOLD_MS) return null;
273→
274→    const sessionId = path.basename(jsonlPath, '.jsonl');
275→
276→    // Read first line for session metadata
277→    const firstLineStr = await readFirstLine(jsonlPath);
278→    const firstEntry = firstLineStr ? parseJsonl(firstLineStr) : null;
279→
280→    // Read last ~64KB for recent activity
281→    const lastLines = await tailFileBytes(jsonlPath, 65536);
282→    if (lastLines.length === 0) return null;
283→
284→    const lastEntry = parseJsonl(lastLines[lastLines.length - 1]!);
285→    if (!lastEntry) return null;
286→
287→    // Get project path from the JSONL cwd field (most reliable)
288→    const cwd = (firstEntry?.cwd ?? lastEntry.cwd) as string | undefined;
289→    const projectName = cwd
290→      ? path.basename(cwd)
291→      : extractProjectName(projectDirName);
292→    const projectPath = cwd ?? `/${projectDirName.replace(/^-/, '').replace(/-/g, '/')}`;
293→
294→    // Count subagents
295→    let subagentCount = 0;
296→    const subagentDir = path.join(path.dirname(jsonlPath), sessionId, 'subagents');
297→    try {
298→      const subagents = await readdir(subagentDir);
299→      subagentCount = subagents.filter(f => f.endsWith('.jsonl')).length;
300→    } catch { /* no subagents dir */ }
301→
302→    const status: AgentActivity['status'] = age <= ACTIVE_THRESHOLD_MS ? 'active' : 'recent';
303→
304→    const startTime = firstEntry?.timestamp as string | undefined;
305→    const endTime = lastEntry.timestamp as string | undefined;
306→    let duration: string | undefined;
307→    if (startTime && endTime) {
308→      duration = formatDuration(new Date(startTime).getTime(), new Date(endTime).getTime());
309→    }
310→
311→    const { message, currentTool } = describeCurrentActivity(lastEntry);
312→
313→    // Parse last 20 activity events from the tail
314→    const recentEvents = parseActivityEvents(lastLines, 20);
315→
316→    // Count total lines via file size estimation (~500 bytes per line avg)
317→    const estimatedEntries = Math.round(fileStat.size / 500);
318→
319→    return {
320→      sessionId,
321→      slug: (lastEntry.slug ?? firstEntry?.slug) as string | undefined,
322→      projectPath,
323→      projectName,
324→      status,
325→      lastTimestamp: endTime ?? new Date(modifiedMs).toISOString(),
326→      startTimestamp: startTime,
327→      lastType: lastEntry.type as string,
328→      lastMessage: message,
329→      currentTool,
330→      duration,
331→      entryCount: estimatedEntries,
332→      subagentCount,
333→      version: (firstEntry?.version ?? lastEntry.version) as string | undefined,
334→      gitBranch: (firstEntry?.gitBranch ?? lastEntry.gitBranch) as string | undefined,
335→      recentEvents,
336→    };
337→  } catch {
338→    return null;
339→  }
340→}
341→
342→// ── Public API ──
343→
344→export async function getLiveAgentDashboard(): Promise<LiveDashboard> {
345→  const activeAgents: AgentActivity[] = [];
346→  const recentAgents: AgentActivity[] = [];
347→  let totalSessions = 0;
348→
349→  try {
350→    const projectDirs = await readdir(PROJECTS_DIR);
351→
352→    for (const projectDir of projectDirs) {
353→      const projectPath = path.join(PROJECTS_DIR, projectDir);
354→      const dirStat = await stat(projectPath);
355→      if (!dirStat.isDirectory()) continue;
356→
357→      const entries = await readdir(projectPath);
358→      const jsonlFiles = entries.filter(e => e.endsWith('.jsonl'));
359→      totalSessions += jsonlFiles.length;
360→
361→      for (const jsonl of jsonlFiles) {
362→        const activity = await scanSession(
363→          path.join(projectPath, jsonl),
364→          projectDir,
365→        );
366→        if (!activity) continue;
367→
368→        if (activity.status === 'active') {
369→          activeAgents.push(activity);
370→        } else {
371→          recentAgents.push(activity);
372→        }
373→      }
374→    }
375→  } catch (err) {
376→    console.error('Failed to scan agent sessions:', err);
377→  }
378→
379→  activeAgents.sort((a, b) => new Date(b.lastTimestamp).getTime() - new Date(a.lastTimestamp).getTime());
380→  recentAgents.sort((a, b) => new Date(b.lastTimestamp).getTime() - new Date(a.lastTimestamp).getTime());
381→
382→  return { activeAgents, recentAgents, totalSessions };
383→}
384→
385→export async function getProjectAgents(projectName: string): Promise<AgentActivity[]> {
386→  const dashboard = await getLiveAgentDashboard();
387→  return [...dashboard.activeAgents, ...dashboard.recentAgents]
388→    .filter(a => a.projectName === projectName);
389→}
