 63→export default function SessionsTab({ project }: Props) {
 64→  const sessions = project.sessions?.sessions ?? [];
 65→  const [expanded, setExpanded] = useState<Set<string>>(new Set(sessions[0]?.id ? [sessions[0].id] : []));
 66→
 67→  const { data: liveAgents } = useQuery({
 68→    queryKey: ['agents-project', project.name],
 69→    queryFn: () => fetchProjectAgents(project.name),
 70→    refetchInterval: 5000,
 71→  });
 72→
 73→  const toggle = (id: string) => {
 74→    setExpanded(prev => {
 75→      const next = new Set(prev);
 76→      if (next.has(id)) next.delete(id);
 77→      else next.add(id);
 78→      return next;
 79→    });
 80→  };
 81→
 82→  if (sessions.length === 0 && (!liveAgents || liveAgents.length === 0)) {
 83→    return (
 84→      <div className="glass-card rounded-xl p-8 text-center">
 85→        <Bot size={32} className="text-text-muted mx-auto mb-3" />
 86→        <p className="text-text-muted text-sm">No session history found</p>
 87→        <p className="text-text-muted text-xs mt-1">Create a SESSION-HISTORY.md to track agent activity</p>
