import { readFile, readdir, stat, access } from 'fs/promises';
import path from 'path';

// ── Types ──

export interface AgentRole {
  name: string;          // e.g. "Fullstack", "Frontend", "Tester", "BA", "DevOps"
  responsibility: string;
  keyFiles: string[];
  pipelineOrder: number; // 0=BA, 1=Fullstack, 2=Frontend, 3=Tester, 4=DevOps
}

export interface AgentTask {
  id: string;
  task: string;
  status: 'DONE' | 'TODO' | 'IN_PROGRESS';
  phase: string;
  phaseId: string;
  priority?: string;
  complexity?: string;
}

export interface AgentDocument {
  name: string;
  path: string;
  sizeKb: number;
  lastModified: string;
  producer: string;      // Which agent produced this
}

export interface PipelineAgent {
  role: AgentRole;
  status: 'idle' | 'active' | 'blocked' | 'complete';
  tasks: AgentTask[];
  totalTasks: number;
  completedTasks: number;
  progress: number;
  documents: AgentDocument[];
  currentPhase?: string; // Which phase this agent is currently working in
  blockedReason?: string;
}

export interface PipelineData {
  agents: PipelineAgent[];
  currentPhase: string;
  currentPhaseId: string;
  overallProgress: number;
  totalTasks: number;
  completedTasks: number;
}

// ── Pipeline order (left to right) ──

const PIPELINE_ORDER: Record<string, number> = {
  'ba': 0,
  'ba agent': 0,
  'system analyst': 0,
  'claude code': 1,
  'fullstack': 2,
  'frontend': 3,
  'frontend agent': 3,
  'tester': 4,
  'devops': 5,
};

const ROLE_DEFAULTS: Record<string, { responsibility: string; keyFiles: string[] }> = {
  'BA': { responsibility: 'Requirements, user stories, acceptance criteria', keyFiles: ['Doc1-*.txt', 'Doc2-*.txt', 'Doc3-*.txt'] },
  'Claude Code': { responsibility: 'Infrastructure, tooling, session management', keyFiles: ['CLAUDE.md', 'SESSION-HISTORY.md', 'EXECUTION-PLAN.md'] },
  'Fullstack': { responsibility: 'Server routes, API integrations, data models', keyFiles: ['server/src/'] },
  'Frontend': { responsibility: 'UI components, pages, styling', keyFiles: ['app/src/pages/', 'app/src/components/'] },
  'Tester': { responsibility: 'Selenium tests, unit tests, integration tests', keyFiles: ['tests/', 'DEV-FIX-LIST.md'] },
  'DevOps': { responsibility: 'CI/CD, monitoring, deployment, security', keyFiles: ['cloudbuild.yaml', 'Dockerfile', 'DEVOPS.md'] },
};

// ── Document detection ──

async function exists(p: string): Promise<boolean> {
  try { await access(p); return true; } catch { return false; }
}

/** Scan for known document files and attribute to agent */
async function scanDocuments(projectPath: string): Promise<AgentDocument[]> {
  const docs: AgentDocument[] = [];

  const docPatterns: { pattern: RegExp; producer: string }[] = [
    { pattern: /^Doc1-.*\.txt$/i, producer: 'BA' },
    { pattern: /^Doc2-.*\.txt$/i, producer: 'BA' },
    { pattern: /^Doc3-.*\.txt$/i, producer: 'BA' },
    { pattern: /^Doc4-.*\.md$/i, producer: 'Frontend' },
    { pattern: /^CLAUDE\.md$/i, producer: 'Claude Code' },
    { pattern: /^SESSION-HISTORY\.md$/i, producer: 'Claude Code' },
    { pattern: /^DEV-FIX-LIST\.md$/i, producer: 'Tester' },
    { pattern: /^EXECUTION-PLAN\.md$/i, producer: 'Claude Code' },
    { pattern: /^DEVOPS\.md$/i, producer: 'DevOps' },
    { pattern: /^cloudbuild\.yaml$/i, producer: 'DevOps' },
    { pattern: /^Dockerfile$/i, producer: 'DevOps' },
  ];

  try {
    const entries = await readdir(projectPath);
    for (const entry of entries) {
      for (const { pattern, producer } of docPatterns) {
        if (pattern.test(entry)) {
          const fullPath = path.join(projectPath, entry);
          const fileStat = await stat(fullPath);
          docs.push({
            name: entry,
            path: entry,
            sizeKb: Math.round(fileStat.size / 1024),
            lastModified: fileStat.mtime.toISOString(),
            producer,
          });
          break;
        }
      }
    }
  } catch { /* skip */ }

  return docs;
}

// ── Main parser ──

export async function parsePipelineData(projectPath: string): Promise<PipelineData | null> {
  const planPath = path.join(projectPath, 'EXECUTION-PLAN.md');
  if (!(await exists(planPath))) return null;

  const content = await readFile(planPath, 'utf-8');

  // Parse agent roles from ## AGENT ROLES table
  const parsedRoles: Map<string, AgentRole> = new Map();
  const rolesMatch = content.match(/##\s*AGENT ROLES\s*\n([\s\S]*?)(?=\n---|\n##)/i);
  if (rolesMatch) {
    const roleRowRegex = /\|\s*\*\*(.+?)\*\*\s*\|\s*(.+?)\s*\|\s*`?(.+?)`?\s*\|/g;
    let rm;
    while ((rm = roleRowRegex.exec(rolesMatch[1]!)) !== null) {
      const name = rm[1]!.trim();
      const responsibility = rm[2]!.trim();
      const keyFiles = rm[3]!.split(',').map(f => f.trim().replace(/`/g, ''));
      const order = PIPELINE_ORDER[name.toLowerCase()] ?? 99;
      parsedRoles.set(name, { name, responsibility, keyFiles, pipelineOrder: order });
    }
  }

  // Ensure default roles exist
  for (const [name, defaults] of Object.entries(ROLE_DEFAULTS)) {
    if (!parsedRoles.has(name)) {
      parsedRoles.set(name, {
        name,
        responsibility: defaults.responsibility,
        keyFiles: defaults.keyFiles,
        pipelineOrder: PIPELINE_ORDER[name.toLowerCase()] ?? 99,
      });
    }
  }

  // Parse all tasks from all phases with agent assignments
  const agentTasks: Map<string, AgentTask[]> = new Map();

  const phaseRegex = /##\s*PHASE\s+(\d+):\s*(.+?)(?:\s*\(.*?\))?\s*\n([\s\S]*?)(?=##\s*PHASE|\n---\s*\n\s*$|$)/gi;
  let phaseMatch;
  let latestActivePhaseId = '0';
  let latestActivePhaseName = 'INFRASTRUCTURE';

  while ((phaseMatch = phaseRegex.exec(content)) !== null) {
    const phaseId = phaseMatch[1]!;
    const phaseName = phaseMatch[2]!.trim();
    const phaseBody = phaseMatch[3]!;

    // Parse task rows line-by-line to avoid cross-line matching
    const lines = phaseBody.split('\n');
    const tableRowRegex = /^\|\s*(\d+\.\d+)\s*\|\s*(.+?)\s*\|\s*(.+?)\s*\|\s*(.+?)\s*\|(?:\s*(.+?)\s*\|)?\s*$/;
    let phaseHasTodo = false;
    let phaseHasDone = false;

    for (const line of lines) {
      const rowMatch = tableRowRegex.exec(line);
      if (!rowMatch) continue;
      const id = rowMatch[1]!.trim();
      const task = rowMatch[2]!.replace(/\*\*/g, '').trim();
      const col3 = rowMatch[3]!.trim();
      const col4 = rowMatch[4]!.trim();
      const col5 = rowMatch[5]?.trim();

      const isDone = /DONE|complete/i.test(col3) || /DONE|complete/i.test(col4);
      const status: AgentTask['status'] = isDone ? 'DONE' : 'TODO';

      if (isDone) phaseHasDone = true;
      else phaseHasTodo = true;

      // Determine agent name — last column is always agent
      let agentName: string;
      if (col5) {
        agentName = col5;
      } else {
        // Phase 0 format: | # | Task | Status | Agent |
        agentName = col4;
      }

      // Normalize agent name
      agentName = agentName.replace(/\*\*/g, '').trim();
      if (agentName === '-' || agentName === '') agentName = 'Unassigned';

      // Map aliases
      if (/^ba$/i.test(agentName) || /ba agent/i.test(agentName)) agentName = 'BA';
      if (/frontend agent/i.test(agentName)) agentName = 'Frontend';

      if (!agentTasks.has(agentName)) agentTasks.set(agentName, []);
      agentTasks.get(agentName)!.push({
        id,
        task,
        status,
        phase: phaseName,
        phaseId,
        priority: /^P\d/.test(col3) ? col3 : undefined,
        complexity: /Low|Medium|High/i.test(col4) ? col4 : undefined,
      });
    }

    // Track current phase (has both done and todo, or first with only todo)
    if (phaseHasTodo && phaseHasDone) {
      latestActivePhaseId = phaseId;
      latestActivePhaseName = phaseName;
    } else if (phaseHasTodo && !phaseHasDone && latestActivePhaseId === '0') {
      latestActivePhaseId = phaseId;
      latestActivePhaseName = phaseName;
    }
  }

  // Scan documents
  const documents = await scanDocuments(projectPath);

  // Build pipeline agents
  const agents: PipelineAgent[] = [];
  let totalTasks = 0;
  let completedTasks = 0;

  for (const [, role] of parsedRoles) {
    const tasks = agentTasks.get(role.name) ?? [];
    const done = tasks.filter(t => t.status === 'DONE').length;
    const total = tasks.length;
    const progress = total > 0 ? Math.round((done / total) * 100) : 0;

    totalTasks += total;
    completedTasks += done;

    // Determine agent documents
    const agentDocs = documents.filter(d => d.producer === role.name);

    // Determine status
    let status: PipelineAgent['status'];
    if (total === 0) {
      status = 'idle';
    } else if (done === total) {
      status = 'complete';
    } else if (done > 0) {
      status = 'active';
    } else {
      // Check if prerequisite agents are done
      const prereqAgents = [...parsedRoles.values()]
        .filter(r => r.pipelineOrder < role.pipelineOrder);
      const prereqsDone = prereqAgents.every(pa => {
        const paTasks = agentTasks.get(pa.name) ?? [];
        return paTasks.length === 0 || paTasks.every(t => t.status === 'DONE');
      });
      status = prereqsDone ? 'idle' : 'blocked';
    }

    // Current phase for this agent
    const activePhaseTasks = tasks.filter(t => t.status === 'TODO');
    const currentPhase = activePhaseTasks.length > 0 ? activePhaseTasks[0]!.phase : undefined;

    agents.push({
      role,
      status,
      tasks,
      totalTasks: total,
      completedTasks: done,
      progress,
      documents: agentDocs,
      currentPhase,
    });
  }

  // Also add Claude Code tasks if present
  const ccTasks = agentTasks.get('Claude Code') ?? [];
  if (ccTasks.length > 0 && !agents.find(a => a.role.name === 'Claude Code')) {
    const done = ccTasks.filter(t => t.status === 'DONE').length;
    agents.push({
      role: parsedRoles.get('Claude Code')!,
      status: done === ccTasks.length ? 'complete' : 'active',
      tasks: ccTasks,
      totalTasks: ccTasks.length,
      completedTasks: done,
      progress: Math.round((done / ccTasks.length) * 100),
      documents: documents.filter(d => d.producer === 'Claude Code'),
    });
  }

  // Sort by pipeline order
  agents.sort((a, b) => a.role.pipelineOrder - b.role.pipelineOrder);

  const overallProgress = totalTasks > 0 ? Math.round((completedTasks / totalTasks) * 100) : 0;

  return {
    agents,
    currentPhase: latestActivePhaseName,
    currentPhaseId: latestActivePhaseId,
    overallProgress,
    totalTasks,
    completedTasks,
  };
}

import { useState } from 'react';
import { useQuery } from '@tanstack/react-query';
import { fetchPipeline } from '../../services/api';
import {
  Users, CheckCircle2, Circle, Clock, AlertTriangle,
  FileText, ChevronDown, ChevronRight, ArrowRight,
  Briefcase, Code2, Paintbrush, TestTube, Rocket,
} from 'lucide-react';
import { formatDistanceToNow } from 'date-fns';
import type { PipelineAgent, AgentTask } from '../../types';

const ROLE_ICONS: Record<string, typeof Users> = {
  'BA': Briefcase,
  'Claude Code': Code2,
  'Fullstack': Code2,
  'Frontend': Paintbrush,
  'Tester': TestTube,
  'DevOps': Rocket,
};

const STATUS_CONFIG = {
  idle: { bg: 'bg-text-muted/10', border: 'border-text-muted/30', text: 'text-text-muted', label: 'Idle', dot: 'bg-text-muted' },
  active: { bg: 'bg-brand-teal/10', border: 'border-brand-teal/30', text: 'text-brand-teal', label: 'Active', dot: 'bg-brand-teal' },
  blocked: { bg: 'bg-status-warning/10', border: 'border-status-warning/30', text: 'text-status-warning', label: 'Blocked', dot: 'bg-status-warning' },
  complete: { bg: 'bg-status-compliant/10', border: 'border-status-compliant/30', text: 'text-status-compliant', label: 'Complete', dot: 'bg-status-compliant' },
};

function TaskRow({ task }: { task: AgentTask }) {
  return (
    <div className="flex items-start gap-2 py-1">
      {task.status === 'DONE' ? (
        <CheckCircle2 size={13} className="text-status-compliant mt-0.5 shrink-0" />
      ) : (
        <Circle size={13} className="text-text-muted mt-0.5 shrink-0" />
      )}
      <div className="flex-1 min-w-0">
        <span className={`text-[11px] ${task.status === 'DONE' ? 'text-text-muted line-through' : 'text-text-primary'}`}>
          {task.task}
        </span>
        <div className="flex gap-2 mt-0.5">
          <span className="text-[9px] text-text-muted font-mono">{task.phase}</span>
          {task.priority && (
            <span className={`text-[9px] font-mono ${
              task.priority === 'P1' ? 'text-status-critical' :
              task.priority === 'P2' ? 'text-status-warning' : 'text-text-muted'
            }`}>{task.priority}</span>
          )}
        </div>
      </div>
    </div>
  );
}

function AgentCard({ agent }: { agent: PipelineAgent }) {
  const [expanded, setExpanded] = useState(agent.status === 'active');
  const sc = STATUS_CONFIG[agent.status];
  const Icon = ROLE_ICONS[agent.role.name] ?? Users;

  return (
    <div className={`glass-card rounded-xl overflow-hidden border ${sc.border}`}>
      {/* Header */}
      <button
        onClick={() => setExpanded(!expanded)}
        className="w-full p-4 text-left hover:bg-surface-hover/20 transition-colors"
      >
        <div className="flex items-center gap-3">
          <div className={`p-2 rounded-lg ${sc.bg}`}>
            <Icon size={18} className={sc.text} />
          </div>
          <div className="flex-1 min-w-0">
            <div className="flex items-center gap-2">
              <h4 className="font-heading text-sm font-semibold text-text-primary">{agent.role.name}</h4>
              <span className={`text-[10px] font-mono uppercase tracking-wider px-1.5 py-0 rounded-full ${sc.bg} ${sc.text} border ${sc.border}`}>
                {sc.label}
              </span>
            </div>
            <p className="text-[10px] text-text-muted mt-0.5">{agent.role.responsibility}</p>
          </div>
          <div className="text-right shrink-0">
            <div className={`text-lg font-heading font-bold ${sc.text}`}>
              {agent.completedTasks}/{agent.totalTasks}
            </div>
            <div className="text-[10px] text-text-muted">tasks</div>
          </div>
          {expanded ? <ChevronDown size={14} className="text-text-muted" /> : <ChevronRight size={14} className="text-text-muted" />}
        </div>

        {/* Progress bar */}
        {agent.totalTasks > 0 && (
          <div className="mt-3 flex items-center gap-2">
            <div className="flex-1 h-2 rounded-full bg-surface-elevated overflow-hidden">
              <div
                className={`h-full rounded-full transition-all duration-500 ${
                  agent.status === 'complete'
                    ? 'bg-status-compliant'
                    : 'bg-gradient-to-r from-brand-teal to-brand-blue'
                }`}
                style={{ width: `${agent.progress}%` }}
              />
            </div>
            <span className="text-[10px] font-mono text-text-muted w-8 text-right">{agent.progress}%</span>
          </div>
        )}
      </button>

      {/* Expanded content */}
      {expanded && (
        <div className="border-t border-border-subtle px-4 pb-4 pt-3 space-y-4">
          {/* Documents */}
          {agent.documents.length > 0 && (
            <div>
              <h5 className="text-[10px] text-text-muted uppercase tracking-wider mb-1.5 flex items-center gap-1">
                <FileText size={10} /> Documents Produced
              </h5>
              <div className="space-y-1">
                {agent.documents.map(doc => (
                  <div key={doc.name} className="flex items-center gap-2 p-1.5 rounded bg-surface-elevated text-xs">
                    <FileText size={11} className="text-brand-peach shrink-0" />
                    <span className="text-text-primary font-mono text-[11px] flex-1 truncate">{doc.name}</span>
                    <span className="text-text-muted text-[10px]">{doc.sizeKb}KB</span>
                    <span className="text-text-muted text-[10px]">
                      {formatDistanceToNow(new Date(doc.lastModified), { addSuffix: true })}
                    </span>
                  </div>
                ))}
              </div>
            </div>
          )}

          {/* Tasks */}
          {agent.tasks.length > 0 && (
            <div>
              <h5 className="text-[10px] text-text-muted uppercase tracking-wider mb-1.5">
                Tasks ({agent.completedTasks}/{agent.totalTasks})
              </h5>
              <div className="space-y-0.5 max-h-48 overflow-y-auto">
                {agent.tasks.map(task => (
                  <TaskRow key={task.id} task={task} />
                ))}
              </div>
            </div>
          )}

          {/* Current phase */}
          {agent.currentPhase && (
            <div className="flex items-center gap-2 text-[10px]">
              <Clock size={10} className="text-text-muted" />
              <span className="text-text-muted">Next up in:</span>
              <span className="text-brand-teal font-medium">{agent.currentPhase}</span>
            </div>
          )}
        </div>
      )}
    </div>
  );
}

interface Props {
  projectName: string;
}

export default function PipelineTab({ projectName }: Props) {
  const { data: pipeline, isLoading } = useQuery({
    queryKey: ['pipeline', projectName],
    queryFn: () => fetchPipeline(projectName),
  });

  if (isLoading) {
    return <div className="glass-card rounded-xl h-64 animate-shimmer" />;
  }

  if (!pipeline) {
    return (
      <div className="glass-card rounded-xl p-8 text-center">
        <Users size={32} className="text-text-muted mx-auto mb-3" />
        <p className="text-text-muted text-sm">No execution plan found</p>
        <p className="text-text-muted text-xs mt-1">Create an EXECUTION-PLAN.md with agent assignments</p>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Pipeline header */}
      <div className="glass-card rounded-xl p-5">
        <div className="flex items-center justify-between mb-3">
          <div>
            <h3 className="font-heading text-sm font-semibold text-brand-peach">Agent Pipeline</h3>
            <p className="text-xs text-text-muted mt-0.5">
              Current phase: <span className="text-brand-teal font-medium">{pipeline.currentPhase}</span>
            </p>
          </div>
          <div className="text-right">
            <div className="text-2xl font-heading font-bold text-brand-teal">{pipeline.overallProgress}%</div>
            <div className="text-[10px] text-text-muted">{pipeline.completedTasks}/{pipeline.totalTasks} tasks</div>
          </div>
        </div>

        {/* Overall progress */}
        <div className="h-3 rounded-full bg-surface-elevated overflow-hidden">
          <div
            className="h-full rounded-full bg-gradient-to-r from-brand-teal to-brand-blue transition-all duration-700"
            style={{ width: `${pipeline.overallProgress}%` }}
          />
        </div>
      </div>

      {/* Visual pipeline flow */}
      <div className="glass-card rounded-xl p-5">
        <h3 className="font-heading text-xs font-semibold text-text-muted uppercase tracking-wider mb-4">Pipeline Flow</h3>
        <div className="flex items-center gap-1 overflow-x-auto pb-2">
          {pipeline.agents.map((agent, i) => {
            const sc = STATUS_CONFIG[agent.status];
            const Icon = ROLE_ICONS[agent.role.name] ?? Users;
            return (
              <div key={agent.role.name} className="flex items-center gap-1 shrink-0">
                <div className={`flex flex-col items-center gap-1 px-3 py-2 rounded-lg ${sc.bg} border ${sc.border} min-w-[80px]`}>
                  <Icon size={16} className={sc.text} />
                  <span className="text-[10px] font-medium text-text-primary whitespace-nowrap">{agent.role.name}</span>
                  <span className={`text-[9px] font-mono ${sc.text}`}>
                    {agent.totalTasks > 0 ? `${agent.completedTasks}/${agent.totalTasks}` : '-'}
                  </span>
                  <div className="w-full h-1 rounded-full bg-surface-dark overflow-hidden">
                    <div
                      className={`h-full rounded-full ${agent.status === 'complete' ? 'bg-status-compliant' : 'bg-brand-teal'}`}
                      style={{ width: `${agent.progress}%` }}
                    />
                  </div>
                </div>
                {i < pipeline.agents.length - 1 && (
                  <ArrowRight size={14} className="text-text-muted shrink-0" />
                )}
              </div>
            );
          })}
        </div>
      </div>

      {/* Agent detail cards */}
      <div className="space-y-3">
        {pipeline.agents.map(agent => (
          <AgentCard key={agent.role.name} agent={agent} />
        ))}
      </div>
    </div>
  );
}

