import express from 'express';
import cors from 'cors';
import projectsRouter from './routes/projects.js';
import { getLiveAgentDashboard, getProjectAgents } from './services/agent-monitor.js';
import { parsePipelineData } from './services/pipeline-parser.js';

const app = express();
const PORT = 3001;

app.use(cors({ origin: ['http://localhost:5173', 'http://localhost:5174'] }));
app.use(express.json());

// Routes
app.use('/api/projects', projectsRouter);

// Live agent monitoring
app.get('/api/agents/live', async (_req, res) => {
  try {
    const data = await getLiveAgentDashboard();
    res.json(data);
  } catch (err) {
    console.error('Agent monitor error:', err);
    res.status(500).json({ error: 'Failed to scan agent sessions' });
  }
});

app.get('/api/agents/project/:name', async (req, res) => {
  try {
    const agents = await getProjectAgents(req.params.name!);
    res.json(agents);
  } catch {
    res.json([]);
  }
});

// Pipeline data
app.get('/api/projects/:name/pipeline', async (req, res) => {
  try {
    const clean = req.params.name!.replace(/[/\\]/g, '');
    const pp = `/mnt/c/.netapps/${clean}`;
    const data = await parsePipelineData(pp);
    if (!data) {
      res.json(null);
      return;
    }
    res.json(data);
  } catch (err) {
    console.error('Pipeline parse error:', err);
    res.status(500).json({ error: 'Failed to parse pipeline' });
  }
});

// Health check
app.get('/health', (_req, res) => {
  res.json({ status: 'ok', timestamp: new Date().toISOString() });
});

app.listen(PORT, () => {
  console.log(`Mission Control server running on http://localhost:${PORT}`);
});

