  1→import { readdir, stat, readFile, open } from 'fs/promises';
  2→import path from 'path';
  3→
  4→const CLAUDE_DIR = '/home/rajiv/.claude';
  5→const PROJECTS_DIR = path.join(CLAUDE_DIR, 'projects');
  6→
  7→const ACTIVE_THRESHOLD_MS = 5 * 60 * 1000;   // 5 minutes
  8→const RECENT_THRESHOLD_MS = 24 * 60 * 60 * 1000; // 24 hours
  9→
 10→// ── Types ──
 11→
 12→export interface ActivityEvent {
 13→  timestamp: string;
 14→  type: 'tool_use' | 'tool_result' | 'text' | 'hook' | 'user_input' | 'system';
 15→  summary: string;
 16→  detail?: string;
 17→}
 18→
 19→export interface AgentActivity {
 20→  sessionId: string;
 21→  slug?: string;
 22→  projectPath: string;
 23→  projectName: string;
 24→  agentId?: string;
 25→  status: 'active' | 'recent' | 'completed';
 26→  lastTimestamp: string;
 27→  startTimestamp?: string;
 28→  lastType: string;
 29→  lastMessage: string;
 30→  currentTool?: string;
 31→  duration?: string;
 32→  entryCount: number;
 33→  subagentCount: number;
 34→  version?: string;
 35→  gitBranch?: string;
 36→  recentEvents: ActivityEvent[];
 37→}
 38→
 39→export interface LiveDashboard {
 40→  activeAgents: AgentActivity[];
 41→  recentAgents: AgentActivity[];
 42→  totalSessions: number;
 43→}
 44→
 45→// ── Path decoding ──
 46→// Claude encodes project paths as: /mnt/c/.netapps/socialhomes → -mnt-c--netapps-socialhomes
 47→// Rules: leading / becomes leading -, each / becomes -, literal . becomes -, but the FIRST entry's
 48→// cwd field has the real path, so we read it from the JSONL itself.
 49→
 50→function extractProjectName(dirName: string): string {
 51→  // Take last segment after the final - that follows a known pattern
 52→  // e.g. -mnt-c--netapps-socialhomes → socialhomes
 53→  const parts = dirName.split('-').filter(Boolean);
 54→  return parts[parts.length - 1] ?? dirName;
 55→}
 56→
 57→// ── File reading ──
 58→
 59→/** Read the last N bytes of a file and return lines */
 60→async function tailFileBytes(filePath: string, maxBytes: number): Promise<string[]> {
 61→  try {
 62→    const fileStat = await stat(filePath);
 63→    const size = fileStat.size;
 64→    const readStart = Math.max(0, size - maxBytes);
 65→    const fh = await open(filePath, 'r');
 66→    try {
 67→      const buf = Buffer.alloc(Math.min(maxBytes, size));
 68→      await fh.read(buf, 0, buf.length, readStart);
 69→      const text = buf.toString('utf-8');
 70→      const lines = text.split('\n').filter(l => l.trim());
 71→      // First line may be partial if we started mid-file; skip it unless we read from start
 72→      if (readStart > 0 && lines.length > 0) lines.shift();
 73→      return lines;
 74→    } finally {
 75→      await fh.close();
 76→    }
 77→  } catch {
 78→    return [];
 79→  }
 80→}
 81→
 82→/** Read the first line of a file */
 83→async function readFirstLine(filePath: string): Promise<string | null> {
 84→  try {
 85→    const fh = await open(filePath, 'r');
 86→    try {
 87→      const buf = Buffer.alloc(4096);
 88→      await fh.read(buf, 0, 4096, 0);
 89→      const text = buf.toString('utf-8');
 90→      const nl = text.indexOf('\n');
 91→      return nl >= 0 ? text.slice(0, nl) : text;
 92→    } finally {
 93→      await fh.close();
 94→    }
 95→  } catch {
 96→    return null;
 97→  }
 98→}
 99→
100→function parseJsonl(line: string): Record<string, unknown> | null {
101→  try {
102→    return JSON.parse(line) as Record<string, unknown>;
103→  } catch {
104→    return null;
105→  }
106→}
107→
108→function formatDuration(startMs: number, endMs: number): string {
109→  const diff = endMs - startMs;
110→  const secs = Math.floor(diff / 1000);
111→  const mins = Math.floor(secs / 60);
112→  const hours = Math.floor(mins / 60);
113→  if (hours > 0) return `${hours}h ${mins % 60}m`;
114→  if (mins > 0) return `${mins}m ${secs % 60}s`;
115→  return `${secs}s`;
116→}
117→
118→// ── Activity parsing ──
119→
120→function parseActivityEvents(lines: string[], maxEvents: number): ActivityEvent[] {
121→  const events: ActivityEvent[] = [];
122→
123→  for (let i = lines.length - 1; i >= 0 && events.length < maxEvents; i--) {
124→    const entry = parseJsonl(lines[i]!);
125→    if (!entry) continue;
126→
127→    const ts = entry.timestamp as string;
128→    const type = entry.type as string;
129→
130→    if (type === 'assistant') {
131→      const msg = entry.message as Record<string, unknown> | undefined;
132→      if (!msg) continue;
133→      const content = msg.content;
134→
135→      if (Array.isArray(content)) {
136→        for (const block of content) {
137→          const b = block as Record<string, unknown>;
138→          if (b.type === 'tool_use') {
139→            const toolName = b.name as string;
140→            const input = b.input as Record<string, unknown> | undefined;
141→            const summary = describeToolUse(toolName, input);
142→            events.push({ timestamp: ts, type: 'tool_use', summary, detail: toolName });
143→          } else if (b.type === 'text' && typeof b.text === 'string') {
144→            const text = (b.text as string).trim();
145→            if (text.length > 0) {
146→              events.push({ timestamp: ts, type: 'text', summary: text.slice(0, 150) });
147→            }
148→          }
149→        }
150→      } else if (typeof content === 'string' && content.trim()) {
151→        events.push({ timestamp: ts, type: 'text', summary: content.slice(0, 150) });
152→      }
153→    } else if (type === 'progress') {
154→      const data = entry.data as Record<string, unknown> | undefined;
155→      if (!data) continue;
156→      if (data.type === 'hook_progress') {
157→        events.push({
158→          timestamp: ts,
159→          type: 'hook',
160→          summary: `Hook: ${data.hookEvent as string ?? 'unknown'}`,
161→          detail: data.hookName as string | undefined,
162→        });
163→      }
164→    } else if (type === 'user') {
165→      const msg = entry.message as Record<string, unknown> | undefined;
166→      if (msg) {
167→        const content = typeof msg.content === 'string' ? msg.content : '';
168→        events.push({
169→          timestamp: ts,
170→          type: 'user_input',
171→          summary: content.slice(0, 100) || 'User message',
172→        });
173→      }
174→    }
175→  }
176→
177→  return events.reverse(); // Chronological order
178→}
179→
180→function describeToolUse(toolName: string, input: Record<string, unknown> | undefined): string {
181→  if (!input) return toolName;
182→
183→  switch (toolName) {
184→    case 'Read':
185→      return `Read ${shortenPath(input.file_path as string)}`;
186→    case 'Write':
187→      return `Write ${shortenPath(input.file_path as string)}`;
188→    case 'Edit':
189→      return `Edit ${shortenPath(input.file_path as string)}`;
190→    case 'Bash':
191→      return `Bash: ${(input.command as string ?? '').slice(0, 80)}`;
192→    case 'Glob':
193→      return `Glob: ${input.pattern as string ?? ''}`;
194→    case 'Grep':
195→      return `Grep: ${(input.pattern as string ?? '').slice(0, 50)}`;
196→    case 'Task':
197→      return `Agent: ${(input.description as string ?? input.prompt as string ?? '').slice(0, 60)}`;
198→    case 'TaskCreate':
199→      return `Task: ${(input.subject as string ?? '').slice(0, 60)}`;
200→    case 'TaskUpdate':
201→      return `Task #${input.taskId as string}: ${input.status as string ?? 'update'}`;
202→    case 'WebFetch':
203→      return `Fetch: ${(input.url as string ?? '').slice(0, 60)}`;
204→    case 'WebSearch':
