import { readdir, stat, readFile, open } from 'fs/promises';
import path from 'path';

const CLAUDE_DIR = '/home/rajiv/.claude';
const PROJECTS_DIR = path.join(CLAUDE_DIR, 'projects');

const ACTIVE_THRESHOLD_MS = 5 * 60 * 1000;   // 5 minutes
const RECENT_THRESHOLD_MS = 24 * 60 * 60 * 1000; // 24 hours

// ── Types ──

export interface ActivityEvent {
  timestamp: string;
  type: 'tool_use' | 'tool_result' | 'text' | 'hook' | 'user_input' | 'system';
  summary: string;
  detail?: string;
}

export interface AgentActivity {
  sessionId: string;
  slug?: string;
  projectPath: string;
  projectName: string;
  agentId?: string;
  status: 'active' | 'recent' | 'completed';
  lastTimestamp: string;
  startTimestamp?: string;
  lastType: string;
  lastMessage: string;
  currentTool?: string;
  duration?: string;
  entryCount: number;
  subagentCount: number;
  version?: string;
  gitBranch?: string;
  recentEvents: ActivityEvent[];
}

export interface LiveDashboard {
  activeAgents: AgentActivity[];
  recentAgents: AgentActivity[];
  totalSessions: number;
}

// ── Path decoding ──
// Claude encodes project paths as: /mnt/c/.netapps/socialhomes → -mnt-c--netapps-socialhomes
// Rules: leading / becomes leading -, each / becomes -, literal . becomes -, but the FIRST entry's
// cwd field has the real path, so we read it from the JSONL itself.

function extractProjectName(dirName: string): string {
  // Take last segment after the final - that follows a known pattern
  // e.g. -mnt-c--netapps-socialhomes → socialhomes
  const parts = dirName.split('-').filter(Boolean);
  return parts[parts.length - 1] ?? dirName;
}

// ── File reading ──

/** Read the last N bytes of a file and return lines */
async function tailFileBytes(filePath: string, maxBytes: number): Promise<string[]> {
  try {
    const fileStat = await stat(filePath);
    const size = fileStat.size;
    const readStart = Math.max(0, size - maxBytes);
    const fh = await open(filePath, 'r');
    try {
      const buf = Buffer.alloc(Math.min(maxBytes, size));
      await fh.read(buf, 0, buf.length, readStart);
      const text = buf.toString('utf-8');
      const lines = text.split('\n').filter(l => l.trim());
      // First line may be partial if we started mid-file; skip it unless we read from start
      if (readStart > 0 && lines.length > 0) lines.shift();
      return lines;
    } finally {
      await fh.close();
    }
  } catch {
    return [];
  }
}

/** Read the first line of a file */
async function readFirstLine(filePath: string): Promise<string | null> {
  try {
    const fh = await open(filePath, 'r');
    try {
      const buf = Buffer.alloc(4096);
      await fh.read(buf, 0, 4096, 0);
      const text = buf.toString('utf-8');
      const nl = text.indexOf('\n');
      return nl >= 0 ? text.slice(0, nl) : text;
    } finally {
      await fh.close();
    }
  } catch {
    return null;
  }
}

function parseJsonl(line: string): Record<string, unknown> | null {
  try {
    return JSON.parse(line) as Record<string, unknown>;
  } catch {
    return null;
  }
}

function formatDuration(startMs: number, endMs: number): string {
  const diff = endMs - startMs;
  const secs = Math.floor(diff / 1000);
  const mins = Math.floor(secs / 60);
  const hours = Math.floor(mins / 60);
  if (hours > 0) return `${hours}h ${mins % 60}m`;
  if (mins > 0) return `${mins}m ${secs % 60}s`;
  return `${secs}s`;
}

// ── Activity parsing ──

function parseActivityEvents(lines: string[], maxEvents: number): ActivityEvent[] {
  const events: ActivityEvent[] = [];

  for (let i = lines.length - 1; i >= 0 && events.length < maxEvents; i--) {
    const entry = parseJsonl(lines[i]!);
    if (!entry) continue;

    const ts = entry.timestamp as string;
    const type = entry.type as string;

    if (type === 'assistant') {
      const msg = entry.message as Record<string, unknown> | undefined;
      if (!msg) continue;
      const content = msg.content;

      if (Array.isArray(content)) {
        for (const block of content) {
          const b = block as Record<string, unknown>;
          if (b.type === 'tool_use') {
            const toolName = b.name as string;
            const input = b.input as Record<string, unknown> | undefined;
            const summary = describeToolUse(toolName, input);
            events.push({ timestamp: ts, type: 'tool_use', summary, detail: toolName });
          } else if (b.type === 'text' && typeof b.text === 'string') {
            const text = (b.text as string).trim();
            if (text.length > 0) {
              events.push({ timestamp: ts, type: 'text', summary: text.slice(0, 150) });
            }
          }
        }
      } else if (typeof content === 'string' && content.trim()) {
        events.push({ timestamp: ts, type: 'text', summary: content.slice(0, 150) });
      }
    } else if (type === 'progress') {
      const data = entry.data as Record<string, unknown> | undefined;
      if (!data) continue;
      if (data.type === 'hook_progress') {
        events.push({
          timestamp: ts,
          type: 'hook',
          summary: `Hook: ${data.hookEvent as string ?? 'unknown'}`,
          detail: data.hookName as string | undefined,
        });
      }
    } else if (type === 'user') {
      const msg = entry.message as Record<string, unknown> | undefined;
      if (msg) {
        const content = typeof msg.content === 'string' ? msg.content : '';
        events.push({
          timestamp: ts,
          type: 'user_input',
          summary: content.slice(0, 100) || 'User message',
        });
      }
    }
  }

  return events.reverse(); // Chronological order
}

function describeToolUse(toolName: string, input: Record<string, unknown> | undefined): string {
  if (!input) return toolName;

  switch (toolName) {
    case 'Read':
      return `Read ${shortenPath(input.file_path as string)}`;
    case 'Write':
      return `Write ${shortenPath(input.file_path as string)}`;
    case 'Edit':
      return `Edit ${shortenPath(input.file_path as string)}`;
    case 'Bash':
      return `Bash: ${(input.command as string ?? '').slice(0, 80)}`;
    case 'Glob':
      return `Glob: ${input.pattern as string ?? ''}`;
    case 'Grep':
      return `Grep: ${(input.pattern as string ?? '').slice(0, 50)}`;
    case 'Task':
      return `Agent: ${(input.description as string ?? input.prompt as string ?? '').slice(0, 60)}`;
    case 'TaskCreate':
      return `Task: ${(input.subject as string ?? '').slice(0, 60)}`;
    case 'TaskUpdate':
      return `Task #${input.taskId as string}: ${input.status as string ?? 'update'}`;
    case 'WebFetch':
      return `Fetch: ${(input.url as string ?? '').slice(0, 60)}`;
    case 'WebSearch':
      return `Search: ${(input.query as string ?? '').slice(0, 60)}`;
    case 'mcp__telegram__notify_user':
    case 'mcp__telegram__send_message':
      return `Telegram: ${(input.message as string ?? '').slice(0, 60)}`;
    default:
      return toolName;
  }
}

function shortenPath(p: string | undefined): string {
  if (!p) return '?';
  const parts = p.split('/');
  if (parts.length <= 3) return p;
  return `.../${parts.slice(-2).join('/')}`;
}

/** Describe what the agent is doing right now based on the last entry */
function describeCurrentActivity(lastEntry: Record<string, unknown>): { message: string; currentTool?: string } {
  const type = lastEntry.type as string;

  if (type === 'assistant') {
    const msg = lastEntry.message as Record<string, unknown> | undefined;
    if (msg && Array.isArray(msg.content)) {
      for (const block of msg.content as Array<Record<string, unknown>>) {
        if (block.type === 'tool_use') {
          const toolName = block.name as string;
          const input = block.input as Record<string, unknown> | undefined;
          return {
            message: describeToolUse(toolName, input),
            currentTool: toolName,
          };
        }
        if (block.type === 'text' && typeof block.text === 'string') {
          return { message: (block.text as string).slice(0, 120) };
        }
      }
    }
    if (msg && typeof msg.content === 'string') {
      return { message: (msg.content as string).slice(0, 120) };
    }
    return { message: 'Generating response...' };
  }

  if (type === 'progress') {
    const data = lastEntry.data as Record<string, unknown> | undefined;
    if (data?.type === 'hook_progress') {
      return { message: `Running hook: ${data.hookEvent as string ?? ''}` };
    }
    return { message: 'Processing...' };
  }

  if (type === 'user') return { message: 'Waiting for input' };
  return { message: type };
}

// ── Session scanner ──

async function scanSession(
  jsonlPath: string,
  projectDirName: string,
): Promise<AgentActivity | null> {
  try {
    const fileStat = await stat(jsonlPath);
    const modifiedMs = fileStat.mtimeMs;
    const now = Date.now();
    const age = now - modifiedMs;

    if (age > RECENT_THRESHOLD_MS) return null;

    const sessionId = path.basename(jsonlPath, '.jsonl');

    // Read first line for session metadata
    const firstLineStr = await readFirstLine(jsonlPath);
    const firstEntry = firstLineStr ? parseJsonl(firstLineStr) : null;

    // Read last ~64KB for recent activity
    const lastLines = await tailFileBytes(jsonlPath, 65536);
    if (lastLines.length === 0) return null;

    const lastEntry = parseJsonl(lastLines[lastLines.length - 1]!);
    if (!lastEntry) return null;

    // Get project path from the JSONL cwd field (most reliable)
    const cwd = (firstEntry?.cwd ?? lastEntry.cwd) as string | undefined;
    const projectName = cwd
      ? path.basename(cwd)
      : extractProjectName(projectDirName);
    const projectPath = cwd ?? `/${projectDirName.replace(/^-/, '').replace(/-/g, '/')}`;

    // Count subagents
    let subagentCount = 0;
    const subagentDir = path.join(path.dirname(jsonlPath), sessionId, 'subagents');
    try {
      const subagents = await readdir(subagentDir);
      subagentCount = subagents.filter(f => f.endsWith('.jsonl')).length;
    } catch { /* no subagents dir */ }

    const status: AgentActivity['status'] = age <= ACTIVE_THRESHOLD_MS ? 'active' : 'recent';

    const startTime = firstEntry?.timestamp as string | undefined;
    const endTime = lastEntry.timestamp as string | undefined;
    let duration: string | undefined;
    if (startTime && endTime) {
      duration = formatDuration(new Date(startTime).getTime(), new Date(endTime).getTime());
    }

    const { message, currentTool } = describeCurrentActivity(lastEntry);

    // Parse last 20 activity events from the tail
    const recentEvents = parseActivityEvents(lastLines, 20);

    // Count total lines via file size estimation (~500 bytes per line avg)
    const estimatedEntries = Math.round(fileStat.size / 500);

    return {
      sessionId,
      slug: (lastEntry.slug ?? firstEntry?.slug) as string | undefined,
      projectPath,
      projectName,
      status,
      lastTimestamp: endTime ?? new Date(modifiedMs).toISOString(),
      startTimestamp: startTime,
      lastType: lastEntry.type as string,
      lastMessage: message,
      currentTool,
      duration,
      entryCount: estimatedEntries,
      subagentCount,
      version: (firstEntry?.version ?? lastEntry.version) as string | undefined,
      gitBranch: (firstEntry?.gitBranch ?? lastEntry.gitBranch) as string | undefined,
      recentEvents,
    };
  } catch {
    return null;
  }
}

// ── Public API ──

export async function getLiveAgentDashboard(): Promise<LiveDashboard> {
  const activeAgents: AgentActivity[] = [];
  const recentAgents: AgentActivity[] = [];
  let totalSessions = 0;

  try {
    const projectDirs = await readdir(PROJECTS_DIR);

    for (const projectDir of projectDirs) {
      const projectPath = path.join(PROJECTS_DIR, projectDir);
      const dirStat = await stat(projectPath);
      if (!dirStat.isDirectory()) continue;

      const entries = await readdir(projectPath);
      const jsonlFiles = entries.filter(e => e.endsWith('.jsonl'));
      totalSessions += jsonlFiles.length;

      for (const jsonl of jsonlFiles) {
        const activity = await scanSession(
          path.join(projectPath, jsonl),
          projectDir,
        );
        if (!activity) continue;

        if (activity.status === 'active') {
          activeAgents.push(activity);
        } else {
          recentAgents.push(activity);
        }
      }
    }
  } catch (err) {
    console.error('Failed to scan agent sessions:', err);
  }

  activeAgents.sort((a, b) => new Date(b.lastTimestamp).getTime() - new Date(a.lastTimestamp).getTime());
  recentAgents.sort((a, b) => new Date(b.lastTimestamp).getTime() - new Date(a.lastTimestamp).getTime());

  return { activeAgents, recentAgents, totalSessions };
}

export async function getProjectAgents(projectName: string): Promise<AgentActivity[]> {
  const dashboard = await getLiveAgentDashboard();
  return [...dashboard.activeAgents, ...dashboard.recentAgents]
    .filter(a => a.projectName === projectName);
}

