// Mirror of server types for the frontend

export interface ProjectSummary {
  name: string;
  path: string;
  status: 'active' | 'stale' | 'unconfigured';
  description?: string;
  gitBranch?: string;
  lastCommitTime?: string;
  lastCommitMessage?: string;
  testPassRate?: number;
  bugCount?: number;
  warningCount?: number;
  currentPhase?: string;
  phaseProgress?: number;
  isGitRepo: boolean;
  isBootstrapped: boolean;
}

export interface GitCommit {
  hash: string;
  shortHash: string;
  message: string;
  author: string;
  date: string;
  relativeDate: string;
}

export interface GitStatus {
  branch: string;
  remoteUrl?: string;
  dirty: boolean;
  dirtyFiles: string[];
  ahead: number;
  behind: number;
}

export interface GitData {
  status: GitStatus;
  commits: GitCommit[];
}

export interface Bug {
  id: string;
  title: string;
  page: string;
  severity: string;
  description: string;
  status?: string;
}

export interface Warning {
  id: string;
  title: string;
  page: string;
  severity: string;
  description: string;
}

export interface TestData {
  totalTests: number;
  passing: number;
  failing: number;
  warnings: number;
  passRate: number;
  bugs: Bug[];
  warningList: Warning[];
  generatedDate?: string;
}

export interface PlanTask {
  id: string;
  task: string;
  status: string;
  priority?: string;
  complexity?: string;
  agent?: string;
}

export interface Phase {
  id: string;
  name: string;
  goal?: string;
  tasks: PlanTask[];
  totalTasks: number;
  completedTasks: number;
  progress: number;
}

export interface RoadmapData {
  phases: Phase[];
  totalTasks: number;
  completedTasks: number;
  overallProgress: number;
}

export interface Session {
  id: string;
  date: string;
  agent?: string;
  workingDirectory?: string;
  summary: string[];
  commits: string[];
  decisions: string[];
  pendingWork: string[];
}

export interface SessionData {
  sessions: Session[];
}

export interface McpServer {
  name: string;
  command: string;
  args?: string[];
}

export interface Hook {
  event: string;
  command: string;
}

export interface InfraData {
  mcpServers: McpServer[];
  hooks: Hook[];
  deploymentUrl?: string;
  cloudRunRegion?: string;
  cicd: {
    hasCloudBuild: boolean;
    hasDockerfile: boolean;
  };
  packageName?: string;
  scripts: Record<string, string>;
  dependencies: Record<string, string>;
  devDependencies: Record<string, string>;
}

export interface ProjectDetail extends ProjectSummary {
  git?: GitData;
  tests?: TestData;
  roadmap?: RoadmapData;
  sessions?: SessionData;
  infra?: InfraData;
  techStack?: string[];
  commands?: Record<string, string>;
}

// ── Live Agent Monitoring ──

export interface ActivityEvent {
  timestamp: string;
  type: 'tool_use' | 'tool_result' | 'text' | 'hook' | 'user_input' | 'system';
  summary: string;
  detail?: string;
}

export interface AgentActivity {
  sessionId: string;
  slug?: string;
  projectPath: string;
  projectName: string;
  agentId?: string;
  status: 'active' | 'recent' | 'completed';
  lastTimestamp: string;
  startTimestamp?: string;
  lastType: string;
  lastMessage: string;
  currentTool?: string;
  duration?: string;
  entryCount: number;
  subagentCount: number;
  version?: string;
  gitBranch?: string;
  recentEvents: ActivityEvent[];
}

export interface LiveDashboard {
  activeAgents: AgentActivity[];
  recentAgents: AgentActivity[];
  totalSessions: number;
}

// ── Pipeline / Agent Roles ──

export interface AgentRole {
  name: string;
  responsibility: string;
  keyFiles: string[];
  pipelineOrder: number;
}

export interface AgentTask {
  id: string;
  task: string;
  status: 'DONE' | 'TODO' | 'IN_PROGRESS';
  phase: string;
  phaseId: string;
  priority?: string;
  complexity?: string;
}

export interface AgentDocument {
  name: string;
  path: string;
  sizeKb: number;
  lastModified: string;
  producer: string;
}

export interface PipelineAgent {
  role: AgentRole;
  status: 'idle' | 'active' | 'blocked' | 'complete';
  tasks: AgentTask[];
  totalTasks: number;
  completedTasks: number;
  progress: number;
  documents: AgentDocument[];
  currentPhase?: string;
  blockedReason?: string;
}

export interface PipelineData {
  agents: PipelineAgent[];
  currentPhase: string;
  currentPhaseId: string;
  overallProgress: number;
  totalTasks: number;
  completedTasks: number;
}

import type { ProjectSummary, ProjectDetail, LiveDashboard, AgentActivity, PipelineData } from '../types';

const BASE = '/api';

async function fetchJson<T>(url: string): Promise<T> {
  const res = await fetch(`${BASE}${url}`);
  if (!res.ok) throw new Error(`API error: ${res.status} ${res.statusText}`);
  return res.json() as Promise<T>;
}

export async function fetchProjects(): Promise<ProjectSummary[]> {
  return fetchJson<ProjectSummary[]>('/projects');
}

export async function fetchProjectDetail(name: string): Promise<ProjectDetail> {
  return fetchJson<ProjectDetail>(`/projects/${encodeURIComponent(name)}`);
}

export async function bootstrapProject(name: string): Promise<{ success: boolean; message: string }> {
  const res = await fetch(`${BASE}/projects/${encodeURIComponent(name)}/bootstrap`, {
    method: 'POST',
  });
  if (!res.ok) throw new Error('Bootstrap failed');
  return res.json() as Promise<{ success: boolean; message: string }>;
}

export async function fetchLiveAgents(): Promise<LiveDashboard> {
  return fetchJson<LiveDashboard>('/agents/live');
}

export async function fetchProjectAgents(name: string): Promise<AgentActivity[]> {
  return fetchJson<AgentActivity[]>(`/agents/project/${encodeURIComponent(name)}`);
}

export async function fetchPipeline(name: string): Promise<PipelineData | null> {
  return fetchJson<PipelineData | null>(`/projects/${encodeURIComponent(name)}/pipeline`);
}

