import { useState } from 'react';
import { useParams } from 'react-router-dom';
import { useQuery } from '@tanstack/react-query';
import { fetchProjectDetail } from '../services/api';
import { GitBranch, Target, ShieldCheck, Bot, Server, Users } from 'lucide-react';
import type { ProjectDetail } from '../types';
import OverviewTab from '../components/detail/OverviewTab';
import PipelineTab from '../components/detail/PipelineTab';
import ProgressTab from '../components/detail/ProgressTab';
import QualityTab from '../components/detail/QualityTab';
import SessionsTab from '../components/detail/SessionsTab';
import InfraTab from '../components/detail/InfraTab';

const TABS = [
  { id: 'overview', label: 'Overview', icon: GitBranch },
  { id: 'pipeline', label: 'Pipeline', icon: Users },
  { id: 'progress', label: 'Progress', icon: Target },
  { id: 'quality', label: 'Quality', icon: ShieldCheck },
  { id: 'sessions', label: 'Sessions', icon: Bot },
  { id: 'infra', label: 'Infrastructure', icon: Server },
] as const;

type TabId = typeof TABS[number]['id'];

export default function ProjectDetailPage() {
  const { name } = useParams<{ name: string }>();
  const [activeTab, setActiveTab] = useState<TabId>('overview');

  const { data: project, isLoading, error } = useQuery<ProjectDetail>({
    queryKey: ['project', name],
    queryFn: () => fetchProjectDetail(name!),
    enabled: !!name,
  });

  if (isLoading) {
    return (
      <div className="space-y-4 animate-fade-in">
        <div className="glass-card rounded-xl h-20 animate-shimmer" />
        <div className="glass-card rounded-xl h-12 animate-shimmer" />
        <div className="glass-card rounded-xl h-64 animate-shimmer" />

