import { useState } from 'react';
import { useQuery } from '@tanstack/react-query';
import { fetchPipeline, fetchProjectAgents, fetchDocumentContent } from '../../services/api';
import {
  Users, CheckCircle2, Circle, Clock, AlertTriangle,
  FileText, ArrowRight, X, Eye,
  Briefcase, Code2, Paintbrush, TestTube, Rocket,
} from 'lucide-react';
import { formatDistanceToNow } from 'date-fns';
import type { PipelineAgent, PipelineDependency, AgentTask, AgentDocument, AgentActivity, DocumentContent } from '../../types';

// ── Constants ──

const ROLE_ICONS: Record<string, typeof Users> = {
  'BA': Briefcase,
  'Claude Code': Code2,
  'Fullstack': Code2,
  'Frontend': Paintbrush,
  'Tester': TestTube,
  'DevOps': Rocket,
};

const STATUS_CONFIG = {
  idle:     { bg: 'bg-text-muted/10', border: 'border-text-muted/30', text: 'text-text-muted', label: 'Idle', ring: '#6b7280', topBar: 'bg-text-muted/40' },
  active:   { bg: 'bg-brand-teal/10', border: 'border-brand-teal/30', text: 'text-brand-teal', label: 'Active', ring: '#2dd4bf', topBar: 'bg-brand-teal' },
  blocked:  { bg: 'bg-status-warning/10', border: 'border-status-warning/30', text: 'text-status-warning', label: 'Blocked', ring: '#f59e0b', topBar: 'bg-brand-peach' },
  complete: { bg: 'bg-status-compliant/10', border: 'border-status-compliant/30', text: 'text-status-compliant', label: 'Complete', ring: '#22c55e', topBar: 'bg-status-compliant' },
};

// ── ProgressRing ──

function ProgressRing({ progress, status, size = 56 }: { progress: number; status: PipelineAgent['status']; size?: number }) {
  const stroke = 4;
  const radius = (size - stroke) / 2;
  const circumference = 2 * Math.PI * radius;
  const offset = circumference - (progress / 100) * circumference;
  const color = STATUS_CONFIG[status].ring;

  return (
    <svg width={size} height={size} className="shrink-0">
      <circle
        cx={size / 2} cy={size / 2} r={radius}
        fill="none" stroke="currentColor" strokeWidth={stroke}
        className="text-surface-elevated"
      />
      <circle
        cx={size / 2} cy={size / 2} r={radius}
        fill="none" stroke={color} strokeWidth={stroke}
        strokeDasharray={circumference} strokeDashoffset={offset}
        strokeLinecap="round"
        transform={`rotate(-90 ${size / 2} ${size / 2})`}
        className="transition-all duration-700"
      />
      <text
        x={size / 2} y={size / 2}
        textAnchor="middle" dominantBaseline="central"
        className="fill-text-primary text-[11px] font-mono font-bold"
      >
        {progress}%
      </text>
    </svg>
  );
}

// ── DependencyArrow ──

function DependencyArrow({ dep }: { dep: PipelineDependency }) {
  const isBlocking = dep.status === 'blocking';
  return (
    <div className="flex items-center px-1 shrink-0">
      <div className={`w-6 h-0.5 ${isBlocking ? 'bg-brand-peach/60' : 'bg-status-compliant/60'}`} />
      <ArrowRight size={14} className={isBlocking ? 'text-brand-peach/60' : 'text-status-compliant/60'} />
    </div>
  );
}

// ── AgentColumn ──

function AgentColumn({
  agent,
  isSelected,
  isLive,
  onClick,
}: {
  agent: PipelineAgent;
  isSelected: boolean;
  isLive: boolean;
  onClick: () => void;
}) {
  const sc = STATUS_CONFIG[agent.status];
  const Icon = ROLE_ICONS[agent.role.name] ?? Users;

  return (
    <button
      onClick={onClick}
      className={`w-[180px] shrink-0 rounded-xl overflow-hidden border transition-all duration-200 cursor-pointer
        ${isSelected ? 'ring-2 ring-brand-teal shadow-lg shadow-brand-teal/10' : 'hover:shadow-md'}
        ${sc.border} bg-surface-card`}
    >
      {/* Color-coded top bar */}
      <div className={`h-1.5 ${sc.topBar}`} />

      <div className="p-3 space-y-3">
        {/* Icon + Name + Status */}
        <div className="flex items-center gap-2">
          <div className={`p-1.5 rounded-lg ${sc.bg}`}>
            <Icon size={16} className={sc.text} />
          </div>
          <div className="flex-1 min-w-0 text-left">
            <div className="text-xs font-heading font-semibold text-text-primary truncate">{agent.role.name}</div>
            <span className={`text-[9px] font-mono uppercase tracking-wider ${sc.text}`}>{sc.label}</span>
          </div>
        </div>

        {/* Progress ring */}
        <div className="flex justify-center">
          <ProgressRing progress={agent.progress} status={agent.status} />
        </div>

        {/* Task count + dot visualization */}
        <div className="text-center">
          <div className={`text-sm font-heading font-bold ${sc.text}`}>
            {agent.completedTasks}/{agent.totalTasks}
          </div>
          {agent.totalTasks > 0 && (
            <div className="flex justify-center gap-0.5 mt-1 flex-wrap">
              {Array.from({ length: Math.min(agent.totalTasks, 20) }, (_, i) => (
                <div
                  key={i}
                  className={`w-1.5 h-1.5 rounded-full ${
                    i < agent.completedTasks ? 'bg-status-compliant' : 'bg-text-muted/30'
                  }`}
                />
              ))}
              {agent.totalTasks > 20 && (
                <span className="text-[8px] text-text-muted ml-0.5">+{agent.totalTasks - 20}</span>
              )}
            </div>
          )}
        </div>

        {/* Document count badge */}
        {agent.documents.length > 0 && (
          <div className="flex justify-center">
            <span className="inline-flex items-center gap-1 text-[9px] text-brand-peach bg-brand-peach/10 px-1.5 py-0.5 rounded-full">
              <FileText size={9} /> {agent.documents.length} doc{agent.documents.length !== 1 ? 's' : ''}
            </span>
          </div>
        )}

        {/* LIVE indicator */}
        {isLive && (
          <div className="flex justify-center">
            <span className="inline-flex items-center gap-1.5 text-[9px] font-mono text-brand-teal">
              <span className="relative flex h-2 w-2">
                <span className="animate-ping absolute inline-flex h-full w-full rounded-full bg-brand-teal opacity-75" />
                <span className="relative inline-flex rounded-full h-2 w-2 bg-brand-teal" />
              </span>
              LIVE
            </span>
          </div>
        )}

        {/* Blocked reason */}
        {agent.blockedReason && (
          <div className="flex items-start gap-1 text-[9px] text-status-warning bg-status-warning/5 rounded px-1.5 py-1">
            <AlertTriangle size={9} className="shrink-0 mt-0.5" />
            <span>{agent.blockedReason}</span>
          </div>
        )}
      </div>
    </button>
  );
}

// ── AgentDetailDrawer ──

function AgentDetailDrawer({
  agent,
  liveAgent,
  onDocClick,
}: {
  agent: PipelineAgent;
  liveAgent?: AgentActivity;
  onDocClick: (doc: AgentDocument) => void;
}) {
  const sc = STATUS_CONFIG[agent.status];

  // Group tasks by phase
  const tasksByPhase = agent.tasks.reduce<Record<string, AgentTask[]>>((acc, t) => {
    const key = `Phase ${t.phaseId}: ${t.phase}`;
    (acc[key] ??= []).push(t);
    return acc;
  }, {});

  return (
    <div className={`glass-card rounded-xl border ${sc.border} overflow-hidden animate-fade-in`}>
      <div className={`h-1 ${sc.topBar}`} />
      <div className="p-5">
        <div className="flex items-center gap-2 mb-4">
          <h3 className="font-heading text-sm font-semibold text-text-primary">{agent.role.name}</h3>
          <span className={`text-[10px] font-mono uppercase tracking-wider px-1.5 py-0 rounded-full ${sc.bg} ${sc.text} border ${sc.border}`}>
            {sc.label}
          </span>
          <span className="text-[10px] text-text-muted ml-auto">{agent.role.responsibility}</span>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-3 gap-5">
          {/* Left: Tasks */}
          <div className="space-y-3">
            <h4 className="text-[10px] text-text-muted uppercase tracking-wider font-semibold flex items-center gap-1">
              <CheckCircle2 size={10} /> Tasks ({agent.completedTasks}/{agent.totalTasks})
            </h4>
            <div className="space-y-3 max-h-72 overflow-y-auto pr-1">
              {Object.entries(tasksByPhase).map(([phase, tasks]) => (
                <div key={phase}>
                  <div className="text-[9px] font-mono text-brand-teal mb-1">{phase}</div>
                  <div className="space-y-0.5">
                    {tasks.map(task => (
                      <div key={task.id} className="flex items-start gap-1.5 py-0.5">
                        {task.status === 'DONE' ? (
                          <CheckCircle2 size={11} className="text-status-compliant mt-0.5 shrink-0" />
                        ) : (
                          <Circle size={11} className="text-text-muted mt-0.5 shrink-0" />
                        )}
                        <span className={`text-[10px] leading-tight ${task.status === 'DONE' ? 'text-text-muted line-through' : 'text-text-primary'}`}>
                          {task.task}
                        </span>
                        {task.priority && (
                          <span className={`text-[8px] font-mono shrink-0 ${
                            task.priority === 'P1' ? 'text-status-critical' :
                            task.priority === 'P2' ? 'text-status-warning' : 'text-text-muted'
                          }`}>{task.priority}</span>
                        )}
                      </div>
                    ))}
                  </div>
                </div>
              ))}
            </div>
          </div>

          {/* Middle: Documents */}
          <div className="space-y-3">
            <h4 className="text-[10px] text-text-muted uppercase tracking-wider font-semibold flex items-center gap-1">
              <FileText size={10} /> Documents ({agent.documents.length})
            </h4>
            {agent.documents.length === 0 ? (
              <p className="text-[10px] text-text-muted italic">No documents</p>
            ) : (
              <div className="space-y-1">
                {agent.documents.map(doc => (
                  <button
                    key={doc.name}
                    onClick={() => onDocClick(doc)}
                    className="w-full flex items-center gap-2 p-2 rounded-lg bg-surface-elevated hover:bg-surface-hover/30 transition-colors text-left group"
                  >
                    <FileText size={12} className="text-brand-peach shrink-0" />
                    <div className="flex-1 min-w-0">
                      <div className="text-[11px] font-mono text-text-primary truncate">{doc.name}</div>
                      <div className="text-[9px] text-text-muted">
                        {doc.sizeKb}KB &middot; {formatDistanceToNow(new Date(doc.lastModified), { addSuffix: true })}
                      </div>
                    </div>
                    <Eye size={12} className="text-text-muted opacity-0 group-hover:opacity-100 transition-opacity shrink-0" />
                  </button>
                ))}
              </div>
            )}
          </div>

          {/* Right: Live Activity */}
          <div className="space-y-3">
            <h4 className="text-[10px] text-text-muted uppercase tracking-wider font-semibold flex items-center gap-1">
              <Clock size={10} /> Live Activity
            </h4>
            {liveAgent ? (
              <div className="space-y-2">
                <div className="flex items-center gap-2">
                  <span className={`inline-flex items-center gap-1 text-[9px] font-mono px-1.5 py-0.5 rounded-full ${
                    liveAgent.status === 'active'
                      ? 'text-brand-teal bg-brand-teal/10'
                      : 'text-text-muted bg-text-muted/10'
                  }`}>
                    {liveAgent.status === 'active' && (
                      <span className="relative flex h-1.5 w-1.5">
                        <span className="animate-ping absolute inline-flex h-full w-full rounded-full bg-brand-teal opacity-75" />
                        <span className="relative inline-flex rounded-full h-1.5 w-1.5 bg-brand-teal" />
                      </span>
                    )}
                    {liveAgent.status}
                  </span>
                  {liveAgent.currentTool && (
                    <span className="text-[9px] font-mono text-brand-peach">{liveAgent.currentTool}</span>
                  )}
                  {liveAgent.duration && (
                    <span className="text-[9px] text-text-muted ml-auto">{liveAgent.duration}</span>
                  )}
                </div>
                <div className="space-y-1 max-h-52 overflow-y-auto pr-1">
                  {liveAgent.recentEvents.slice(-10).map((ev, i) => (
                    <div key={i} className="text-[9px] text-text-muted flex gap-1.5">
                      <span className="text-text-muted/60 shrink-0 font-mono w-12">
                        {new Date(ev.timestamp).toLocaleTimeString('en-GB', { hour: '2-digit', minute: '2-digit' })}
                      </span>
                      <span className={
                        ev.type === 'tool_use' ? 'text-brand-teal' :
                        ev.type === 'user_input' ? 'text-brand-peach' : 'text-text-muted'
                      }>
                        {ev.summary}
                      </span>
                    </div>
                  ))}
                </div>
              </div>
            ) : (
              <p className="text-[10px] text-text-muted italic">No active session</p>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

// ── DocumentPreviewModal ──

function DocumentPreviewModal({
  projectName,
  doc,
  onClose,
}: {
  projectName: string;
  doc: AgentDocument;
  onClose: () => void;
}) {
  const { data, isLoading } = useQuery<DocumentContent>({
    queryKey: ['doc-content', projectName, doc.path],
    queryFn: () => fetchDocumentContent(projectName, doc.path),
    staleTime: 60_000,
  });

  return (
    <div
      className="fixed inset-0 z-50 flex items-center justify-center p-4 bg-black/60 backdrop-blur-sm animate-fade-in"
      onClick={onClose}
    >
      <div
        className="glass-card rounded-2xl w-full max-w-4xl max-h-[85vh] flex flex-col overflow-hidden border border-border-subtle shadow-2xl"
        onClick={e => e.stopPropagation()}
      >
        {/* Header */}
        <div className="flex items-center justify-between px-5 py-3 border-b border-border-subtle">
          <div className="flex items-center gap-2">
            <FileText size={14} className="text-brand-peach" />
            <span className="font-mono text-sm text-text-primary">{doc.name}</span>
            <span className="text-[10px] text-text-muted">{doc.sizeKb}KB</span>
          </div>
          <button onClick={onClose} className="p-1 rounded-lg hover:bg-surface-hover/30 transition-colors">
            <X size={16} className="text-text-muted" />
          </button>
        </div>

        {/* Content */}
        <div className="flex-1 overflow-auto p-5">
          {isLoading ? (
            <div className="h-48 animate-shimmer rounded-lg" />
          ) : data ? (
            <pre className="text-[11px] font-mono text-text-primary whitespace-pre-wrap break-words leading-relaxed">
              {data.content}
            </pre>
          ) : (
            <p className="text-text-muted text-sm">Failed to load document</p>
          )}
        </div>

        {/* Footer */}
        {data && (
          <div className="px-5 py-2 border-t border-border-subtle text-[9px] text-text-muted flex gap-4">
            <span>{data.sizeBytes.toLocaleString()} bytes</span>
            <span>Modified {formatDistanceToNow(new Date(data.lastModified), { addSuffix: true })}</span>
          </div>
        )}
      </div>
    </div>
  );
}

// ── PipelineTab (main) ──

interface Props {
  projectName: string;
}

export default function PipelineTab({ projectName }: Props) {
  const [selectedAgent, setSelectedAgent] = useState<string | null>(null);
  const [previewDoc, setPreviewDoc] = useState<AgentDocument | null>(null);

  const { data: pipeline, isLoading } = useQuery({
    queryKey: ['pipeline', projectName],
    queryFn: () => fetchPipeline(projectName),
    refetchInterval: 30_000,
  });

  const { data: liveAgents } = useQuery({
    queryKey: ['agents-project', projectName],
    queryFn: () => fetchProjectAgents(projectName),
    refetchInterval: 5_000,
  });

  if (isLoading) {
    return <div className="glass-card rounded-xl h-64 animate-shimmer" />;
  }

  if (!pipeline) {
    return (
      <div className="glass-card rounded-xl p-8 text-center">
        <Users size={32} className="text-text-muted mx-auto mb-3" />
        <p className="text-text-muted text-sm">No execution plan found</p>
        <p className="text-text-muted text-xs mt-1">Create an EXECUTION-PLAN.md with agent assignments</p>
      </div>
    );
  }

  const selectedAgentData = pipeline.agents.find(a => a.role.name === selectedAgent);
  const liveAgentMap = new Map((liveAgents ?? []).map(a => [a.projectName, a]));

  // Match live agents to pipeline agents by checking if agent slug or session references them
  function getLiveAgent(agentName: string): AgentActivity | undefined {
    return (liveAgents ?? []).find(a =>
      a.slug?.toLowerCase().includes(agentName.toLowerCase()) ||
      a.lastMessage?.toLowerCase().includes(agentName.toLowerCase()) ||
      a.projectName === projectName
    );
  }

  function isAgentLive(agentName: string): boolean {
    return (liveAgents ?? []).some(a =>
      a.status === 'active' && (
        a.slug?.toLowerCase().includes(agentName.toLowerCase()) ||
        a.projectName === projectName
      )
    );
  }

  return (
    <div className="space-y-5">
      {/* Pipeline Header — overall progress */}
      <div className="glass-card rounded-xl p-5">
        <div className="flex items-center justify-between mb-3">
          <div>
            <h3 className="font-heading text-sm font-semibold text-brand-peach">Agent Pipeline</h3>
            <p className="text-xs text-text-muted mt-0.5">
              Current phase: <span className="text-brand-teal font-medium">{pipeline.currentPhase}</span>
            </p>
          </div>
          <div className="text-right">
            <div className="text-2xl font-heading font-bold text-brand-teal">{pipeline.overallProgress}%</div>
            <div className="text-[10px] text-text-muted">{pipeline.completedTasks}/{pipeline.totalTasks} tasks</div>
          </div>
        </div>
        <div className="h-3 rounded-full bg-surface-elevated overflow-hidden">
          <div
            className="h-full rounded-full bg-gradient-to-r from-brand-teal to-brand-blue transition-all duration-700"
            style={{ width: `${pipeline.overallProgress}%` }}
          />
        </div>
      </div>

      {/* Horizontal Swimlane */}
      <div className="glass-card rounded-xl p-5">
        <h3 className="font-heading text-xs font-semibold text-text-muted uppercase tracking-wider mb-4">Pipeline Flow</h3>
        <div className="flex items-center overflow-x-auto pb-2 gap-0">
          {pipeline.agents.map((agent, i) => {
            const dep = pipeline.dependencies.find(d => d.from === agent.role.name);
            return (
              <div key={agent.role.name} className="flex items-center shrink-0">
                <AgentColumn
                  agent={agent}
                  isSelected={selectedAgent === agent.role.name}
                  isLive={isAgentLive(agent.role.name)}
                  onClick={() => setSelectedAgent(
                    selectedAgent === agent.role.name ? null : agent.role.name
                  )}
                />
                {dep && <DependencyArrow dep={dep} />}
              </div>
            );
          })}
        </div>
      </div>

      {/* Agent Detail Drawer */}
      {selectedAgentData && (
        <AgentDetailDrawer
          agent={selectedAgentData}
          liveAgent={getLiveAgent(selectedAgentData.role.name)}
          onDocClick={setPreviewDoc}
        />
      )}

      {/* Document Preview Modal */}
      {previewDoc && (
        <DocumentPreviewModal
          projectName={projectName}
          doc={previewDoc}
          onClose={() => setPreviewDoc(null)}
        />
      )}
    </div>
  );
}

toolUseResult.content:
import { useState } from 'react';
import { useQuery } from '@tanstack/react-query';
import { fetchPipeline, fetchProjectAgents, fetchDocumentContent } from '../../services/api';
import {
  Users, CheckCircle2, Circle, Clock, AlertTriangle,
  FileText, ArrowRight, X, Eye,
  Briefcase, Code2, Paintbrush, TestTube, Rocket,
} from 'lucide-react';
import { formatDistanceToNow } from 'date-fns';
import type { PipelineAgent, PipelineDependency, AgentTask, AgentDocument, AgentActivity, DocumentContent } from '../../types';

// ── Constants ──

const ROLE_ICONS: Record<string, typeof Users> = {
  'BA': Briefcase,
  'Claude Code': Code2,
  'Fullstack': Code2,
  'Frontend': Paintbrush,
  'Tester': TestTube,
  'DevOps': Rocket,
};

const STATUS_CONFIG = {
  idle:     { bg: 'bg-text-muted/10', border: 'border-text-muted/30', text: 'text-text-muted', label: 'Idle', ring: '#6b7280', topBar: 'bg-text-muted/40' },
  active:   { bg: 'bg-brand-teal/10', border: 'border-brand-teal/30', text: 'text-brand-teal', label: 'Active', ring: '#2dd4bf', topBar: 'bg-brand-teal' },
  blocked:  { bg: 'bg-status-warning/10', border: 'border-status-warning/30', text: 'text-status-warning', label: 'Blocked', ring: '#f59e0b', topBar: 'bg-brand-peach' },
  complete: { bg: 'bg-status-compliant/10', border: 'border-status-compliant/30', text: 'text-status-compliant', label: 'Complete', ring: '#22c55e', topBar: 'bg-status-compliant' },
};

// ── ProgressRing ──

function ProgressRing({ progress, status, size = 56 }: { progress: number; status: PipelineAgent['status']; size?: number }) {
  const stroke = 4;
  const radius = (size - stroke) / 2;
  const circumference = 2 * Math.PI * radius;
  const offset = circumference - (progress / 100) * circumference;
  const color = STATUS_CONFIG[status].ring;

  return (
    <svg width={size} height={size} className="shrink-0">
      <circle
        cx={size / 2} cy={size / 2} r={radius}
        fill="none" stroke="currentColor" strokeWidth={stroke}
        className="text-surface-elevated"
      />
      <circle
        cx={size / 2} cy={size / 2} r={radius}
        fill="none" stroke={color} strokeWidth={stroke}
        strokeDasharray={circumference} strokeDashoffset={offset}
        strokeLinecap="round"
        transform={`rotate(-90 ${size / 2} ${size / 2})`}
        className="transition-all duration-700"
      />
      <text
        x={size / 2} y={size / 2}
        textAnchor="middle" dominantBaseline="central"
        className="fill-text-primary text-[11px] font-mono font-bold"
      >
        {progress}%
      </text>
    </svg>
  );
}

// ── DependencyArrow ──

function DependencyArrow({ dep }: { dep: PipelineDependency }) {
  const isBlocking = dep.status === 'blocking';
  return (
    <div className="flex items-center px-1 shrink-0">
      <div className={`w-6 h-0.5 ${isBlocking ? 'bg-brand-peach/60' : 'bg-status-compliant/60'}`} />
      <ArrowRight size={14} className={isBlocking ? 'text-brand-peach/60' : 'text-status-compliant/60'} />
    </div>
  );
}

// ── AgentColumn ──

function AgentColumn({
  agent,
  isSelected,
  isLive,
  onClick,
}: {
  agent: PipelineAgent;
  isSelected: boolean;
  isLive: boolean;
  onClick: () => void;
}) {
  const sc = STATUS_CONFIG[agent.status];
  const Icon = ROLE_ICONS[agent.role.name] ?? Users;

  return (
    <button
      onClick={onClick}
      className={`w-[180px] shrink-0 rounded-xl overflow-hidden border transition-all duration-200 cursor-pointer
        ${isSelected ? 'ring-2 ring-brand-teal shadow-lg shadow-brand-teal/10' : 'hover:shadow-md'}
        ${sc.border} bg-surface-card`}
    >
      {/* Color-coded top bar */}
      <div className={`h-1.5 ${sc.topBar}`} />

      <div className="p-3 space-y-3">
        {/* Icon + Name + Status */}
        <div className="flex items-center gap-2">
          <div className={`p-1.5 rounded-lg ${sc.bg}`}>
            <Icon size={16} className={sc.text} />
          </div>
          <div className="flex-1 min-w-0 text-left">
            <div className="text-xs font-heading font-semibold text-text-primary truncate">{agent.role.name}</div>
            <span className={`text-[9px] font-mono uppercase tracking-wider ${sc.text}`}>{sc.label}</span>
          </div>
        </div>

        {/* Progress ring */}
        <div className="flex justify-center">
          <ProgressRing progress={agent.progress} status={agent.status} />
        </div>

        {/* Task count + dot visualization */}
        <div className="text-center">
          <div className={`text-sm font-heading font-bold ${sc.text}`}>
            {agent.completedTasks}/{agent.totalTasks}
          </div>
          {agent.totalTasks > 0 && (
            <div className="flex justify-center gap-0.5 mt-1 flex-wrap">
              {Array.from({ length: Math.min(agent.totalTasks, 20) }, (_, i) => (
                <div
                  key={i}
                  className={`w-1.5 h-1.5 rounded-full ${
                    i < agent.completedTasks ? 'bg-status-compliant' : 'bg-text-muted/30'
                  }`}
                />
              ))}
              {agent.totalTasks > 20 && (
                <span className="text-[8px] text-text-muted ml-0.5">+{agent.totalTasks - 20}</span>
              )}
            </div>
          )}
        </div>

        {/* Document count badge */}
        {agent.documents.length > 0 && (
          <div className="flex justify-center">
            <span className="inline-flex items-center gap-1 text-[9px] text-brand-peach bg-brand-peach/10 px-1.5 py-0.5 rounded-full">
              <FileText size={9} /> {agent.documents.length} doc{agent.documents.length !== 1 ? 's' : ''}
            </span>
          </div>
        )}

        {/* LIVE indicator */}
        {isLive && (
          <div className="flex justify-center">
            <span className="inline-flex items-center gap-1.5 text-[9px] font-mono text-brand-teal">
              <span className="relative flex h-2 w-2">
                <span className="animate-ping absolute inline-flex h-full w-full rounded-full bg-brand-teal opacity-75" />
                <span className="relative inline-flex rounded-full h-2 w-2 bg-brand-teal" />
              </span>
              LIVE
            </span>
          </div>
        )}

        {/* Blocked reason */}
        {agent.blockedReason && (
          <div className="flex items-start gap-1 text-[9px] text-status-warning bg-status-warning/5 rounded px-1.5 py-1">
            <AlertTriangle size={9} className="shrink-0 mt-0.5" />
            <span>{agent.blockedReason}</span>
          </div>
        )}
      </div>
    </button>
  );
}

// ── AgentDetailDrawer ──

function AgentDetailDrawer({
  agent,
  liveAgent,
  onDocClick,
}: {
  agent: PipelineAgent;
  liveAgent?: AgentActivity;
  onDocClick: (doc: AgentDocument) => void;
}) {
  const sc = STATUS_CONFIG[agent.status];

  // Group tasks by phase
  const tasksByPhase = agent.tasks.reduce<Record<string, AgentTask[]>>((acc, t) => {
    const key = `Phase ${t.phaseId}: ${t.phase}`;
    (acc[key] ??= []).push(t);
    return acc;
  }, {});

  return (
    <div className={`glass-card rounded-xl border ${sc.border} overflow-hidden animate-fade-in`}>
      <div className={`h-1 ${sc.topBar}`} />
      <div className="p-5">
        <div className="flex items-center gap-2 mb-4">
          <h3 className="font-heading text-sm font-semibold text-text-primary">{agent.role.name}</h3>
          <span className={`text-[10px] font-mono uppercase tracking-wider px-1.5 py-0 rounded-full ${sc.bg} ${sc.text} border ${sc.border}`}>
            {sc.label}
          </span>
          <span className="text-[10px] text-text-muted ml-auto">{agent.role.responsibility}</span>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-3 gap-5">
          {/* Left: Tasks */}
          <div className="space-y-3">
            <h4 className="text-[10px] text-text-muted uppercase tracking-wider font-semibold flex items-center gap-1">
              <CheckCircle2 size={10} /> Tasks ({agent.completedTasks}/{agent.totalTasks})
            </h4>
            <div className="space-y-3 max-h-72 overflow-y-auto pr-1">
              {Object.entries(tasksByPhase).map(([phase, tasks]) => (
                <div key={phase}>
                  <div className="text-[9px] font-mono text-brand-teal mb-1">{phase}</div>
                  <div className="space-y-0.5">
                    {tasks.map(task => (
                      <div key={task.id} className="flex items-start gap-1.5 py-0.5">
                        {task.status === 'DONE' ? (
                          <CheckCircle2 size={11} className="text-status-compliant mt-0.5 shrink-0" />
                        ) : (
                          <Circle size={11} className="text-text-muted mt-0.5 shrink-0" />
                        )}
                        <span className={`text-[10px] leading-tight ${task.status === 'DONE' ? 'text-text-muted line-through' : 'text-text-primary'}`}>
                          {task.task}
                        </span>
                        {task.priority && (
                          <span className={`text-[8px] font-mono shrink-0 ${
                            task.priority === 'P1' ? 'text-status-critical' :
                            task.priority === 'P2' ? 'text-status-warning' : 'text-text-muted'
                          }`}>{task.priority}</span>
                        )}
                      </div>
                    ))}
                  </div>
                </div>
              ))}
            </div>
          </div>

          {/* Middle: Documents */}
          <div className="space-y-3">
            <h4 className="text-[10px] text-text-muted uppercase tracking-wider font-semibold flex items-center gap-1">
              <FileText size={10} /> Documents ({agent.documents.length})
            </h4>
            {agent.documents.length === 0 ? (
              <p className="text-[10px] text-text-muted italic">No documents</p>
            ) : (
              <div className="space-y-1">
                {agent.documents.map(doc => (
                  <button
                    key={doc.name}
                    onClick={() => onDocClick(doc)}
                    className="w-full flex items-center gap-2 p-2 rounded-lg bg-surface-elevated hover:bg-surface-hover/30 transition-colors text-left group"
                  >
                    <FileText size={12} className="text-brand-peach shrink-0" />
                    <div className="flex-1 min-w-0">
                      <div className="text-[11px] font-mono text-text-primary truncate">{doc.name}</div>
                      <div className="text-[9px] text-text-muted">
                        {doc.sizeKb}KB &middot; {formatDistanceToNow(new Date(doc.lastModified), { addSuffix: true })}
                      </div>
                    </div>
                    <Eye size={12} className="text-text-muted opacity-0 group-hover:opacity-100 transition-opacity shrink-0" />
                  </button>
                ))}
              </div>
            )}
          </div>

          {/* Right: Live Activity */}
          <div className="space-y-3">
            <h4 className="text-[10px] text-text-muted uppercase tracking-wider font-semibold flex items-center gap-1">
              <Clock size={10} /> Live Activity
            </h4>
            {liveAgent ? (
              <div className="space-y-2">
                <div className="flex items-center gap-2">
                  <span className={`inline-flex items-center gap-1 text-[9px] font-mono px-1.5 py-0.5 rounded-full ${
                    liveAgent.status === 'active'
                      ? 'text-brand-teal bg-brand-teal/10'
                      : 'text-text-muted bg-text-muted/10'
                  }`}>
                    {liveAgent.status === 'active' && (
                      <span className="relative flex h-1.5 w-1.5">
                        <span className="animate-ping absolute inline-flex h-full w-full rounded-full bg-brand-teal opacity-75" />
                        <span className="relative inline-flex rounded-full h-1.5 w-1.5 bg-brand-teal" />
                      </span>
                    )}
                    {liveAgent.status}
                  </span>
                  {liveAgent.currentTool && (
                    <span className="text-[9px] font-mono text-brand-peach">{liveAgent.currentTool}</span>
                  )}
                  {liveAgent.duration && (
                    <span className="text-[9px] text-text-muted ml-auto">{liveAgent.duration}</span>
                  )}
                </div>
                <div className="space-y-1 max-h-52 overflow-y-auto pr-1">
                  {liveAgent.recentEvents.slice(-10).map((ev, i) => (
                    <div key={i} className="text-[9px] text-text-muted flex gap-1.5">
                      <span className="text-text-muted/60 shrink-0 font-mono w-12">
                        {new Date(ev.timestamp).toLocaleTimeString('en-GB', { hour: '2-digit', minute: '2-digit' })}
                      </span>
                      <span className={
                        ev.type === 'tool_use' ? 'text-brand-teal' :
                        ev.type === 'user_input' ? 'text-brand-peach' : 'text-text-muted'
                      }>
                        {ev.summary}
                      </span>
                    </div>
                  ))}
                </div>
              </div>
            ) : (
              <p className="text-[10px] text-text-muted italic">No active session</p>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

// ── DocumentPreviewModal ──

function DocumentPreviewModal({
  projectName,
  doc,
  onClose,
}: {
  projectName: string;
  doc: AgentDocument;
  onClose: () => void;
}) {
  const { data, isLoading } = useQuery<DocumentContent>({
    queryKey: ['doc-content', projectName, doc.path],
    queryFn: () => fetchDocumentContent(projectName, doc.path),
    staleTime: 60_000,
  });

  return (
    <div
      className="fixed inset-0 z-50 flex items-center justify-center p-4 bg-black/60 backdrop-blur-sm animate-fade-in"
      onClick={onClose}
    >
      <div
        className="glass-card rounded-2xl w-full max-w-4xl max-h-[85vh] flex flex-col overflow-hidden border border-border-subtle shadow-2xl"
        onClick={e => e.stopPropagation()}
      >
        {/* Header */}
        <div className="flex items-center justify-between px-5 py-3 border-b border-border-subtle">
          <div className="flex items-center gap-2">
            <FileText size={14} className="text-brand-peach" />
            <span className="font-mono text-sm text-text-primary">{doc.name}</span>
            <span className="text-[10px] text-text-muted">{doc.sizeKb}KB</span>
          </div>
          <button onClick={onClose} className="p-1 rounded-lg hover:bg-surface-hover/30 transition-colors">
            <X size={16} className="text-text-muted" />
          </button>
        </div>

        {/* Content */}
        <div className="flex-1 overflow-auto p-5">
          {isLoading ? (
            <div className="h-48 animate-shimmer rounded-lg" />
          ) : data ? (
            <pre className="text-[11px] font-mono text-text-primary whitespace-pre-wrap break-words leading-relaxed">
              {data.content}
            </pre>
          ) : (
            <p className="text-text-muted text-sm">Failed to load document</p>
          )}
        </div>

        {/* Footer */}
        {data && (
          <div className="px-5 py-2 border-t border-border-subtle text-[9px] text-text-muted flex gap-4">
            <span>{data.sizeBytes.toLocaleString()} bytes</span>
            <span>Modified {formatDistanceToNow(new Date(data.lastModified), { addSuffix: true })}</span>
          </div>
        )}
      </div>
    </div>
  );
}

// ── PipelineTab (main) ──

interface Props {
  projectName: string;
}

export default function PipelineTab({ projectName }: Props) {
  const [selectedAgent, setSelectedAgent] = useState<string | null>(null);
  const [previewDoc, setPreviewDoc] = useState<AgentDocument | null>(null);

  const { data: pipeline, isLoading } = useQuery({
    queryKey: ['pipeline', projectName],
    queryFn: () => fetchPipeline(projectName),
    refetchInterval: 30_000,
  });

  const { data: liveAgents } = useQuery({
    queryKey: ['agents-project', projectName],
    queryFn: () => fetchProjectAgents(projectName),
    refetchInterval: 5_000,
  });

  if (isLoading) {
    return <div className="glass-card rounded-xl h-64 animate-shimmer" />;
  }

  if (!pipeline) {
    return (
      <div className="glass-card rounded-xl p-8 text-center">
        <Users size={32} className="text-text-muted mx-auto mb-3" />
        <p className="text-text-muted text-sm">No execution plan found</p>
        <p className="text-text-muted text-xs mt-1">Create an EXECUTION-PLAN.md with agent assignments</p>
      </div>
    );
  }

  const selectedAgentData = pipeline.agents.find(a => a.role.name === selectedAgent);
  const liveAgentMap = new Map((liveAgents ?? []).map(a => [a.projectName, a]));

  // Match live agents to pipeline agents by checking if agent slug or session references them
  function getLiveAgent(agentName: string): AgentActivity | undefined {
    return (liveAgents ?? []).find(a =>
      a.slug?.toLowerCase().includes(agentName.toLowerCase()) ||
      a.lastMessage?.toLowerCase().includes(agentName.toLowerCase()) ||
      a.projectName === projectName
    );
  }

  function isAgentLive(agentName: string): boolean {
    return (liveAgents ?? []).some(a =>
      a.status === 'active' && (
        a.slug?.toLowerCase().includes(agentName.toLowerCase()) ||
        a.projectName === projectName
      )
    );
  }

  return (
    <div className="space-y-5">
      {/* Pipeline Header — overall progress */}
      <div className="glass-card rounded-xl p-5">
        <div className="flex items-center justify-between mb-3">
          <div>
            <h3 className="font-heading text-sm font-semibold text-brand-peach">Agent Pipeline</h3>
            <p className="text-xs text-text-muted mt-0.5">
              Current phase: <span className="text-brand-teal font-medium">{pipeline.currentPhase}</span>
            </p>
          </div>
          <div className="text-right">
            <div className="text-2xl font-heading font-bold text-brand-teal">{pipeline.overallProgress}%</div>
            <div className="text-[10px] text-text-muted">{pipeline.completedTasks}/{pipeline.totalTasks} tasks</div>
          </div>
        </div>
        <div className="h-3 rounded-full bg-surface-elevated overflow-hidden">
          <div
            className="h-full rounded-full bg-gradient-to-r from-brand-teal to-brand-blue transition-all duration-700"
            style={{ width: `${pipeline.overallProgress}%` }}
          />
        </div>
      </div>

      {/* Horizontal Swimlane */}
      <div className="glass-card rounded-xl p-5">
        <h3 className="font-heading text-xs font-semibold text-text-muted uppercase tracking-wider mb-4">Pipeline Flow</h3>
        <div className="flex items-center overflow-x-auto pb-2 gap-0">
          {pipeline.agents.map((agent, i) => {
            const dep = pipeline.dependencies.find(d => d.from === agent.role.name);
            return (
              <div key={agent.role.name} className="flex items-center shrink-0">
                <AgentColumn
                  agent={agent}
                  isSelected={selectedAgent === agent.role.name}
                  isLive={isAgentLive(agent.role.name)}
                  onClick={() => setSelectedAgent(
                    selectedAgent === agent.role.name ? null : agent.role.name
                  )}
                />
                {dep && <DependencyArrow dep={dep} />}
              </div>
            );
          })}
        </div>
      </div>

      {/* Agent Detail Drawer */}
      {selectedAgentData && (
        <AgentDetailDrawer
          agent={selectedAgentData}
          liveAgent={getLiveAgent(selectedAgentData.role.name)}
          onDocClick={setPreviewDoc}
        />
      )}

      {/* Document Preview Modal */}
      {previewDoc && (
        <DocumentPreviewModal
          projectName={projectName}
          doc={previewDoc}
          onClose={() => setPreviewDoc(null)}
        />
      )}
    </div>
  );
}

