async function scanDocuments(projectPath: string): Promise<AgentDocument[]> {
  const docs: AgentDocument[] = [];

  const docPatterns: { pattern: RegExp; producer: string }[] = [
    { pattern: /^Doc1-.*\.txt$/i, producer: 'BA' },
    { pattern: /^Doc2-.*\.txt$/i, producer: 'BA' },
    { pattern: /^Doc3-.*\.txt$/i, producer: 'BA' },
    { pattern: /^Doc4-.*\.md$/i, producer: 'Frontend' },
    { pattern: /^CLAUDE\.md$/i, producer: 'Claude Code' },
    { pattern: /^SESSION-HISTORY\.md$/i, producer: 'Claude Code' },
    { pattern: /^DEV-FIX-LIST\.md$/i, producer: 'Tester' },
    { pattern: /^EXECUTION-PLAN\.md$/i, producer: 'Claude Code' },
    { pattern: /^DEVOPS\.md$/i, producer: 'DevOps' },
    { pattern: /^cloudbuild\.yaml$/i, producer: 'DevOps' },
    { pattern: /^Dockerfile$/i, producer: 'DevOps' },
  ];

  try {
    const entries = await readdir(projectPath);
    for (const entry of entries) {
      for (const { pattern, producer } of docPatterns) {
        if (pattern.test(entry)) {
          const fullPath = path.join(projectPath, entry);
          const fileStat = await stat(fullPath);
          docs.push({
            name: entry,
            path: entry,
            sizeKb: Math.round(fileStat.size / 1024),
            lastModified: fileStat.mtime.toISOString(),
            producer,
          });
          break;
        }
      }
    }
  } catch { /* skip */ }

