/** Scan for known document files and attribute to agent */
async function scanDocuments(projectPath: string): Promise<AgentDocument[]> {
  const docs: AgentDocument[] = [];

  // Ordered: first match wins
  const docPatterns: { pattern: RegExp; producer: string }[] = [
    // BA documents
    { pattern: /^Doc1-.*\.txt$/i, producer: 'BA' },
    { pattern: /^Doc2-.*\.txt$/i, producer: 'BA' },
    { pattern: /^Doc3-.*\.txt$/i, producer: 'BA' },
    // Frontend documents
    { pattern: /^Doc4-.*\.md$/i, producer: 'Frontend' },
    // Claude Code documents
    { pattern: /^CLAUDE\.md$/i, producer: 'Claude Code' },
    { pattern: /^SESSION-HISTORY\.md$/i, producer: 'Claude Code' },
    { pattern: /^EXECUTION-PLAN\.md$/i, producer: 'Claude Code' },
    { pattern: /^README\.md$/i, producer: 'Claude Code' },
    // Tester documents
    { pattern: /^DEV-FIX-LIST\.md$/i, producer: 'Tester' },
    { pattern: /^TEST-REPORT.*\.md$/i, producer: 'Tester' },
    // DevOps documents
    { pattern: /^DEVOPS\.md$/i, producer: 'DevOps' },
    { pattern: /^cloudbuild\.yaml$/i, producer: 'DevOps' },
    { pattern: /^Dockerfile$/i, producer: 'DevOps' },
    { pattern: /^\.dockerignore$/i, producer: 'DevOps' },
  ];

  try {
    const entries = await readdir(projectPath);
    for (const entry of entries) {
      // Skip hidden dirs/files (except .dockerignore) and node_modules
      if (entry === 'node_modules' || entry === '.git') continue;
      if (entry.startsWith('.') && entry !== '.dockerignore') continue;

      // Only scan files, not directories
      const fullPath = path.join(projectPath, entry);
      let fileStat;
      try { fileStat = await stat(fullPath); } catch { continue; }
      if (!fileStat.isFile()) continue;

      // Match against known patterns
      let matched = false;
      for (const { pattern, producer } of docPatterns) {
        if (pattern.test(entry)) {
          docs.push({
            name: entry,
            path: entry,
            sizeKb: Math.round(fileStat.size / 1024),
            lastModified: fileStat.mtime.toISOString(),
            producer,
          });
          matched = true;
          break;
        }
      }

      // Catch-all: any remaining .md/.txt/.yaml files → attribute to Claude Code
      if (!matched && /\.(md|txt|yaml|yml)$/i.test(entry)) {
        docs.push({
          name: entry,
          path: entry,
          sizeKb: Math.round(fileStat.size / 1024),
          lastModified: fileStat.mtime.toISOString(),
          producer: 'Claude Code',
        });
      }
    }
  } catch { /* skip */ }

  // Also count source files per agent directory
  const dirCounts: { dir: string; producer: string }[] = [
    { dir: 'server/src', producer: 'Fullstack' },
    { dir: 'app/src/pages', producer: 'Frontend' },
    { dir: 'app/src/components', producer: 'Frontend' },
    { dir: 'tests', producer: 'Tester' },
  ];
  for (const { dir, producer } of dirCounts) {
    try {
      const count = await countFiles(path.join(projectPath, dir));
      if (count > 0) {
        docs.push({
          name: `${dir}/ (${count} files)`,
          path: dir,
          sizeKb: 0,
          lastModified: new Date().toISOString(),
          producer,
        });
      }
    } catch { /* skip */ }
  }

  return docs;
}

/** Recursively count files in a directory */
async function countFiles(dirPath: string): Promise<number> {
  let count = 0;
  try {
    const entries = await readdir(dirPath, { withFileTypes: true });
    for (const entry of entries) {
      if (entry.name === 'node_modules' || entry.name === '.git') continue;
      if (entry.isFile()) count++;
      else if (entry.isDirectory()) count += await countFiles(path.join(dirPath, entry.name));
    }
  } catch { /* skip */ }
  return count;
}

